mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT PRDT_CD , PRDT_NM , PRDT_DIV , FN_CM05M01_CD_TO_NM(PRDT_DIV) AS PRDT_DIV_NM , PRDT_STKN , FN_CM05M01_CD_TO_NM(PRDT_STKN) AS PRDT_STKN_NM , PRDT_ERQK_YN , FN_CM05M01_CD_TO_NM(PRDT_ERQK_YN) AS PRDT_ERQK_NM , PRDT_SIZE , NVL(FN_CM05M01_CD_TO_NM(PRDT_SIZE), PRDT_SIZE) AS PRDT_SIZE_NM , PRDT_SPEC , NVL(FN_CM05M01_CD_TO_NM(PRDT_SPEC), PRDT_SPEC) AS PRDT_SPEC_NM , PRDT_COIL_YN , PRDT_UNIT , NVL(FN_CM05M01_CD_TO_NM(PRDT_UNIT), PRDT_UNIT) AS PRDT_UNIT_NM , PRDT_WT , PRDT_UPR , TO_CHAR(TO_DATE(PRDT_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS PRDT_DT , PRDT_DK_CD , PRDT_MES_CD
, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), #{selpchCd}, #{impYn}, T.PRDT_CD), 0) AS ORDRG_UPR
, USE_YN , ETC_FIELD1 , ETC_FIELD2 , ETC_FIELD3 , PCHS_UPR , SELL_UPR , PRDT_CNVRSN_WT , CREAT_ID, CREAT_PGM, CREAT_DTTM, UDT_ID, UPD_PGM, UDT_DTTM FROM TB_BM01M01 T
USE_YN = #{useYn}
AND PRDT_DIV = #{prdtDiv}
AND ${searchType} LIKE '%${searchValue}%'
AND TO_DATE(PRDT_DT, 'YYYYMMDD') <= TO_DATE(#{searchPrdtDt}, 'YYYYMMDD')
ORDER BY PRDT_DIV, PRDT_NM, PRDT_STKN, PRDT_ERQK_YN, PRDT_SIZE, PRDT_SPEC, PRDT_COIL_YN ) A ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT COUNT(*) FROM TB_BM01M01
USE_YN = #{useYn}
AND PRDT_DIV = #{prdtDiv}
AND ${searchType} LIKE '%${searchValue}%'
AND TO_DATE(PRDT_DT, 'YYYYMMDD') <= TO_DATE(#{searchPrdtDt}, 'YYYYMMDD')

SELECT COUNT(*) FROM TB_BM01M01
WHERE 

PRDT_NM = #{prdtNm}


 AND PRDT_CD = #{prdtCd}


AND PRDT_DIV = #{prdtDiv}


AND PRDT_STKN = #{prdtStkn}


AND PRDT_ERQK_YN = #{prdtErqkYn}


AND PRDT_SIZE = #{prdtSize}


AND PRDT_SPEC = #{prdtSpec}


AND PRDT_COIL_YN= #{prdtCoilYn}

