mapper.select:
 	SELECT 
 		COUNT(*) 
 	FROM 
 		TB_BM01M01

 		USE_YN = #{useYn}

 			AND PRDT_DIV = #{prdtDiv}


 			AND PRDT_STKN = #{prdtStkn}


			AND ${searchType} LIKE '%'||UPPER(#{searchValue})||'%'

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT PRDT_CD , PRDT_NM , PRDT_GRP , FN_CM05M01_CD_TO_NM(PRDT_GRP) AS PRDT_GRP_NM , PRDT_DIV , FN_CM05M01_CD_TO_NM(PRDT_DIV) AS PRDT_DIV_NM , PRDT_STKN , FN_CM05M01_CD_TO_NM(PRDT_STKN) AS PRDT_STKN_NM , PRDT_ERQK_YN , FN_CM05M01_CD_TO_NM(PRDT_ERQK_YN) AS PRDT_ERQK_NM , PRDT_SIZE , NVL(FN_CM05M01_CD_TO_NM(PRDT_SIZE), PRDT_SIZE) AS PRDT_SIZE_NM , PRDT_SPEC , NVL(FN_CM05M01_CD_TO_NM(PRDT_SPEC), PRDT_SPEC) AS PRDT_SPEC_NM
, (SELECT SUM(STOCK_QTY) FROM TB_SM01M01 WHERE CO_CD = #{coCd} AND OWNER_CD = 'OWNER1' AND PRDT_CD = T.PRDT_CD) TOT_STOCK_QTY , (SELECT SUM(STOCK_Wt) FROM TB_SM01M01 WHERE CO_CD = #{coCd} AND OWNER_CD = 'OWNER1' AND PRDT_CD = T.PRDT_CD) TOT_STOCK_WT
, PRDT_COIL_YN , PRDT_UNIT , NVL(FN_CM05M01_CD_TO_NM(PRDT_UNIT), PRDT_UNIT) AS PRDT_UNIT_NM , PRDT_WT , PRDT_UPR , TO_CHAR(TO_DATE(PRDT_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS PRDT_DT , PRDT_DK_CD , PRDT_MES_CD
, DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), #{selpchCd}, #{impYn}, T.PRDT_CD, #{clntCd}, #{prjctCd}), 0)) AS ORDRG_UPR , DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', #{impYn}, T.PRDT_CD, #{clntCd}, #{prjctCd}), 0)) AS SHIP_UPR , DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'N', T.PRDT_CD, #{clntCd}, #{prjctCd}), 0)) AS SHIP_UPR_D , DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'Y', T.PRDT_CD, #{clntCd}, #{prjctCd}), 0)) AS SHIP_UPR_I
, DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), #{selpchCd}, #{impYn}, T.PRDT_CD, #{clntCd}, ''), 0)) AS ORDRG_UPR , DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', #{impYn}, T.PRDT_CD, #{clntCd}, ''), 0)) AS SHIP_UPR , DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'N', T.PRDT_CD, #{clntCd}, ''), 0)) AS SHIP_UPR_D , DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'Y', T.PRDT_CD, #{clntCd}, ''), 0)) AS SHIP_UPR_I
, DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), #{selpchCd}, #{impYn}, T.PRDT_CD, '', ''), 0)) AS ORDRG_UPR , DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', #{impYn}, T.PRDT_CD, '', ''), 0)) AS SHIP_UPR , DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'N', T.PRDT_CD, '', ''), 0)) AS SHIP_UPR_D , DECODE(T.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'Y', T.PRDT_CD, '', ''), 0)) AS SHIP_UPR_I
, NVL(FN_SM01_UPR_FROM_PRDT_CD(#{coCd},T.PRDT_CD), 0) AS STOCK_UPR
, USE_YN , ETC_FIELD1 , ETC_FIELD2 , ETC_FIELD3 , PCHS_UPR , SELL_UPR , PRDT_CNVRSN_WT , PRDT_LEN , PRDT_STOCK_CD , RMK , CREAT_ID, CREAT_PGM, CREAT_DTTM, UDT_ID, UPD_PGM, UDT_DTTM FROM TB_BM01M01 T WHERE 1=1
AND USE_YN = #{useYn}
AND PRDT_DIV = #{prdtDiv}
AND PRDT_STKN = #{prdtStkn}
AND ${searchType} LIKE '%'||UPPER(#{searchValue})||'%'
ORDER BY PRDT_DIV, PRDT_NM, PRDT_STKN, PRDT_ERQK_YN, PRDT_SIZE, PRDT_SPEC, PRDT_COIL_YN ) A ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT COUNT(*) FROM TB_BM01M01
WHERE 

PRDT_NM = #{prdtNm}


 AND PRDT_CD = #{prdtCd}


AND PRDT_DIV = #{prdtDiv}


AND PRDT_STKN = #{prdtStkn}


AND PRDT_ERQK_YN = #{prdtErqkYn}


AND PRDT_SIZE = #{prdtSize}


AND PRDT_SPEC = #{prdtSpec}


AND PRDT_COIL_YN= #{prdtCoilYn}

