mapper.select:
SELECT COUNT(*) FROM TB_BM02M01 WHERE USE_YN = #{useYn}
AND ${searchType} LIKE '%${searchValue}%'
ORDER BY CLNT_CD

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT CLNT_CD, CLNT_NM, CRN, (SELECT CODE_NM FROM GOLDMOON.TB_CM05M01 WHERE CODE_ID = CLNT.CLNT_DIV_CD) CLNT_DIV_NM, BIZCON_NM, BSTY_NM, BIZ_TEL_NO, CREAT_DTTM, UDT_DTTM FROM TB_BM02M01 CLNT WHERE USE_YN = #{useYn}
AND ${searchType} LIKE '%${searchValue}%'
ORDER BY CLNT_CD ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
