mapper.select:
SELECT COUNT(*) FROM TB_BM02M01
USE_YN = #{useYn}
USE_YN = 'Y'
AND ${searchType} LIKE '%${searchValue}%'
AND CLNT_DIV_CD = #{clntDivCd}
ORDER BY CLNT_CD

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT CLNT_CD, CLNT_NM, CRN, COPRT_NO, FN_CM05M01_CD_TO_NM(CLNT_DIV_CD) CLNT_DIV_NM, BIZCON_NM, BSTY_NM, BIZ_TEL_NO, TO_CHAR(CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, TO_CHAR(UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM, BASIS_CDTLN_AMT FROM TB_BM02M01
USE_YN = #{useYn}
USE_YN = 'Y'
AND ${searchType} LIKE '%${searchValue}%'
AND CLNT_DIV_CD = #{clntDivCd}
ORDER BY CLNT_CD ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
