mapper.select:
		SELECT 
			COUNT(*)
		FROM (	SELECT BM02.*,		
                     nvl2(LINK_GRP_CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 B WHERE B.CLNT_CD = BM02.LINK_GRP_CLNT_CD),BM02.CLNT_NM ) AS LINK_GRP_CLNT_NM
				FROM
					TB_BM02M01 BM02			             


						BM02.USE_YN = #{useYn}


						AND BM02.CLNT_DIV_CD = #{clntDivCd} 


						AND BM02.CLNT_CD =  #{clntCd}


						AND BM02.CLNT_NM LIKE '%${clntNm}%'


						AND BM02.LINK_GRP_CLNT_CD = #{linkGrpClntCd}


						AND ${searchType} LIKE '%${searchValue}%'



		     ) T


					    LINK_GRP_CLNT_NM LIKE '%${linkGrpClntNm}%'

SELECT * FROM ( SELECT ROWNUM AS RNUM, T.*, T.CDTLN_AMT - T.REAL_SECURITY - T.GUARNTEE_SECURITY - T.CASH_SECURITY - T.EACH_CREDIT_SECURITY AS OTHER_SECURITY FROM ( SELECT A.CLNT_CD, A.CLNT_NM, A.CRN, A.COPRT_NO, A.CLNT_DIV_CD, FN_CM05M01_CD_TO_NM(A.CLNT_DIV_CD) CLNT_DIV_NM, A.BIZCON_NM, A.BSTY_NM, A.BIZ_TEL_NO, TO_CHAR(A.CREAT_DTTM, 'YYYY-MM-DD') AS CREAT_DTTM, TO_CHAR(A.UDT_DTTM, 'YYYY-MM-DD') AS UDT_DTTM, A.BASIS_CDTLN_AMT, F_CREDIT_LOAN('T', A.CLNT_CD,
#{coCd}
''
,to_char(CURRENT_DATE,'yyyymmdd'),0) AS TOT_LOAN_AMT, F_CREDIT_LOAN('C', A.CLNT_CD,
#{coCd}
''
,to_char(CURRENT_DATE,'yyyymmdd'),0) AS LOAN_AMT, LINK_GRP_YN, nvl(LINK_GRP_CLNT_CD, A.CLNT_CD) as LINK_GRP_CLNT_CD, nvl2(LINK_GRP_CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 B WHERE B.CLNT_CD = A.LINK_GRP_CLNT_CD),A.CLNT_NM )AS LINK_GRP_CLNT_NM, A.REPST_NM, A.BIZ_ZIP, A.BIZ_ADDR, A.BIZ_ADDR_DTL, A.BIZ_FAX_NO, A.SUB_RMK_URL, A.BANK_CD, A.BKAC_NO, A.BKAC_OWNER, A.PCHS_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PAY_DIV_CD) AS PCHS_PAY_DIV_NM, A.PCHS_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.PCHS_CLMN_DIV_CD) AS PCHS_CLMN_DIV_NM, A.PCHS_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.PCHS_PMNT_MTD_CD) AS PCHS_PMNT_MTD_NM, A.PCHS_VAT_CD, FN_CM05M01_CD_TO_NM(A.PCHS_VAT_CD) AS PCHS_VAT_NM, A.SELL_PAY_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM, A.SELL_CLMN_DIV_CD, FN_CM05M01_CD_TO_NM(A.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM, A.SELL_PMNT_MTD_CD, FN_CM05M01_CD_TO_NM(A.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_NM, A.SELL_VAT_CD, FN_CM05M01_CD_TO_NM(A.SELL_VAT_CD) AS SELL_VAT_NM, NVL((SELECT CODE_RPRC FROM TB_CM05M01 WHERE CODE_ID = A.SELL_VAT_CD), 10) AS SELL_VAT, A.CLNT_RMK, A.USE_YN, A.ETC_FIELD1, A.ETC_FIELD2, A.ETC_FIELD3, A.CREAT_ID, A.CREAT_PGM, A.UDT_ID, A.UDT_PGM, A.LINK_GRP_YN, A.LINK_GRP_CLNT_CD, (SELECT SUM(B.PLDG_AMT * PLDG_RCOGN_RATE / 100) FROM TB_BM02D02 B WHERE B.CLNT_CD = A.CLNT_CD AND B.USE_YN = 'Y' AND B.SELPCH_CD = 'SELPCH2' AND B.PLDG_DIV_CD = 'PLDG03' ) AS COMMON_CREDIT_SECURITY, D02.REAL_SECURITY, D02.GUARNTEE_SECURITY, D02.EACH_CREDIT_SECURITY, D02.CASH_SECURITY, D02.CDTLN_AMT, D02.PCHS_REAL_SECURITY, D02.PCHS_GUARNTEE_SECURITY, D02.PCHS_EACH_CREDIT_SECURITY, D02.PCHS_CASH_SECURITY, D02.PCHS_CDTLN_AMT, A.PCHS_CLMN_DAY, A.PCHS_CLMN_RMK, A.SELL_CLMN_DAY, A.SELL_CLMN_RMK FROM TB_BM02M01 A LEFT OUTER JOIN ( SELECT CLNT_CD, SUM(DECODE(SELPCH_CD ,'SELPCH2',0,DECODE(PLDG_DIV_CD,'PLDG01',PLDG_AMT * PLDG_RCOGN_RATE / 100,0))) AS PCHS_REAL_SECURITY, SUM(DECODE(SELPCH_CD ,'SELPCH2',0,DECODE(PLDG_DIV_CD,'PLDG02',PLDG_AMT * PLDG_RCOGN_RATE / 100,0))) AS PCHS_GUARNTEE_SECURITY, SUM(DECODE(SELPCH_CD ,'SELPCH2',0,DECODE(PLDG_DIV_CD,'PLDG04',PLDG_AMT * PLDG_RCOGN_RATE / 100,0))) AS PCHS_EACH_CREDIT_SECURITY, SUM(DECODE(SELPCH_CD ,'SELPCH2',0,DECODE(PLDG_DIV_CD,'PLDG05',PLDG_AMT * PLDG_RCOGN_RATE / 100,0))) AS PCHS_CASH_SECURITY, SUM(DECODE(SELPCH_CD ,'SELPCH2',0,DECODE(PLDG_DIV_CD,'PLDG03',0,PLDG_AMT * PLDG_RCOGN_RATE / 100))) AS PCHS_CDTLN_AMT, SUM(DECODE(SELPCH_CD ,'SELPCH1',0,DECODE(PLDG_DIV_CD,'PLDG01',PLDG_AMT * PLDG_RCOGN_RATE / 100,0))) AS REAL_SECURITY, SUM(DECODE(SELPCH_CD ,'SELPCH1',0,DECODE(PLDG_DIV_CD,'PLDG02',PLDG_AMT * PLDG_RCOGN_RATE / 100,0))) AS GUARNTEE_SECURITY, SUM(DECODE(SELPCH_CD ,'SELPCH1',0,DECODE(PLDG_DIV_CD,'PLDG04',PLDG_AMT * PLDG_RCOGN_RATE / 100,0))) AS EACH_CREDIT_SECURITY, SUM(DECODE(SELPCH_CD ,'SELPCH1',0,DECODE(PLDG_DIV_CD,'PLDG05',PLDG_AMT * PLDG_RCOGN_RATE / 100,0))) AS CASH_SECURITY, SUM(DECODE(SELPCH_CD ,'SELPCH1',0,DECODE(PLDG_DIV_CD,'PLDG03',0,PLDG_AMT * PLDG_RCOGN_RATE / 100))) AS CDTLN_AMT FROM TB_BM02D02 BMD02 WHERE USE_YN = 'Y'
AND CO_CD = #{coCd}
GROUP BY CLNT_CD ) D02 ON D02.CLNT_CD = A.CLNT_CD
USE_YN = #{useYn}
AND ${searchType} LIKE '%${searchValue}%'
AND A.CLNT_DIV_CD = #{clntDivCd}
AND A.CLNT_CD = #{clntCd}
AND A.CLNT_NM LIKE '%${clntNm}%'
AND A.LINK_GRP_CLNT_CD = #{linkGrpClntCd}
ORDER BY A.LINK_GRP_CLNT_CD, A.CLNT_CD ) T
LINK_GRP_CLNT_NM LIKE '%${linkGrpClntNm}%'
) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

mapper.update:
UPDATE TB_BM02D01
   SET 
       BIZDEPT_NM      = #{bizdeptNm}
     , BIZDEPT_DIV_CD  = #{bizdeptDivCd}
     , MNG_NM          = #{mngNm}
     , MNG_DEPT_NM     = #{mngDeptNm}
     , MNG_PST_CD      = #{mngPstCd}
     , MNG_TEL_NO      = #{mngTelNo}
     , MNG_MOBLPHON_NO = #{mngMoblphonNo}
     , MNG_EMAIL       = #{mngEmail}
     , SALES_CO_CD     = #{salesCoCd}
     , SALES_EMP_ID    = #{salesEmpId}
     , USE_YN          = #{useYn}
     , UDT_ID          = #{udtId}
     , UDT_PGM         = #{udtPgm}
     , UDT_DTTM        = SYSDATE
 WHERE BIZDEPT_SN      = #{bizdeptSn}
   AND (BIZDEPT_NM    != #{bizdeptNm}
    OR NVL(BIZDEPT_DIV_CD,'@')  != NVL(#{bizdeptDivCd},'@')
    OR NVL(MNG_NM,'@')          != NVL(#{mngNm},'@')
    OR NVL(MNG_DEPT_NM,'@')     != NVL(#{mngDeptNm},'@')
    OR NVL(MNG_PST_CD,'@')      != NVL(#{mngPstCd},'@')
    OR NVL(MNG_TEL_NO,'@')      != NVL(#{mngTelNo},'@')
    OR NVL(MNG_MOBLPHON_NO,'@') != NVL(#{mngMoblphonNo},'@')
    OR NVL(MNG_EMAIL,'@')       != NVL(#{mngEmail},'@')
    OR NVL(SALES_CO_CD,'@')     != NVL(#{salesCoCd},'@')
    OR NVL(SALES_EMP_ID,'@')    != NVL(#{salesEmpId},'@')
    OR USE_YN        		    != #{useYn}
   	)

UPDATE TB_BM02D02
   SET 
       CO_CD           	   = #{coCd}
     , SELPCH_CD           = #{selpchCd}
     , PLDG_DIV_CD         = #{pldgDivCd}
     , PLDG_CHG_CD     	   = #{pldgChgCd}
     , PLDG_AMT        	   = #{pldgAmt}
     , PLDG_RCOGN_RATE 	   = #{pldgRcognRate}
     , EXPRTN_DT		   = #{exprtnDt}
     , SETUP_DT    		   = #{setupDt}
     , PLDG_RMK     	   = #{pldgRmk}
     , USE_YN       	   = #{useYn}
	 , PTON_NM       	   = #{ptonNm}
     , UDT_ID        	   = #{udtId}
     , UDT_PGM       	   = #{udtPgm}
     , UDT_DTTM      	   = SYSDATE
 WHERE PLDG_SN        	   = #{pldgSn}
   AND (PLDG_AMT      	  != #{pldgAmt}
    OR PLDG_RCOGN_RATE 	  != #{pldgRcognRate}
    OR NVL(EXPRTN_DT,'X') != NVL(#{exprtnDt},'X')
    OR SETUP_DT    	   	  != #{setupDt}
    OR NVL(PLDG_RMK,'X')  != NVL(#{pldgRmk},'X')
    OR USE_YN        	  != #{useYn}
    OR SELPCH_CD    	  != #{selpchCd}
    OR CO_CD         	  != #{coCd}
    OR PLDG_CHG_CD    	  != #{pldgChgCd}
	OR NVL(PTON_NM ,'X')  != NVL(#{ptonNm},'X')
    )
