mapper.select:
SELECT count(*) FROM ( SELECT A.USE_YN, A.LINK_GRP_CLNT_CD, nvl2(A.LINK_GRP_CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 C WHERE C.CLNT_CD = A.LINK_GRP_CLNT_CD), A.CLNT_NM ) AS LINK_GRP_CLNT_NM, CO_CD, CO_NM, CLNT_CD, CLNT_NM, BASIS_CDTLN_AMT, PLDG01_AMT, PLDG02_AMT, PLDG04_AMT, PLDG05_AMT, CDTLN_AMT FROM ( SELECT A.USE_YN, nvl(A.LINK_GRP_CLNT_CD, A.CLNT_CD) as LINK_GRP_CLNT_CD, B.CO_CD, FN_CM05M01_CD_TO_NM(b.CO_CD) CO_NM, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG01', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0))) AS PLDG01_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG02', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0))) AS PLDG02_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG04', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0))) AS PLDG04_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG05', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0))) AS PLDG05_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG03',0,B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100),0))) AS CDTLN_AMT FROM TB_BM02M01 A INNER JOIN TB_BM02D02 B ON B.CLNT_CD = A.CLNT_CD
AND B.CO_CD = #{coCd}
WHERE 1=1
AND A.USE_YN = #{useYn}
AND A.CLNT_CD = #{clntCdS}
AND A.CLNT_NM like '%${clntNm}%'
AND A.CLNT_DIV_CD = #{clntDivCd}
AND B.PLDG_DIV_CD = #{pldgDivCd}
AND B.SELPCH_CD = #{selpchCd}
AND B.PRDT_GRP = #{prdtGrp}
GROUP BY A.USE_YN, nvl(A.LINK_GRP_CLNT_CD, A.CLNT_CD) , B.CO_CD, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT ) AS a ORDER BY LINK_GRP_CLNT_CD, CLNT_NM, CLNT_CD, CO_NM, CO_CD, USE_YN ) A
ORDER BY a.LINK_GRP_CLNT_NM

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.*, CDTLN_AMT - PLDG01_AMT - PLDG02_AMT - PLDG04_AMT - PLDG05_AMT AS PLDG99_AMT FROM ( SELECT A.USE_YN, A.LINK_GRP_CLNT_CD, nvl2(A.LINK_GRP_CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 C WHERE C.CLNT_CD = A.LINK_GRP_CLNT_CD), A.CLNT_NM ) AS LINK_GRP_CLNT_NM, CO_CD, CO_NM, CLNT_CD, CLNT_NM, BASIS_CDTLN_AMT, PLDG01_AMT, PLDG02_AMT, PLDG04_AMT, PLDG05_AMT, CDTLN_AMT, (SELECT SUM(B.PLDG_AMT * PLDG_RCOGN_RATE / 100) FROM TB_BM02D02 B WHERE B.CLNT_CD = A.CLNT_CD AND B.USE_YN = 'Y' AND B.SELPCH_CD = 'SELPCH2' AND B.PLDG_DIV_CD = 'PLDG03' ) AS PLDG03_AMT, F_CREDIT_LOAN('T', A.CLNT_CD,A.CO_CD, REPLACE(#{baseDt},'-','') ,0) AS TOT_LOAN_AMT, F_CREDIT_LOAN('C', A.CLNT_CD,A.CO_CD, REPLACE(#{baseDt},'-','') ,0) AS LOAN_AMT FROM ( SELECT A.USE_YN, nvl(A.LINK_GRP_CLNT_CD, A.CLNT_CD) as LINK_GRP_CLNT_CD, B.CO_CD, FN_CM05M01_CD_TO_NM(b.CO_CD) CO_NM, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG01', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0))) AS PLDG01_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG02', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0))) AS PLDG02_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG04', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0))) AS PLDG04_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG05', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0))) AS PLDG05_AMT, SUM(decode(B.USE_YN,'Y',decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG03',0,B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100),0))) AS CDTLN_AMT FROM TB_BM02M01 A INNER JOIN TB_BM02D02 B ON B.CLNT_CD = A.CLNT_CD
AND B.CO_CD = #{coCd}
WHERE 1=1
AND A.USE_YN = #{useYn}
AND A.CLNT_CD = #{clntCdS}
AND A.CLNT_NM like '%${clntNm}%'
AND A.CLNT_DIV_CD = #{clntDivCd}
AND B.PLDG_DIV_CD = #{pldgDivCd}
AND B.SELPCH_CD = #{selpchCd}
AND B.PRDT_GRP = #{prdtGrp}
GROUP BY A.USE_YN, nvl(A.LINK_GRP_CLNT_CD, A.CLNT_CD) , B.CO_CD, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT ) AS a ORDER BY LINK_GRP_CLNT_CD, CLNT_NM, CLNT_CD, CO_NM, CO_CD, USE_YN ) A
ORDER BY a.LINK_GRP_CLNT_NM ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

	SELECT COUNT(*) 
	FROM TB_BM02M01 A
	LEFT OUTER JOIN TB_BM02D02 B
            ON  A.CLNT_CD = B.CLNT_CD
	WHERE 1=1
	  AND B.CO_CD = #{coCd}
	  AND B.CLNT_CD = #{clntCd}

	  AND B.USE_YN = #{useYn}

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT B.USE_YN, B.CO_CD, FN_CM05M01_CD_TO_NM(CO_CD) CO_NM, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT, B.PLDG_AMT, B.PLDG_CHG_CD, FN_CM05M01_CD_TO_NM(PLDG_CHG_CD) PLDG_CHG_NM, B.PLDG_DIV_CD, FN_CM05M01_CD_TO_NM(PLDG_DIV_CD) PLDG_DIV_NM, B.PLDG_RCOGN_RATE, B.PLDG_RMK, B.PLDG_AMT * PLDG_RCOGN_RATE / 100 AS CDTLN_AMT, B.EXPRTN_DT, B.SETUP_DT, B.SELPCH_CD, FN_CM05M01_CD_TO_NM(B.SELPCH_CD) SELPCH_NM, B.PTON_NM, FN_CM05M01_CD_TO_NM(B.PRDT_GRP) PRDT_GRP_NM FROM TB_BM02M01 A LEFT OUTER JOIN TB_BM02D02 B ON A.CLNT_CD = B.CLNT_CD WHERE 1=1 AND B.CLNT_CD = #{clntCd}
AND B.CO_CD = #{coCd}
AND B.USE_YN = #{useYn}
) A ORDER BY PLDG_DIV_CD , CLNT_CD ,CO_CD ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

	SELECT count(*)
      FROM TB_BM02H02 A
	  INNER JOIN TB_BM02M01 BM02
	          ON BM02.CLNT_CD = A.CLNT_CD 
	 WHERE 1 = 1

	   AND A.PROC_DTTM  BETWEEN TO_DATE( #{startDt},'YYYYMMDD') AND TO_DATE( #{endDt},'YYYYMMDD') +1


	   AND A.CHG_CD      = #{chgCd}


	   AND A.CLNT_CD     = #{clntCd}


	   AND A.CO_CD       = #{coCd}


	   AND A.SELPCH_CD   = #{selpchCd}


	   AND A.PLDG_DIV_CD = #{pldgDivCd}


	   AND A.PLDG_RMK    LIKE '%' || #{pldgRmk} || '%'


	   AND A.USE_YN      = #{useYn}


	   AND BM02.CLNT_NM  LIKE '%' || #{clntNm} || '%'


		AND A.PRDT_GRP = #{prdtGrp}


	   AND TO_DATE(A.EXPRTN_DT,'YYYYMMDD') + 1  <   SYSDATE


	   AND TO_DATE(NVL(A.EXPRTN_DT,20991231),'YYYYMMDD')  >=   SYSDATE

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT A.TRST_NO
, A.PROC_DTTM AS PROC_DTTM , A.CHG_CD , FN_CM05M01_CD_TO_NM(A.CHG_CD) AS CHG_NM , A.PLDG_SN , A.CLNT_CD , BM02.CLNT_NM , A.CO_CD , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM , A.SELPCH_CD , FN_CM05M01_CD_TO_NM(A.SELPCH_CD) AS SELPCH_NM , A.PLDG_CHG_CD , FN_CM05M01_CD_TO_NM(A.PLDG_CHG_CD) AS PLDG_CHG_NM , A.PLDG_DIV_CD , FN_CM05M01_CD_TO_NM(A.PLDG_DIV_CD) AS PLDG_DIV_NM , A.PLDG_AMT , A.PLDG_RCOGN_RATE , A.PLDG_AMT * A.PLDG_RCOGN_RATE /100 AS CDTLN_AMT , A.PRE_PLDG_AMT , A.PRE_PLDG_RCOGN_RATE , A.PRE_PLDG_AMT * A.PRE_PLDG_RCOGN_RATE /100 AS PRE_CDTLN_AMT , TO_CHAR(TO_DATE(A.EXPRTN_DT,'YYYYMMDD'),'YYYY-MM-DD') AS EXPRTN_DT , TO_CHAR(TO_DATE(A.SETUP_DT, 'YYYYMMDD'),'YYYY-MM-DD') AS SETUP_DT , A.PLDG_RMK , A.USE_YN , A.CREAT_ID ,(SELECT NAME FROM TB_CM06M01 WHERE ID = A.CREAT_ID) AS CREAT_NM , A.CREAT_PGM , TO_CHAR(A.CREAT_DTTM,'YYYY-MM-DD') AS CREAT_DTTM , A.UDT_ID ,(SELECT NAME FROM TB_CM06M01 WHERE ID = A.UDT_ID) AS UDT_NM , A.UDT_PGM , TO_CHAR(A.UDT_DTTM,'YYYY-MM-DD') AS UDT_DTTM FROM TB_BM02H02 A INNER JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = A.CLNT_CD WHERE 1 = 1
AND A.PROC_DTTM BETWEEN TO_DATE(#{startDt},'YYYYMMDD') AND TO_DATE(#{endDt},'YYYYMMDD')+1
AND A.CHG_CD = #{chgCd}
AND A.CLNT_CD = #{clntCd}
AND A.CO_CD = #{coCd}
AND A.SELPCH_CD = #{selpchCd}
AND A.PLDG_DIV_CD = #{pldgDivCd}
AND A.PLDG_RMK LIKE '%' || #{pldgRmk} || '%'
AND A.USE_YN = #{useYn}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} || '%'
AND A.PRDT_GRP = #{prdtGrp}
AND TO_DATE(A.EXPRTN_DT,'YYYYMMDD') < SYSDATE
AND TO_DATE(NVL(A.EXPRTN_DT,20991231),'YYYYMMDD') >= SYSDATE
) A ORDER BY A.PROC_DTTM DESC
) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
