mapper.select:
	SELECT count(*)
	FROM ( 
		SELECT 
		    count(*)
        FROM TB_BM02M01 A
			LEFT OUTER JOIN TB_BM02D02 B
			             ON  A.CLNT_CD = B.CLNT_CD 
			             AND nvl(B.EXPRTN_DT,99999999) >= to_char(CURRENT_DATE,'yyyymmdd')

						  AND B.CO_CD = #{coCd} 

	  WHERE 1=1

			  AND A.USE_YN = #{useYn}


			  AND A.CLNT_CD = #{clntCdS}


			  AND A.CLNT_NM like '%${clntNm}%'


			  AND A.CLNT_DIV_CD = #{clntDivCd} 

		GROUP BY a.LINK_GRP_CLNT_CD, b.CO_CD, a.CLNT_CD
		) AS a

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.*, CDTLN_AMT - PLDG01_AMT - PLDG02_AMT - PLDG04_AMT - PLDG05_AMT AS PLDG99_AMT FROM ( SELECT A.USE_YN, A.LINK_GRP_CLNT_CD, nvl2(A.LINK_GRP_CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 C WHERE C.CLNT_CD = A.LINK_GRP_CLNT_CD), A.CLNT_NM ) AS LINK_GRP_CLNT_NM, CO_CD, CO_NM, CLNT_CD, CLNT_NM, BASIS_CDTLN_AMT, PLDG01_AMT, PLDG02_AMT, PLDG04_AMT, PLDG05_AMT, CDTLN_AMT, (SELECT SUM(B.PLDG_AMT * PLDG_RCOGN_RATE / 100) FROM TB_BM02D02 B WHERE B.CLNT_CD = A.CLNT_CD AND B.USE_YN = 'Y' AND B.SELPCH_CD = 'SELPCH2' AND B.PLDG_DIV_CD = 'PLDG03' ) AS PLDG03_AMT, F_CREDIT_LOAN('T', A.CLNT_CD,A.CO_CD, to_char(CURRENT_DATE,'yyyymmdd'),0) AS TOT_LOAN_AMT, F_CREDIT_LOAN('C', A.CLNT_CD,A.CO_CD, to_char(CURRENT_DATE,'yyyymmdd'),0) AS LOAN_AMT FROM ( SELECT A.USE_YN, nvl(A.LINK_GRP_CLNT_CD, A.CLNT_CD) as LINK_GRP_CLNT_CD, B.CO_CD, FN_CM05M01_CD_TO_NM(b.CO_CD) CO_NM, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT, SUM(decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG01', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0)) AS PLDG01_AMT, SUM(decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG02', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0)) AS PLDG02_AMT, SUM(decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG04', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0)) AS PLDG04_AMT, SUM(decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG05', B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100,0),0)) AS PLDG05_AMT, SUM(decode(B.SELPCH_CD,'SELPCH2',decode(b.PLDG_DIV_CD,'PLDG03',0,B.PLDG_AMT * B.PLDG_RCOGN_RATE / 100),0)) AS CDTLN_AMT FROM TB_BM02M01 A LEFT OUTER JOIN TB_BM02D02 B ON B.CLNT_CD = A.CLNT_CD AND B.USE_YN = 'Y'
AND B.CO_CD = #{coCd}
WHERE 1=1
AND A.USE_YN = #{useYn}
AND A.CLNT_CD = #{clntCdS}
AND A.CLNT_NM like '%${clntNm}%'
AND A.CLNT_DIV_CD = #{clntDivCd}
GROUP BY A.USE_YN, nvl(A.LINK_GRP_CLNT_CD, A.CLNT_CD) , B.CO_CD, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT ) AS a ORDER BY LINK_GRP_CLNT_CD, CLNT_NM, CLNT_CD, CO_NM, CO_CD, USE_YN ) A WHERE TOT_LOAN_AMT != 0 ORDER BY a.LINK_GRP_CLNT_NM ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

	SELECT COUNT(*) 
	FROM TB_BM02M01 A
	LEFT OUTER JOIN TB_BM02D02 B
            ON  A.CLNT_CD = B.CLNT_CD
	WHERE 1=1
	  AND B.CO_CD = #{coCd}
	  AND B.CLNT_CD = #{clntCd}

	  AND B.USE_YN = #{useYn}

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT B.USE_YN, B.CO_CD, FN_CM05M01_CD_TO_NM(CO_CD) CO_NM, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT, B.PLDG_AMT, B.PLDG_DIV_CD, FN_CM05M01_CD_TO_NM(PLDG_DIV_CD) PLDG_DIV_NM, B.PLDG_RCOGN_RATE, B.PLDG_RMK, B.PLDG_AMT * PLDG_RCOGN_RATE / 100 AS CDTLN_AMT, B.EXPRTN_DT, B.SETUP_DT, B.SELPCH_CD, FN_CM05M01_CD_TO_NM(B.SELPCH_CD) SELPCH_NM FROM TB_BM02M01 A LEFT OUTER JOIN TB_BM02D02 B ON A.CLNT_CD = B.CLNT_CD WHERE 1=1
AND B.CO_CD = #{coCd}
AND B.CLNT_CD = #{clntCd}
AND B.USE_YN = #{useYn}
) A ORDER BY PLDG_DIV_CD , CLNT_CD ,CO_CD ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
