mapper.select:
SELECT COUNT(*) 
FROM TB_BM02M01 A
		LEFT OUTER JOIN TB_BM02D02 B
		             ON  A.CLNT_CD = B.CLNT_CD

		  AND B.CO_CD = #{coCd} 

WHERE 1=1
  AND A.USE_YN = 'Y'

		  AND B.USE_YN = #{useYn}


		  AND A.CLNT_CD = #{clntCdS}


		  AND A.CLNT_NM like '%${clntNm}%'


		  AND A.CLNT_DIV_CD = #{clntDivCd}

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT B.CO_CD, FN_CM05M01_CD_TO_NM(CO_CD) CO_NM, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT, SUM(B.PLDG_AMT) AS PLDG_AMT, SUM(B.PLDG_AMT * PLDG_RCOGN_RATE / 100) AS CDTLN_AMT FROM TB_BM02M01 A LEFT OUTER JOIN TB_BM02D02 B ON A.CLNT_CD = B.CLNT_CD
AND B.CO_CD = #{coCd}
WHERE 1=1 AND A.USE_YN = 'Y'
AND B.USE_YN = #{useYn}
AND A.CLNT_CD = #{clntCdS}
AND A.CLNT_NM like '%${clntNm}%'
AND A.CLNT_DIV_CD = #{clntDivCd}
GROUP BY B.CO_CD, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT ORDER BY DECODE( NVL(A.BASIS_CDTLN_AMT,0),-1,decode(NVL(CDTLN_AMT,0),0,-1,CLNT_CD)) DESC ,CO_CD ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

	SELECT COUNT(*) 
	FROM TB_BM02M01 A
	LEFT OUTER JOIN TB_BM02D02 B
            ON  A.CLNT_CD = B.CLNT_CD
	WHERE 1=1
	  AND B.CO_CD = #{coCd}
	  AND B.CLNT_CD = #{clntCd}
	  AND A.USE_YN = 'Y'

	  AND B.USE_YN = #{useYn}

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT B.USE_YN, B.CO_CD, FN_CM05M01_CD_TO_NM(CO_CD) CO_NM, A.CLNT_CD, A.CLNT_NM, A.BASIS_CDTLN_AMT, B.PLDG_AMT, B.PLDG_DIV_CD, FN_CM05M01_CD_TO_NM(PLDG_DIV_CD) PLDG_DIV_NM, B.PLDG_RCOGN_RATE, B.PLDG_RMK, B.PLDG_AMT * PLDG_RCOGN_RATE / 100 AS CDTLN_AMT FROM TB_BM02M01 A LEFT OUTER JOIN TB_BM02D02 B ON A.CLNT_CD = B.CLNT_CD WHERE 1=1 AND A.USE_YN = 'Y' AND B.CO_CD = #{coCd} AND B.CLNT_CD = #{clntCd}
AND B.USE_YN = #{useYn}
) A ORDER BY B.PLDG_DIV_CD , CLNT_CD ,CO_CD ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
