mapper.select:
SELECT DEPT_ID AS ID, DEPT_NM AS TEXT, DECODE(LV, 1, '#', UP_DEPT_ID) AS PARENT, 'unit' AS TYPE, DEPT_ID, DEPT_NM, UP_DEPT_ID, (SELECT DEPT_NM FROM TB_CM04M01 WHERE DEPT_ID = B.UP_DEPT_ID) AS UP_DEPT_NM, SORT_NO, USE_YN FROM ( SELECT LEVEL LV, A.* FROM TB_CM04M01 A
WHERE USE_YN = #{useYn}
START WITH DEPT_ID = #{coCd} CONNECT BY PRIOR DEPT_ID = UP_DEPT_ID ORDER SIBLINGS BY SORT_NO ) B
