mapper.select:
SELECT ID, EMP_NO, NAME, (SELECT LEVEL_NM FROM TB_CM07M01 WHERE LEVEL_CD = U.LEVEL_CD) AS LEVEL_NM, EMAIL, TEL_NO, USE_YN, TO_CHAR(CREAT_DTTM, 'yyyy-MM-dd') CREAT_DTTM, TO_CHAR(UDT_DTTM, 'yyyy-MM-dd') UDT_DTTM FROM TB_CM06M01 U WHERE 1=1
AND DEPT_ID = #{deptId}
AND USE_YN = #{useYn}
AND NAME = #{searchValue}

SELECT A.ID AS ID, A.NAME AS TEXT, A.DEPT_ID AS PARENT, 'person' AS TYPE, B.DEPT_NM FROM TB_CM06M01 A INNER JOIN TB_CM04M01 B on A.DEPT_ID = B.DEPT_ID
A.USE_YN = #{useYn}
AND CO_CD = #{coCd}

mapper.update:
UPDATE
	TB_CM06M01
SET
	PASSWORD = #{password}
WHERE
	ID = #{userId}

UPDATE
	OAUTH_CLIENT_DETAILS
SET
	CLIENT_SECRET = #{password}
WHERE
	CLIENT_ID = #{userId}
