mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM ,NOTI_KEY ,CO_CD ,NOTI_TITLE ,NOTI_CNTS ,EXPRTN_YN ,TO_CHAR(TO_DATE(T.EXPRTN_DT), 'YYYY-MM-DD') AS EXPRTN_DT ,POPUP_YN ,USE_YN ,SAVE_YN ,READ_CNT ,CREAT_ID ,CREAT_PGM ,CREAT_DTTM ,UDT_ID ,UDT_PGM ,TO_CHAR(UDT_DTTM, 'yyyy-MM-dd') AS UDT_DTTM ,(SELECT CODE_NM FROM TB_CM05M01 WHERE CODE_ID = T.CO_CD) AS CO_NM ,(SELECT NAME FROM TB_CM06M01 WHERE ID = T.CREAT_ID) AS CREAT_NM FROM ( SELECT * FROM TB_CM09M01 WHERE 1=1
AND ${searchType} LIKE '%${searchValue}%'
AND (NOTI_TITLE LIKE '%${searchValue}%' OR NOTI_CNTS LIKE '%${searchValue}%')
AND CO_CD = #{coCd}
AND USE_YN = #{useYn}
ORDER BY UDT_DTTM DESC ) T ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT COUNT(*) FROM TB_CM09M01 WHERE 1=1
AND ${searchType} LIKE '%${searchValue}%'
AND (NOTI_TITLE LIKE '%${searchValue}%' OR NOTI_CNTS LIKE '%${searchValue}%')
AND CO_CD = #{coCd}
AND USE_YN = #{useYn}

