mapper.select:
	SELECT 
 		COUNT(*) 
 	FROM TB_AR01M01 AR01
 	INNER JOIN TB_BM02M01  BM02
 	 	    ON BM02.CLNT_CD = AR01.CLNT_CD
 	WHERE 1=1

 		AND 
		(
			AR01.DLVR_DTTM BETWEEN #{reqDtFrom} AND #{reqDtTo}
			OR
			AR01.REAL_DLVR_DTTM BETWEEN #{reqDtFrom} AND #{reqDtTo}			
		)


		AND AR01.CO_CD = #{coCd}				


		AND AR01.TYP_CD = #{typCd}				


		AND AR01.PRJCT_CD = #{prjctCd}				


		AND AR01.ODR_SEQ = #{odrSeq}				


		AND AR01.WH_CD = #{whCd}				


		AND BM02.CLNT_NM like '%${clntNm}%'	


		AND AR01.CLNT_CD = #{clntCd}				


		AND ${searchType} LIKE '%${searchValue}%'				


		AND AR01.TAXIVC_COPRT= #{taxivcCoprt}				


	    AND AR01.SHIP_SEQ IN (SELECT D01.SHIP_SEQ  
						      FROM TB_AR01D01 D01
						      WHERE D01.PRDT_CD IN (SELECT DISTINCT PRDT_CD
													FROM TB_BM01M01
												   WHERE PRDT_GRP = #{prdtGrp} --출하상세 PRDT_CD로 제품그룹 조회조건 추각
						    				      )
		                      )


		AND AR01.SHIP_RMK LIKE '%${shipRmk}%'				


		AND AR01.SITE_CD = #{siteCd}				


		AND (SELECT SITE_NM FROM TB_SD09M01 SD09 WHERE AR01.SITE_CD = SD09.SITE_CD) LIKE '%${siteNm}%'

SELECT * FROM ( SELECT ROWNUM RNUM, T.CO_CD, FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM, TO_CHAR(TO_DATE(T.REQ_DT), 'YYYY-MM-DD') AS REQ_DT, T.CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.CLNT_CD) AS CLNT_NM, T.SHIP_SEQ, T.PRJCT_CD, FN_CM05M01_CD_TO_NM(T.PRJCT_CD) AS PRJCT_NM, T.WH_CD, FN_CM05M01_CD_TO_NM(T.WH_CD) AS WH_NM, T.TYP_CD, FN_CM05M01_CD_TO_NM(T.TYP_CD) AS TYP_NM, T.OWNER_CD, FN_CM05M01_CD_TO_NM(T.OWNER_CD) AS OWNER_NM, T.SALES_AREA_CD, FN_CM05M01_CD_TO_NM(T.SALES_AREA_CD) AS SALES_AREA_NM, T.MNG_TEL, TO_CHAR(T.DLVR_DTTM, 'yyyy-MM-dd') AS DLVR_DTTM, TO_CHAR(T.REAL_DLVR_DTTM, 'yyyy-MM-dd') AS REAL_DLVR_DTTM, T.DIRTRS_YN, T.DLVR_RMK, T.ADDR_ZIP, T.ADDR_MAIN, T.ADDR_SUB, T.SHIP_RMK, T.SHIP_TOT_QTY, T.SHIP_TOT_WT, T.SHIP_TOT_AMT, (SELECT NVL(SUM(REAL_SHIP_QTY), 0) FROM TB_AR01D01 WHERE SHIP_YN = 'Y' AND SHIP_SEQ = T.SHIP_SEQ) AS REAL_TOT_QTY, (SELECT NVL(SUM(REAL_SHIP_WT), 0) FROM TB_AR01D01 WHERE SHIP_YN = 'Y' AND SHIP_SEQ = T.SHIP_SEQ) AS REAL_TOT_WT, (SELECT NVL(SUM(REAL_SHIP_AMT), 0) FROM TB_AR01D01 WHERE SHIP_YN = 'Y' AND SHIP_SEQ = T.SHIP_SEQ) AS REAL_TOT_AMT, T.TRANS_AMT, T.TRANS_SEQ, T.ODR_SEQ, T.ORDRG_SEQ, T.SALES_MNG, (SELECT NAME FROM TB_CM06M01 WHERE ID = T.SALES_MNG) AS SALES_MNG_NM, T.SHIP_YN, T.SHIP_PROC_ID, TO_CHAR(T.SHIP_DTTM, 'yyyy-MM-dd') AS SHIP_DTTM, T.ODR_CREAT_DIV, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = T.CREAT_ID) AS CREAT_NM, T.CREAT_PGM, TO_CHAR(T.CREAT_DTTM, 'yyyy-MM-dd') AS CREAT_DTTM, T.UDT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = T.UDT_ID) AS UDT_NM, T.UDT_PGM, TO_CHAR(T.UDT_DTTM, 'yyyy-MM-dd') AS UDT_DTTM, ADDR_MAIN || ' ' || ADDR_SUB AS ADDR, (SELECT CODE_ETC FROM TB_CM05M01 WHERE CODE_ID = (SELECT CODE_ETC FROM TB_CM05M01 WHERE CODE_ID = T.WH_CD)) AS WH_CLNT_CD, (SELECT CODE_RPRC FROM TB_CM05M01 WHERE CODE_ID = T.WH_CD) AS WH_TYPE, T.SITE_CD, (SELECT SITE_NM FROM TB_SD09M01 WHERE SITE_CD = T.SITE_CD) AS SITE_NM, T.ALLOC_VEHL_NO, T.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT) AS TAXIVC_COPRT_NM, T.RECPT_YN, T.SHIP_CNT, (SELECT COUNT(*) FROM TB_AR01D01 WHERE SHIP_SEQ = T.SHIP_SEQ) AS D01CNT FROM ( SELECT AR01.* FROM TB_AR01M01 AR01 INNER JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR01.CLNT_CD WHERE 1=1
AND ( AR01.DLVR_DTTM BETWEEN #{reqDtFrom} AND #{reqDtTo} OR AR01.REAL_DLVR_DTTM BETWEEN #{reqDtFrom} AND #{reqDtTo} )
AND AR01.CO_CD = #{coCd}
AND AR01.TYP_CD = #{typCd}
AND AR01.PRJCT_CD = #{prjctCd}
AND AR01.ODR_SEQ = #{odrSeq}
AND AR01.WH_CD = #{whCd}
AND AR01.CLNT_CD = #{clntCd}
AND BM02.CLNT_NM like '%${clntNm}%'
AND ${searchType} LIKE '%${searchValue}%'
AND AR01.SHIP_YN = #{shipYn}
AND AR01.TAXIVC_COPRT= #{taxivcCoprt}
AND AR01.SHIP_SEQ IN (SELECT D01.SHIP_SEQ FROM TB_AR01D01 D01 WHERE D01.PRDT_CD IN (SELECT DISTINCT PRDT_CD FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} --출하상세 PRDT_CD로 제품그룹 조회조건 추각 ) )
AND AR01.SHIP_RMK LIKE '%${shipRmk}%'
AND AR01.SITE_CD = #{siteCd}
AND (SELECT SITE_NM FROM TB_SD09M01 SD09 WHERE AR01.SITE_CD = SD09.SITE_CD) LIKE '%${siteNm}%'
ORDER BY DLVR_DTTM DESC, REAL_DLVR_DTTM DESC, SHIP_SEQ DESC ) T ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT T.ODR_SEQ, T.ODR_DTL_SEQ, T.PRDT_CD, (SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = T.PRDT_CD) AS PRDT_NM, B.PRDT_DIV, B.PRDT_STKN, FN_CM05M01_CD_TO_NM(B.PRDT_STKN) PRDT_STKN_NM, B.PRDT_SIZE, B.PRDT_SPEC, T.PRDT_LEN, T.PRDT_UNIT, FN_CM05M01_CD_TO_NM(T.PRDT_UNIT) AS PRDT_UNIT_NM, T.PRDT_UPR, T.ODR_QTY AS SHIP_QTY, T.ODR_QTY AS REAL_SHIP_QTY, B.PRDT_CNVRSN_WT, T.ODR_WT AS SHIP_WT, T.ODR_WT AS REAL_SHIP_WT, T.ODR_UPR AS SHIP_UPR, DECODE( M.PRJCT_CD, 0, DECODE(B.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'N', T.PRDT_CD, M.CLNT_CD, ''), 0)), DECODE(B.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'N', T.PRDT_CD, M.CLNT_CD, M.PRJCT_CD), 0)) ) AS SHIP_UPR_D, DECODE( M.PRJCT_CD, 0, DECODE(B.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'Y', T.PRDT_CD, M.CLNT_CD, ''), 0)), DECODE(B.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', 'Y', T.PRDT_CD, M.CLNT_CD, M.PRJCT_CD), 0)) ) AS SHIP_UPR_I, T.ODR_UPR AS REAL_SHIP_UPR, T.ODR_AMT AS SHIP_AMT,
(T.ODR_UPR * T.ODR_QTY - T.STOCK_UPR * T.ODR_QTY) AS EPCT_PAL, T.ODR_AMT AS REAL_SHIP_AMT FROM TB_SD04D01 T INNER JOIN TB_SD04M01 M ON T.ODR_SEQ = M.ODR_SEQ INNER JOIN TB_BM01M01 B ON T.PRDT_CD = B.PRDT_CD WHERE T.ODR_SEQ = #{odrSeq}
AND ODR_DTL_SEQ IN
#{odrDtlSeq}
ORDER BY T.ODR_DTL_SEQ
