mapper.select:
	SELECT 
 		COUNT(*) 
 	FROM TB_AR01M01
 	WHERE 1=1

		AND REQ_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}		


		AND CO_CD = #{coCd}				


		AND TYP_CD = #{typCd}				


		AND PRJCT_CD = #{prjctCd}				


		AND ODR_SEQ = #{odrSeq}				


		AND WH_CD = #{whCd}				


		AND CLNT_CD = #{clntCd}				


		AND ${searchType} LIKE '%${searchValue}%'

SELECT * FROM ( SELECT ROWNUM RNUM, T.CO_CD, FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM, TO_CHAR(TO_DATE(T.REQ_DT), 'YYYY-MM-DD') AS REQ_DT, T.CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.CLNT_CD) AS CLNT_NM, T.SHIP_SEQ, T.PRJCT_CD, T.MAKR_CD, T.WH_CD, FN_CM05M01_CD_TO_NM(T.WH_CD) AS WH_NM, T.TYP_CD, T.OWNER_CD, T.IMP_YN, T.SALES_AREA_CD, T.MNG_TEL, TO_CHAR(T.DLVR_DTTM, 'yyyy-MM-dd') AS DLVR_DTTM, T.DIRTRS_YN, T.DLVR_RMK, T.ADDR_ZIP, T.ADDR_MAIN, T.ADDR_SUB, T.SHIP_RMK, T.SHIP_TOT_QTY, T.SHIP_TOT_WT, T.SHIP_TOT_AMT, T.REAL_TOT_QTY, T.REAL_TOT_WT, T.REAL_TOT_AMT, T.TRANS_AMT, T.ODR_SEQ, T.ORDRG_SEQ, T.SALES_MNG, T.SHIP_YN, T.SHIP_PROC_ID, TO_CHAR(T.SHIP_DTTM, 'yyyy-MM-dd') AS SHIP_DTTM, T.ODR_CREAT_DIV, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, T.CREAT_PGM, T.CREAT_DTTM, T.UDT_ID, T.UDT_PGM, T.UDT_DTTM, ADDR_MAIN || ' ' || ADDR_SUB AS ADDR FROM ( SELECT * FROM TB_AR01M01 WHERE 1=1
AND REQ_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND CO_CD = #{coCd}
AND TYP_CD = #{typCd}
AND PRJCT_CD = #{prjctCd}
AND ODR_SEQ = #{odrSeq}
AND WH_CD = #{whCd}
AND CLNT_CD = #{clntCd}
AND ${searchType} LIKE '%${searchValue}%'
AND SHIP_YN = #{shipYn}
ORDER BY UDT_DTTM DESC ) T ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT T.ODR_SEQ, T.ODR_DTL_SEQ, T.PRDT_CD, (SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = T.PRDT_CD) AS PRDT_NM, FN_CM05M01_CD_TO_NM(B.PRDT_STKN) PRDT_STKN_NM, T.PRDT_UNIT AS PRDT_UNIT_NM, T.PRDT_LEN, T.PRDT_UPR, T.ODR_QTY AS SHIP_QTY, B.PRDT_CNVRSN_WT, T.ODR_WT AS SHIP_WT, T.ODR_UPR AS SHIP_UPR, T.ODR_AMT AS SHIP_AMT, T.ODR_DTL_RMK AS ORDRG_DTL_RMK FROM TB_SD04D01 T INNER JOIN TB_BM01M01 B ON T.PRDT_CD = B.PRDT_CD WHERE ODR_SEQ = #{odrSeq}
AND ODR_DTL_SEQ IN
#{odrDtlSeq}
ORDER BY T.ODR_DTL_SEQ
