SELECT
	COUNT(*)
FROM
(
 	SELECT 
		T.CO_CD

		   , (SELECT CLNT_NM FROM TB_BM02M01  BM02
		                     INNER JOIN  TB_OD01M01 OD01M01  ON BM02.CLNT_CD = OD01M01.CLNT_CD 
		                     WHERE  T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ  AND T.CREAT_PGM LIKE 'OD'||'%' ) AS PCHS_CLNT_NM


		 ,   '' AS PCHS_CLNT_NM

	FROM 
		TB_AR02M01 T
		LEFT JOIN TB_BM01M01 BM01M01 ON T.TRST_PRDT_CD = BM01M01.PRDT_CD
		LEFT JOIN TB_BM02M01 BM02M01 ON T.TRST_CLNT_CD = BM02M01.CLNT_CD
		LEFT JOIN TB_SD05M01 SD05M01 ON T.PRJCT_CD = SD05M01.PRJCT_CD
		LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD
		LEFT JOIN TB_CM05M01 CM05M01 ON NVL(SD05M01.CLMN_DIV_CD, BM02M01.SELL_CLMN_DIV_CD) = CM05M01.CODE_ID
		LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID

			LEFT JOIN TB_OD01M01 OD01M01 ON T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ	

 	WHERE 
 		SELPCH_CD = #{selpchCd}

			AND T.CO_CD = #{coCd}				


			AND T.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}		


			AND T.TRST_CLNT_CD = #{clntCd}


			AND T.CLNT_NM LIKE '%${clntNm}%'


			AND T.PRDT_DIV = #{prdtDiv}				


			AND T.PRDT_SPEC LIKE '%${prdtSpec}%'


			AND T.TAXIVC_COPRT = #{taxivcCoprt}				


			AND T.DIRTRS_YN = #{dirtrsYn}				


			AND T.TYP_CD = #{typCd}				


			AND T.PRJCT_CD = #{prjctCd}				


			AND T.SITE_CD = #{siteCd}				


			AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'				


			AND T.PRDT_STKN = #{pdsk}				




					AND T.BILG_CERT_NO IS NOT NULL 		


					AND T.BILG_CERT_NO IS NULL 




			AND T.SALES_MNG = #{salesMng}				


			AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'				


			AND T.WH_CD = #{whCd}


			AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'


			AND BM01M01.PRDT_GRP = #{prdtGrp}


			AND T.TRSP_TYP_CD = #{trspTypCd}

		ORDER BY TRST_DT DESC, T.CLNT_NM, T.TYP_CD, TRST_PRDT_CD, T.PRDT_STKN, T.PRDT_SIZE, T.PRDT_LEN
) A


		WHERE PCHS_CLNT_NM LIKE '%'|| #{pchsClntNm} || '%'

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM FROM ( SELECT T.CO_CD, T.TRST_CERTI_NO, T.SELPCH_CD, FN_CM05M01_CD_TO_NM(T.SELPCH_CD) AS SELPCH_NM, T.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT) AS TAXIVC_COPRT_NM, TO_CHAR(TO_DATE(T.TRST_DT), 'YYYY-MM-DD') AS TRST_DT, BM02M01.SELL_VAT_CD, NVL((SELECT CODE_RPRC FROM TB_CM05M01 WHERE CODE_ID = BM02M01.SELL_VAT_CD), 10) AS SELL_VAT, NVL(SD05M01.CLMN_DIV_CD, BM02M01.SELL_CLMN_DIV_CD) AS PCHS_CLMN_DIV_CD, CASE WHEN SD05M01.CLMN_DIV_CD IS NULL THEN FN_CM05M01_CD_TO_NM(BM02M01.SELL_CLMN_DIV_CD) || ' ' || NVL2(BM02M01.SELL_CLMN_DAY,FN_CM05M01_CD_TO_NM(BM02M01.SELL_CLMN_DAY)||'일','') || FN_CM05M01_CD_TO_NM(BM02M01.SELL_PMNT_MTD_CD) ELSE FN_CM05M01_CD_TO_NM(SD05M01.CLMN_DIV_CD) END AS PCHS_CLMN_DIV_NM , CASE WHEN CM05M01.CODE_RPRC = 'MON' THEN TO_CHAR(LAST_DAY(ADD_MONTHS(TRUNC(TO_DATE(T.TRST_DT),'MM'), CM05M01.CODE_ETC) -1), 'YYYY-MM-DD') WHEN CM05M01.CODE_RPRC = 'DAY' THEN TO_CHAR(TO_DATE(T.TRST_DT) -1 + CM05M01.CODE_ETC, 'YYYY-MM-DD') END AS PCHS_CLMN_DAY, T.TRST_CLNT_CD, T.TRST_CLNT_CD AS CLNT_CD, T.TRST_PRDT_CD, T.TRST_PRDT_CD AS PRDT_CD, BM01M01.PRDT_NM, BM01M01.PRDT_GRP, FN_CM05M01_CD_TO_NM(BM01M01.PRDT_GRP) PRDT_GRP_NM, T.OWNER_CD, FN_CM05M01_CD_TO_NM(T.OWNER_CD) AS OWNER_NM, T.IMP_YN, T.TYP_CD, FN_CM05M01_CD_TO_NM(T.TYP_CD) AS TYP_NM, T.TRSP_TYP_CD, FN_CM05M01_CD_TO_NM(T.TRSP_TYP_CD) AS TRSP_TYP_NM, T.TRST_RPRC_SEQ, T.TRST_DTL_SEQ, T.PRDT_LEN, T.ODR_NO, T.SALES_MNG, CM06M01.NAME AS SALES_MNG_NM, CM06M01.DEPT_ID AS DEPT_ID, (SELECT DEPT_NM FROM TB_CM04M01 WHERE DEPT_ID = CM06M01.DEPT_ID) AS DEPT_NM, T.PRJCT_CD, SD05M01.PRJCT_NM, T.MAKR_CD, FN_CM05M01_CD_TO_NM(T.MAKR_CD) AS MAKR_NM, T.WH_CD, FN_CM05M01_CD_TO_NM(T.WH_CD) AS WH_NM, T.MNG_TEL, TO_CHAR(T.DLVR_DTTM, 'YYYY-MM-DD') AS DLVR_DTTM, T.DIRTRS_YN, T.PRDT_DIV, T.PRDT_STKN, FN_CM05M01_CD_TO_NM(T.PRDT_STKN) AS PRDT_STKN_NM, T.PRDT_SIZE, NVL(FN_CM05M01_CD_TO_NM(T.PRDT_SIZE), T.PRDT_SIZE) AS PRDT_SIZE_NM, T.PRDT_UNIT, T.PRDT_UPR, T.STOCK_UPR, T.PCHS_UPR, T.SELL_UPR, T.TRST_QTY, T.TRST_WT, T.TRST_UPR, T.TRST_AMT, T.REAL_TRST_QTY, T.REAL_TRST_WT, T.REAL_TRST_UPR, T.REAL_TRST_AMT, T.BILG_QTY, T.BILG_WT, T.BILG_UPR, T.BILG_AMT, NVL(T.TRST_DC_AMT, '0') AS TRST_DC_AMT, NVL(T.ETC_AMT, '0') AS ETC_AMT, T.TRSP_RMK, NVL(T.TRANS_AMT, '0') AS TRANS_AMT, T.SHIP_VEH_NO, T.CLNT_NM, T.BILG_CERT_NO, CASE WHEN T.BILG_CERT_NO IS NOT NULL THEN 'Y' ELSE 'N' END BILG_YN, CASE WHEN T.BILG_AMT < 0 THEN 'Y' ELSE 'N' END RETURN_YN, NVL(T.LOSS_RATE, '0') AS LOSS_RATE, T.ODR_CREAT_DIV, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, T.CREAT_PGM, TO_CHAR(T.CREAT_DTTM, 'yyyy-MM-dd') AS CREAT_DTTM, T.UDT_ID, T.UDT_PGM, TO_CHAR(T.UDT_DTTM, 'yyyy-MM-dd') AS UDT_DTTM, T.SALES_AREA_CD, T.SITE_CD, SD09M01.SITE_NM, T.BILG_VAT_AMT, T.BILG_VAT_AMT + T.BILG_AMT AS TOT_AMT, T.PRDT_SPEC, T.RFF_AEA, T.ORGN_TAX_BILG_NO, T.RFF_APG, T.SHIP_CNT
, (SELECT CLNT_NM FROM TB_BM02M01 BM02 INNER JOIN TB_OD01M01 OD01M01 ON BM02.CLNT_CD = OD01M01.CLNT_CD WHERE T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ AND T.CREAT_PGM LIKE 'OD'||'%' ) AS PCHS_CLNT_NM
, '' AS PCHS_CLNT_NM
, OD01M01.SELL_CLNT_CD AS SELL_CLNT_CD , (SELECT CLNT_NM FROM TB_BM02M01 BM02 WHERE OD01M01.SELL_CLNT_CD = BM02.CLNT_CD) AS SELL_CLNT_NM
FROM TB_AR02M01 T LEFT JOIN TB_BM01M01 BM01M01 ON T.TRST_PRDT_CD = BM01M01.PRDT_CD LEFT JOIN TB_BM02M01 BM02M01 ON T.TRST_CLNT_CD = BM02M01.CLNT_CD LEFT JOIN TB_SD05M01 SD05M01 ON T.PRJCT_CD = SD05M01.PRJCT_CD LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD LEFT JOIN TB_CM05M01 CM05M01 ON NVL(SD05M01.CLMN_DIV_CD, BM02M01.SELL_CLMN_DIV_CD) = CM05M01.CODE_ID LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID
LEFT JOIN TB_OD01M01 OD01M01 ON T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ
WHERE SELPCH_CD = #{selpchCd}
AND T.CO_CD = #{coCd}
AND T.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND T.TRST_CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%${clntNm}%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.PRDT_SPEC LIKE '%${prdtSpec}%'
AND T.TAXIVC_COPRT = #{taxivcCoprt}
AND T.DIRTRS_YN = #{dirtrsYn}
AND T.TYP_CD = #{typCd}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_STKN = #{pdsk}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND T.SALES_MNG = #{salesMng}
AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'
AND T.WH_CD = #{whCd}
AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'
AND BM01M01.PRDT_GRP = #{prdtGrp}
AND T.TRSP_TYP_CD = #{trspTypCd}
ORDER BY TRST_DT DESC, T.CLNT_NM, T.TYP_CD, TRST_PRDT_CD, T.PRDT_STKN, T.PRDT_SIZE, T.PRDT_LEN ) A
WHERE PCHS_CLNT_NM LIKE '%'|| #{pchsClntNm} || '%'
)
WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

