SELECT T1.*, NVL(B.SELL_PAY_DIV_CD, 'PAYDIV02') AS RFF_CNO, NVL(B.SELL_VAT_CD, 'VAT01') AS RFF_VA, B.CRN AS TAXIVC_COPRT_NO, B.REPST_NM AS TAXIVC_COPRT_CEO, B.CLNT_NM AS TAXIVC_COPRT_NM, NVL(B.BIZCON_NM, '-') AS TAX_COPRT_BIZCON, NVL(B.BSTY_NM, '-') AS TAXVIC_COPRT_BSTY, (SELECT CRN FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_CRN, (SELECT REPST_NM FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_CEO, (SELECT BIZ_ADDR || ' ' ||BIZ_ADDR_DTL FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_ADDR, (SELECT BIZCON_NM FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_BIZCON, (SELECT BSTY_NM FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_BSTY, '1' AS COMII_EM, B.BIZ_TEL_NO AS COMII_TE, B.BIZ_FAX_NO AS COMII_FX, (SELECT CODE_ETC FROM TB_CM05M01 WHERE CODE_ID = B.CRN_TYP) AS CTAIV_BJ_1, B.BANK_CD, FN_CM05M01_CD_TO_NM(B.BANK_CD) AS BANK_NM, B.BKAC_NO, B.BKAC_OWNER FROM ( SELECT T.CO_CD, T.TAXIVC_COPRT AS TAXIVC_COPRT_CD, (SELECT CODE_ETC FROM TB_CM05M01 WHERE CODE_ID = T.TAXIVC_COPRT) AS TAXIVC_COPRT, T.TRST_CLNT_CD AS CLNT_CD, T.CLNT_NM, T.SELPCH_CD, MAX(T.TRST_DT) AS TRST_DT, SUM(T.TRST_QTY) AS TRST_QTY, NVL(SUM(T.TRST_WT), 0) AS TRST_WT, SUM(T.TRST_UPR) AS TRST_UPR, SUM(T.TRST_AMT) AS TRST_AMT, SUM(T.REAL_TRST_QTY) AS REAL_TRST_QTY, SUM(T.REAL_TRST_WT) AS REAL_TRST_WT, SUM(T.REAL_TRST_UPR) AS REAL_TRST_UPR, SUM(T.REAL_TRST_AMT) AS REAL_TRST_AMT, SUM(T.BILG_QTY) AS BILG_QTY, SUM(T.BILG_WT) AS BILG_WT, SUM(T.BILG_UPR) AS BILG_UPR, SUM(T.BILG_AMT) AS BILG_AMT, SUM(T.TRST_DC_AMT) AS TRST_DC_AMT, SUM(T.ETC_AMT) AS ETC_AMT, SUM(T.TRANS_AMT) AS TRANS_AMT, COUNT(*) AS TAX_DMS5_7240, SUM(T.BILG_AMT) AS TAX_MOA5_23, SUM(T.BILG_VAT_AMT) AS TAX_MOA5_124, SUM(T.BILG_AMT) + SUM(T.BILG_VAT_AMT) AS TAX_MOA5_388, SUM(T.BILG_AMT) + SUM(T.BILG_VAT_AMT) AS TAX_MOA5_9, NVL2(MAX(T.ORGN_TAX_BILG_NO), 'RFFGN102', CASE WHEN SUM(T.BILG_AMT) < 0 THEN 'RFFGN102' ELSE 'RFFGN101' END) AS RFF_GN1, 'RFFGN201' AS RFF_GN2, MAX(T.ORGN_TAX_BILG_NO) AS ORGN_TAX_BILG_NO, CASE WHEN SUM(T.BILG_AMT) < 0 THEN 'RFFAEA03' ELSE MAX(T.RFF_AEA) END AS RFF_AEA, 'BGM12259' AS BGM_1225, '1' AS BGM_4343, 'S' AS XML_FLOW, #{userId} AS TAX_CREAT_ID, #{userNm} AS TAX_CREAT_NM, COUNT(DISTINCT NVL(T.SITE_CD, 0)) AS SITE_CNT, MIN(SD09M01.SITE_NM) AS SITE_NM FROM TB_AR02M01 T LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD WHERE TRST_CERTI_NO IN
#{item}
GROUP BY T.CO_CD, T.TAXIVC_COPRT, T.TRST_CLNT_CD, T.SELPCH_CD, T.CLNT_NM ) T1 INNER JOIN TB_BM02M01 B ON T1.TAXIVC_COPRT = B.CLNT_CD

mapper.update:
UPDATE TB_AR02M01
