WHERE TRST_CERTI_NO = #{trstCertiNo}

UPDATE TB_AR02M01
SET BILG_CERT_NO = ''
WHERE BILG_CERT_NO = #{bilgCertNo}

mapper.select:
SELECT FN_CM05M01_CD_TO_NM(B.PRDT_DIV) AS PRDT_DIV_NM, FN_CM05M01_CD_TO_NM(B.PRDT_STKN) AS PRDT_STKN_NM, A.TRST_PRDT_CD, B.PRDT_NM, CASE WHEN IMP_YN = 'Y' THEN '수입' ELSE '국산' END IMP_YN, FN_CM05M01_CD_TO_NM(A.MAKR_CD) AS MAKR_NM, B.PRDT_SPEC, A.PRDT_LEN, SUM(BILG_QTY) BILG_QTY, SUM(BILG_WT) BILG_WT, AVG(BILG_UPR) BILG_UPR, SUM(BILG_AMT) BILG_AMT, SUM(BILG_VAT_AMT) TAX_AMT FROM TB_AR02M01 A INNER JOIN TB_BM01M01 B ON A.TRST_PRDT_CD = B.PRDT_CD INNER JOIN TB_BM02M01 C ON A.TRST_CLNT_CD = C.CLNT_CD LEFT OUTER JOIN TB_SD09M01 D ON A.CO_CD = D.CO_CD AND A.SITE_CD = D.SITE_CD WHERE SELPCH_CD = #{selpchCd}
AND A.CO_CD = #{coCd}
AND A.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND B.PRDT_DIV = #{prdtDiv}
AND A.TRST_CLNT_CD = #{clntCd}
AND C.CLNT_NM LIKE '%${clntNm}%'
AND A.SITE_CD = #{siteCd}
AND D.SITE_NM LIKE '%${siteNm}%'
AND A.BILG_CERT_NO IS NOT NULL
AND A.BILG_CERT_NO IS NULL
AND A.TYP_CD = #{typCd}
AND B.PRDT_GRP = #{prdtGrp}
GROUP BY B.PRDT_DIV, B.PRDT_NM, A.TRST_PRDT_CD, B.PRDT_STKN, IMP_YN, A.MAKR_CD, B.PRDT_SPEC, A.PRDT_LEN ORDER BY B.PRDT_DIV, B.PRDT_NM, A.TRST_PRDT_CD, B.PRDT_STKN, IMP_YN, A.MAKR_CD, B.PRDT_SPEC, A.PRDT_LEN

SELECT COUNT(*) FROM (SELECT TRST_RPRC_SEQ, TRST_DTL_SEQ, ODR_NO, PRDT_NM, TRST_PRDT_CD , MAX(ORGN_TAX_BILG_NO) ORGN_TAX_BILG_NO, MAX(PCH_CLNT) PCH_CLNT, MAX(PCH_CLNT_NM) PCH_CLNT_NM, SUM(PCH_QTY) PCH_QTY, SUM(PCH_WT) PCH_WT, SUM(PCH_UPR) PCH_UPR, SUM(PCH_AMT) PCH_AMT, SUM(PCH_VAT_AMT) PCH_VAT_AMT, MAX(SEL_CLNT) SEL_CLNT, MAX(SEL_CLNT_NM) SEL_CLNT_NM, SUM(SEL_QTY) SEL_QTY, SUM(SEL_WT) SEL_WT, SUM(SEL_UPR) SEL_UPR, SUM(SEL_AMT) SEL_AMT, SUM(SEL_VAT_AMT) SEL_VAT_AMT, SUM(SEL_AMT)-SUM(PCH_AMT) "이익액", max(SELL_CLMN_DIV_NM) SELL_CLMN_DIV_NM
,max(SITE_CD) SITE_CD, max(SITE_NM) SITE_NM
FROM ( SELECT TRST_RPRC_SEQ, TRST_DTL_SEQ, ODR_NO, TRST_PRDT_CD, SELPCH_CD , ORGN_TAX_BILG_NO, TRST_CLNT_CD , BILG_QTY , BILG_WT , BILG_UPR , BILG_AMT , BILG_VAT_AMT, decode(SELPCH_CD,'SELPCH1',TRST_CLNT_CD) AS PCH_CLNT, decode(SELPCH_CD,'SELPCH1',B.CLNT_NM) AS PCH_CLNT_NM, decode(SELPCH_CD,'SELPCH1',BILG_QTY) AS PCH_QTY, decode(SELPCH_CD,'SELPCH1',BILG_WT) AS PCH_WT, decode(SELPCH_CD,'SELPCH1',BILG_UPR) AS PCH_UPR, decode(SELPCH_CD,'SELPCH1',BILG_AMT) AS PCH_AMT, decode(SELPCH_CD,'SELPCH1',BILG_VAT_AMT) AS PCH_VAT_AMT, decode(SELPCH_CD,'SELPCH2',TRST_CLNT_CD) AS SEL_CLNT, decode(SELPCH_CD,'SELPCH2',B.CLNT_NM) AS SEL_CLNT_NM, decode(SELPCH_CD,'SELPCH2',BILG_QTY) AS SEL_QTY, decode(SELPCH_CD,'SELPCH2',BILG_WT) AS SEL_WT, decode(SELPCH_CD,'SELPCH2',BILG_UPR) AS SEL_UPR, decode(SELPCH_CD,'SELPCH2',BILG_AMT) AS SEL_AMT, decode(SELPCH_CD,'SELPCH2',BILG_VAT_AMT) AS SEL_VAT_AMT, decode(SELPCH_CD,'SELPCH2',FN_CM05M01_CD_TO_NM(B.SELL_CLMN_DIV_CD)) AS SELL_CLMN_DIV_NM
,A.SITE_CD, (SELECT SITE_NM FROM TB_SD09M01 WHERE SITE_CD = A.SITE_CD) SITE_NM
FROM TB_AR02M01 A LEFT OUTER JOIN TB_BM02M01 B ON B.CLNT_CD=A.TRST_CLNT_CD LEFT OUTER JOIN TB_SD09M01 C ON C.SITE_CD=A.SITE_CD WHERE 1=1
AND A.CO_CD = #{coCd}
AND A.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND A.TYP_CD = #{typCd}
AND A.PRDT_DIV = #{prdtDiv}
AND ORGN_TAX_BILG_NO IS NOT NULL
AND ORGN_TAX_BILG_NO IS NULL
) T INNER JOIN TB_BM01M01 B ON TRST_PRDT_CD = B.PRDT_CD
B.PRDT_GRP = #{prdtGrp}
GROUP BY TRST_RPRC_SEQ, TRST_DTL_SEQ, ODR_NO,PRDT_NM,TRST_PRDT_CD HAVING 1 = 1
AND (MAX(PCH_CLNT) = #{clntCd} OR MAX(SEL_CLNT) = #{clntCd})
AND (MAX(PCH_CLNT_NM) LIKE '%'|| #{clntNm} ||'%' OR MAX(SEL_CLNT_NM) LIKE '%'|| #{clntNm} ||'%')
AND MAX(SITE_CD) = #{siteCd}
AND MAX(SITE_NM) LIKE '%'|| #{siteNm} || '%'
ORDER BY PRDT_NM, TRST_PRDT_CD)

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT TRST_RPRC_SEQ, TRST_DTL_SEQ, ODR_NO, PRDT_NM, TRST_PRDT_CD , FN_CM05M01_CD_TO_NM(MAX(PRDT_GRP)) AS PRDT_GRP_NM, FN_CM05M01_CD_TO_NM(MAX(PRDT_DIV)) AS PRDT_DIV_NM, MAX(ORGN_TAX_BILG_NO) ORGN_TAX_BILG_NO, MAX(PCH_CLNT) PCH_CLNT, MAX(PCH_CLNT_NM) PCH_CLNT_NM, SUM(PCH_QTY) PCH_QTY, SUM(PCH_WT) PCH_WT, SUM(PCH_UPR) PCH_UPR, SUM(PCH_AMT) PCH_AMT, SUM(PCH_VAT_AMT) PCH_VAT_AMT, MAX(SEL_CLNT) SEL_CLNT, MAX(SEL_CLNT_NM) SEL_CLNT_NM, SUM(SEL_QTY) SEL_QTY, SUM(SEL_WT) SEL_WT, SUM(SEL_UPR) SEL_UPR, SUM(SEL_AMT) SEL_AMT, SUM(SEL_VAT_AMT) SEL_VAT_AMT, SUM(SEL_AMT)-SUM(PCH_AMT) "이익액", max(SELL_CLMN_DIV_NM) SELL_CLMN_DIV_NM
,max(SITE_CD) SITE_CD, max(SITE_NM) SITE_NM
FROM ( SELECT TRST_RPRC_SEQ, TRST_DTL_SEQ, ODR_NO, TRST_PRDT_CD, SELPCH_CD , ORGN_TAX_BILG_NO, TRST_CLNT_CD , BILG_QTY , BILG_WT , BILG_UPR , BILG_AMT , BILG_VAT_AMT, decode(SELPCH_CD,'SELPCH1',TRST_CLNT_CD) AS PCH_CLNT, decode(SELPCH_CD,'SELPCH1',B.CLNT_NM) AS PCH_CLNT_NM, decode(SELPCH_CD,'SELPCH1',BILG_QTY) AS PCH_QTY, decode(SELPCH_CD,'SELPCH1',BILG_WT) AS PCH_WT, decode(SELPCH_CD,'SELPCH1',BILG_UPR) AS PCH_UPR, decode(SELPCH_CD,'SELPCH1',BILG_AMT) AS PCH_AMT, decode(SELPCH_CD,'SELPCH1',BILG_VAT_AMT) AS PCH_VAT_AMT, decode(SELPCH_CD,'SELPCH2',TRST_CLNT_CD) AS SEL_CLNT, decode(SELPCH_CD,'SELPCH2',B.CLNT_NM) AS SEL_CLNT_NM, decode(SELPCH_CD,'SELPCH2',BILG_QTY) AS SEL_QTY, decode(SELPCH_CD,'SELPCH2',BILG_WT) AS SEL_WT, decode(SELPCH_CD,'SELPCH2',BILG_UPR) AS SEL_UPR, decode(SELPCH_CD,'SELPCH2',BILG_AMT) AS SEL_AMT, decode(SELPCH_CD,'SELPCH2',BILG_VAT_AMT) AS SEL_VAT_AMT, decode(SELPCH_CD,'SELPCH2',FN_CM05M01_CD_TO_NM(B.SELL_CLMN_DIV_CD)) AS SELL_CLMN_DIV_NM
,A.SITE_CD, (SELECT SITE_NM FROM TB_SD09M01 WHERE SITE_CD = A.SITE_CD) SITE_NM
FROM TB_AR02M01 A LEFT OUTER JOIN TB_BM02M01 B ON B.CLNT_CD=A.TRST_CLNT_CD LEFT OUTER JOIN TB_SD09M01 C ON C.SITE_CD=A.SITE_CD WHERE 1=1
AND A.CO_CD = #{coCd}
AND A.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND A.TYP_CD = #{typCd}
AND A.PRDT_DIV = #{prdtDiv}
AND ORGN_TAX_BILG_NO IS NOT NULL
AND ORGN_TAX_BILG_NO IS NULL
) T INNER JOIN TB_BM01M01 B ON TRST_PRDT_CD = B.PRDT_CD
B.PRDT_GRP = #{prdtGrp}
GROUP BY TRST_RPRC_SEQ, TRST_DTL_SEQ, ODR_NO,PRDT_NM,TRST_PRDT_CD HAVING 1 = 1
AND (MAX(PCH_CLNT) = #{clntCd} OR MAX(SEL_CLNT) = #{clntCd})
AND (MAX(PCH_CLNT_NM) LIKE '%'|| #{clntNm} ||'%' OR MAX(SEL_CLNT_NM) LIKE '%'|| #{clntNm} ||'%')
AND MAX(SITE_CD) = #{siteCd}
AND MAX(SITE_NM) LIKE '%'|| #{siteNm} || '%'
ORDER BY
SITE_CD,
PRDT_GRP_NM, PRDT_DIV_NM ) A )
WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(*) FROM ( WITH TT AS ( SELECT T.CO_CD, T.TAXIVC_COPRT, T.SELPCH_CD, T.TRST_CLNT_CD, T.CLNT_NM, SUBSTR(T.TRST_DT,1,6) yymm, sum(T.BILG_AMT+T.BILG_VAT_AMT) BILG_TOT_AMT, count(*) cnt FROM TB_AR02M01 T LEFT JOIN TB_BM01M01 BM01M01 ON T.TRST_PRDT_CD = BM01M01.PRDT_CD LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID WHERE T.SELPCH_CD = #{selpchCd}
AND T.CO_CD = #{coCd}
AND T.TRST_DT BETWEEN #{searchYear}||'0101' AND #{searchYear}||'1231'
AND T.TRST_CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%${clntNm}%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.TAXIVC_COPRT = #{taxivcCoprt}
AND T.DIRTRS_YN = #{dirtrsYn}
AND T.TYP_CD = #{typCd}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_STKN = #{pdsk}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND T.SALES_MNG = #{salesMng}
AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'
AND T.WH_CD = #{whCd}
AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'
AND BM01M01.PRDT_GRP = #{prdtGrp}
AND T.TRSP_TYP_CD = #{trspTypCd}
GROUP BY T.CO_CD, T.TAXIVC_COPRT, T.SELPCH_CD, T.TRST_CLNT_CD, T.CLNT_NM, SUBSTR(T.TRST_DT,1,6) ) SELECT CO_CD, TAXIVC_COPRT, SELPCH_CD, TRST_CLNT_CD, CLNT_NM, sum(BILG_TOT_AMT) TOT_AMT, sum(CNT) TOT_CNT, sum(CASE WHEN substr(yymm,5,2) = 01 THEN BILG_TOT_AMT ELSE 0 END) "01월합계", sum(CASE WHEN substr(yymm,5,2) = 02 THEN BILG_TOT_AMT ELSE 0 END) "02월합계", sum(CASE WHEN substr(yymm,5,2) = 03 THEN BILG_TOT_AMT ELSE 0 END) "03월합계", sum(CASE WHEN substr(yymm,5,2) = 04 THEN BILG_TOT_AMT ELSE 0 END) "04월합계", sum(CASE WHEN substr(yymm,5,2) = 05 THEN BILG_TOT_AMT ELSE 0 END) "05월합계", sum(CASE WHEN substr(yymm,5,2) = 06 THEN BILG_TOT_AMT ELSE 0 END) "06월합계", sum(CASE WHEN substr(yymm,5,2) = 07 THEN BILG_TOT_AMT ELSE 0 END) "07월합계", sum(CASE WHEN substr(yymm,5,2) = 08 THEN BILG_TOT_AMT ELSE 0 END) "08월합계", sum(CASE WHEN substr(yymm,5,2) = 09 THEN BILG_TOT_AMT ELSE 0 END) "09월합계", sum(CASE WHEN substr(yymm,5,2) = 10 THEN BILG_TOT_AMT ELSE 0 END) "10월합계", sum(CASE WHEN substr(yymm,5,2) = 11 THEN BILG_TOT_AMT ELSE 0 END) "11월합계", sum(CASE WHEN substr(yymm,5,2) = 12 THEN BILG_TOT_AMT ELSE 0 END) "12월합계", sum(CASE WHEN substr(yymm,5,2) = 01 THEN cnt ELSE 0 END) "01월건수", sum(CASE WHEN substr(yymm,5,2) = 02 THEN cnt ELSE 0 END) "02월건수", sum(CASE WHEN substr(yymm,5,2) = 03 THEN cnt ELSE 0 END) "03월건수", sum(CASE WHEN substr(yymm,5,2) = 04 THEN cnt ELSE 0 END) "04월건수", sum(CASE WHEN substr(yymm,5,2) = 05 THEN cnt ELSE 0 END) "05월건수", sum(CASE WHEN substr(yymm,5,2) = 06 THEN cnt ELSE 0 END) "06월건수", sum(CASE WHEN substr(yymm,5,2) = 07 THEN cnt ELSE 0 END) "07월건수", sum(CASE WHEN substr(yymm,5,2) = 08 THEN cnt ELSE 0 END) "08월건수", sum(CASE WHEN substr(yymm,5,2) = 09 THEN cnt ELSE 0 END) "09월건수", sum(CASE WHEN substr(yymm,5,2) = 10 THEN cnt ELSE 0 END) "10월건수", sum(CASE WHEN substr(yymm,5,2) = 11 THEN cnt ELSE 0 END) "11월건수", sum(CASE WHEN substr(yymm,5,2) = 12 THEN cnt ELSE 0 END) "12월건수" FROM TT GROUP BY CO_CD, TAXIVC_COPRT, SELPCH_CD, TRST_CLNT_CD, CLNT_NM ORDER BY CO_CD, TAXIVC_COPRT, SELPCH_CD, TRST_CLNT_CD, CLNT_NM )

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM FROM ( WITH TT AS ( SELECT T.CO_CD, T.TAXIVC_COPRT, T.SELPCH_CD, T.TRST_CLNT_CD, T.CLNT_NM, SUBSTR(T.TRST_DT,1,6) yymm, sum(T.BILG_AMT+T.BILG_VAT_AMT) BILG_TOT_AMT, count(*) cnt FROM TB_AR02M01 T LEFT JOIN TB_BM01M01 BM01M01 ON T.TRST_PRDT_CD = BM01M01.PRDT_CD LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID WHERE T.SELPCH_CD = #{selpchCd}
AND T.CO_CD = #{coCd}
AND T.TRST_DT BETWEEN #{searchYear}||'0101' AND #{searchYear}||'1231'
AND T.TRST_CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%${clntNm}%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.TAXIVC_COPRT = #{taxivcCoprt}
AND T.DIRTRS_YN = #{dirtrsYn}
AND T.TYP_CD = #{typCd}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_STKN = #{pdsk}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND T.SALES_MNG = #{salesMng}
AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'
AND T.WH_CD = #{whCd}
AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'
AND BM01M01.PRDT_GRP = #{prdtGrp}
AND T.TRSP_TYP_CD = #{trspTypCd}
GROUP BY T.CO_CD, T.TAXIVC_COPRT, T.SELPCH_CD, T.TRST_CLNT_CD, T.CLNT_NM, SUBSTR(T.TRST_DT,1,6) ) SELECT CO_CD, TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(TAXIVC_COPRT) AS TAXIVC_COPRT_NM, SELPCH_CD, TRST_CLNT_CD, CLNT_NM, round(sum(BILG_TOT_AMT)/#{searchItem}) TOT_AMT, sum(CNT) TOT_CNT, round(sum(CASE WHEN substr(yymm,5,2) = 01 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "01월합계", round(sum(CASE WHEN substr(yymm,5,2) = 02 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "02월합계", round(sum(CASE WHEN substr(yymm,5,2) = 03 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "03월합계", round(sum(CASE WHEN substr(yymm,5,2) = 04 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "04월합계", round(sum(CASE WHEN substr(yymm,5,2) = 05 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "05월합계", round(sum(CASE WHEN substr(yymm,5,2) = 06 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "06월합계", round(sum(CASE WHEN substr(yymm,5,2) = 07 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "07월합계", round(sum(CASE WHEN substr(yymm,5,2) = 08 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "08월합계", round(sum(CASE WHEN substr(yymm,5,2) = 09 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "09월합계", round(sum(CASE WHEN substr(yymm,5,2) = 10 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "10월합계", round(sum(CASE WHEN substr(yymm,5,2) = 11 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "11월합계", round(sum(CASE WHEN substr(yymm,5,2) = 12 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "12월합계", sum(CASE WHEN substr(yymm,5,2) = 01 THEN cnt ELSE 0 END) "01월건수", sum(CASE WHEN substr(yymm,5,2) = 02 THEN cnt ELSE 0 END) "02월건수", sum(CASE WHEN substr(yymm,5,2) = 03 THEN cnt ELSE 0 END) "03월건수", sum(CASE WHEN substr(yymm,5,2) = 04 THEN cnt ELSE 0 END) "04월건수", sum(CASE WHEN substr(yymm,5,2) = 05 THEN cnt ELSE 0 END) "05월건수", sum(CASE WHEN substr(yymm,5,2) = 06 THEN cnt ELSE 0 END) "06월건수", sum(CASE WHEN substr(yymm,5,2) = 07 THEN cnt ELSE 0 END) "07월건수", sum(CASE WHEN substr(yymm,5,2) = 08 THEN cnt ELSE 0 END) "08월건수", sum(CASE WHEN substr(yymm,5,2) = 09 THEN cnt ELSE 0 END) "09월건수", sum(CASE WHEN substr(yymm,5,2) = 10 THEN cnt ELSE 0 END) "10월건수", sum(CASE WHEN substr(yymm,5,2) = 11 THEN cnt ELSE 0 END) "11월건수", sum(CASE WHEN substr(yymm,5,2) = 12 THEN cnt ELSE 0 END) "12월건수" FROM TT GROUP BY CO_CD, TAXIVC_COPRT, SELPCH_CD, TRST_CLNT_CD, CLNT_NM ORDER BY CO_CD, TAXIVC_COPRT, SELPCH_CD, TRST_CLNT_CD, CLNT_NM
) A )
WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
