mapper.select:
	SELECT COUNT(*) FROM TB_AR02M01
 	WHERE SELPCH_CD = #{selpchCd}

		AND CO_CD = #{coCd}				


		AND TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}		


		AND CLNT_NM = #{clntNm}				


		AND PRDT_DIV = #{prdtDiv}				


		AND TAXIVC_COPRT = #{estCoprt}				


		AND DIRTRS_YN = #{dirtrsYn}				


		AND TYP_CD = #{typCd}				


		AND PRJCT_CD = #{prjctCd}				


		AND PRDT_STKN = #{pdsk}				


		AND BILG_CERT_NO IS NOT NULL 		


		AND BILG_CERT_NO IS NULL

SELECT * FROM ( SELECT ROWNUM RNUM, CO_CD, TRST_CERTI_NO, SELPCH_CD, FN_CM05M01_CD_TO_NM(SELPCH_CD) AS SELPCH_NM, TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(TAXIVC_COPRT) AS TAXIVC_COPRT_NM, TO_CHAR(TO_DATE(TRST_DT), 'YYYY-MM-DD') AS TRST_DT, TRST_CLNT_CD, TRST_CLNT_CD AS CLNT_CD, TRST_PRDT_CD, (SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = T.TRST_PRDT_CD) AS PRDT_NM, OWNER_CD, FN_CM05M01_CD_TO_NM(OWNER_CD) AS OWNER_NM, IMP_YN, TYP_CD, FN_CM05M01_CD_TO_NM(TYP_CD) AS TYP_NM, TRSP_TYP_CD, TRST_RPRC_SEQ, TRST_DTL_SEQ, PRDT_LEN, ODR_NO, SALES_MNG, PRJCT_CD, (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = T.PRJCT_CD) AS PRJCT_NM, MAKR_CD, FN_CM05M01_CD_TO_NM(MAKR_CD) AS MAKR_NM, WH_CD, FN_CM05M01_CD_TO_NM(WH_CD) AS WH_NM, MNG_TEL, TO_CHAR(DLVR_DTTM, 'YYYY-MM-DD') AS DLVR_DTTM, DIRTRS_YN, PRDT_DIV, PRDT_STKN, FN_CM05M01_CD_TO_NM(PRDT_STKN) AS PRDT_STKN_NM, PRDT_SIZE, NVL(FN_CM05M01_CD_TO_NM(PRDT_SIZE), PRDT_SIZE) AS PRDT_SIZE_NM, PRDT_UNIT, PRDT_UPR, STOCK_UPR, PCHS_UPR, SELL_UPR, TRST_QTY, TRST_WT, TRST_UPR, TRST_AMT, REAL_TRST_QTY, REAL_TRST_WT, REAL_TRST_UPR, REAL_TRST_AMT, BILG_QTY, BILG_WT, BILG_UPR, BILG_AMT, NVL(TRST_DC_AMT, '0') AS TRST_DC_AMT, NVL(ETC_AMT, '0') AS ETC_AMT, TRSP_RMK, NVL(TRANS_AMT, '0') AS TRANS_AMT, SHIP_VEH_NO, CLNT_NM, BILG_CERT_NO, CASE WHEN BILG_CERT_NO IS NOT NULL THEN 'Y' ELSE 'N' END BILG_YN, NVL(LOSS_RATE, '0') AS LOSS_RATE, ODR_CREAT_DIV, ETC_FIELD1, ETC_FIELD2, ETC_FIELD3, CREAT_ID, CREAT_PGM, TO_CHAR(CREAT_DTTM, 'yyyy-MM-dd') AS CREAT_DTTM, UDT_ID, UDT_PGM, TO_CHAR(UDT_DTTM, 'yyyy-MM-dd') AS UDT_DTTM, SALES_AREA_CD FROM ( SELECT * FROM TB_AR02M01 T WHERE SELPCH_CD = #{selpchCd}
AND CO_CD = #{coCd}
AND TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND CLNT_NM = #{clntNm}
AND PRDT_DIV = #{prdtDiv}
AND TAXIVC_COPRT = #{estCoprt}
AND DIRTRS_YN = #{dirtrsYn}
AND TYP_CD = #{typCd}
AND PRJCT_CD = #{prjctCd}
AND PRDT_STKN = #{pdsk}
AND BILG_CERT_NO IS NOT NULL
AND BILG_CERT_NO IS NULL
ORDER BY UDT_DTTM DESC ) T ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT T1.*, NVL(SELL_PAY_DIV_CD, 'PAYDIV01') AS RFF_CNO, B.CRN AS TAXIVC_COPRT_NO, B.REPST_NM AS TAXIVC_COPRT_CEO, B.CLNT_NM AS TAXIVC_COPRT_NM, NVL(B.BIZCON_NM, '-') AS TAX_COPRT_BIZCON, NVL(B.BSTY_NM, '-') AS TAXVIC_COPRT_BSTY, '1' AS COMII_EM, B.BIZ_TEL_NO AS COMII_TE, B.BIZ_FAX_NO AS COMII_FX, '01' AS CTAIV_BJ_1 FROM ( SELECT CO_CD, TAXIVC_COPRT AS TAXIVC_COPRT_CD, (SELECT CODE_ETC FROM TB_CM05M01 WHERE CODE_ID = TAXIVC_COPRT) AS TAXIVC_COPRT, TRST_CLNT_CD AS CLNT_CD, CLNT_NM, SELPCH_CD, SUM(TRST_QTY) AS TRST_QTY, NVL(SUM(TRST_WT), 0) AS TRST_WT, SUM(TRST_UPR) AS TRST_UPR, SUM(TRST_AMT) AS TRST_AMT, SUM(REAL_TRST_QTY) AS REAL_TRST_QTY, SUM(REAL_TRST_WT) AS REAL_TRST_WT, SUM(REAL_TRST_UPR) AS REAL_TRST_UPR, SUM(REAL_TRST_AMT) AS REAL_TRST_AMT, SUM(BILG_QTY) AS BILG_QTY, SUM(BILG_WT) AS BILG_WT, SUM(BILG_UPR) AS BILG_UPR, SUM(BILG_AMT) AS BILG_AMT, SUM(TRST_DC_AMT) AS TRST_DC_AMT, SUM(ETC_AMT) AS ETC_AMT, SUM(TRANS_AMT) AS TRANS_AMT, SUM(BILG_AMT) AS BILG_AMT, COUNT(*) AS TAX_DMS5_7240, SUM(BILG_AMT) AS TAX_MOA5_23, SUM(BILG_AMT)*0.1 AS TAX_MOA5_124, SUM(BILG_AMT) + SUM(BILG_AMT)*0.1 AS TAX_MOA5_388, CASE WHEN SELPCH_CD = 'SELPCH2' THEN 'PAYDIV01' ELSE 'PAYDIV02' END RFF_VA, 'RFFGN101' AS RFF_GN1, 'RFFGN201' AS RFF_GN2, 'RFFAEA00' AS RFF_AEA, 'BGM122599' AS BGM_1225, '1' AS BGM_4343, 'S' AS XML_FLOW, #{userId} AS TAX_CREAT_ID, #{userNm} AS TAX_CREAT_NM FROM TB_AR02M01 T WHERE TRST_CERTI_NO IN
#{item}
GROUP BY CO_CD, TAXIVC_COPRT, TRST_CLNT_CD, SELPCH_CD, CLNT_NM ) T1 INNER JOIN TB_BM02M01 B ON T1.TAXIVC_COPRT = B.CLNT_CD

mapper.update:
UPDATE TB_AR02M01
SET BILG_CERT_NO = #{bilgCertNo}
WHERE TRST_CERTI_NO = #{trstCertiNo}

mapper.select:
SELECT FN_CM05M01_CD_TO_NM(B.PRDT_DIV) AS PRDT_DIV_NM, FN_CM05M01_CD_TO_NM(B.PRDT_STKN) AS PRDT_STKN_NM, CASE WHEN IMP_YN = 'Y' THEN '수입' ELSE '국산' END IMP_YN, FN_CM05M01_CD_TO_NM(MAKR_CD) AS MAKR_NM, B.PRDT_SPEC, A.PRDT_LEN, SUM(BILG_QTY) BILG_QTY, SUM(BILG_WT) BILG_WT, AVG(BILG_UPR) BILG_UPR, SUM(BILG_AMT) BILG_AMT, SUM(BILG_AMT)*0.1 TAX_AMT FROM TB_AR02M01 A INNER JOIN TB_BM01M01 B ON A.TRST_PRDT_CD = B.PRDT_CD WHERE SELPCH_CD = #{selpchCd}
AND TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
GROUP BY B.PRDT_DIV, B.PRDT_STKN, IMP_YN, MAKR_CD, B.PRDT_SPEC, A.PRDT_LEN ORDER BY B.PRDT_DIV, B.PRDT_STKN, IMP_YN, MAKR_CD, B.PRDT_SPEC, A.PRDT_LEN
