mapper.select:
	SELECT COUNT(*) FROM TB_AR02M01
 	WHERE 1=1

		AND CO_CD = #{coCd}				


		AND TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}		


		AND CLNT_NM = #{clntNm}				


		AND PRDT_DIV = #{prdtDiv}				


		AND TAXIVC_COPRT = #{estCoprt}				


		AND DIRTRS_YN = #{dirtrsYn}				


		AND TYP_CD = #{typCd}				


		AND PRJCT_CD = #{prjctCd}				


		AND PRDT_STKN = #{pdsk}				


		AND BILG_CERT_NO IS NOT NULL 		


		AND BILG_CERT_NO IS NULL

SELECT * FROM ( SELECT ROWNUM RNUM, CO_CD, TRST_CERTI_NO, SELPCH_CD, TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(TAXIVC_COPRT) AS TAXIVC_COPRT_NM, TO_CHAR(TO_DATE(TRST_DT), 'YYYY-MM-DD') AS TRST_DT, TRST_CLNT_CD, TRST_PRDT_CD, (SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = T.TRST_PRDT_CD) AS PRDT_NM, OWNER_CD, IMP_YN, TYP_CD, FN_CM05M01_CD_TO_NM(TYP_CD) AS TYP_NM, TRSP_TYP_CD, TRST_RPRC_SEQ, TRST_DTL_SEQ, PRDT_LEN, ODR_NO, SALES_MNG, PRJCT_CD, (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = T.PRJCT_CD) AS PRJCT_NM, MAKR_CD, WH_CD, MNG_TEL, TO_CHAR(DLVR_DTTM, 'YYYY-MM-DD') AS DLVR_DTTM, DIRTRS_YN, PRDT_DIV, PRDT_STKN, FN_CM05M01_CD_TO_NM(PRDT_STKN) AS PRDT_STKN_NM, PRDT_SIZE, NVL(FN_CM05M01_CD_TO_NM(PRDT_SIZE), PRDT_SIZE) AS PRDT_SIZE_NM, PRDT_UNIT, PRDT_UPR, STOCK_UPR, PCHS_UPR, SELL_UPR, TRST_QTY, TRST_WT, TRST_UPR, TRST_AMT, REAL_TRST_QTY, REAL_TRST_WT, REAL_TRST_UPR, REAL_TRST_AMT, BILG_QTY, BILG_WT, BILG_UPR, BILG_AMT, TRST_DC_AMT, ETC_AMT, TRSP_RMK, TRANS_AMT, SHIP_VEH_NO, CLNT_NM, BILG_CERT_NO, CASE WHEN BILG_CERT_NO IS NOT NULL THEN 'Y' ELSE 'N' END BILG_YN, LOSS_RATE, ODR_CREAT_DIV, ETC_FIELD1, ETC_FIELD2, ETC_FIELD3, CREAT_ID, CREAT_PGM, TO_CHAR(CREAT_DTTM, 'yyyy-MM-dd') AS CREAT_DTTM, UDT_ID, UDT_PGM, TO_CHAR(UDT_DTTM, 'yyyy-MM-dd') AS UDT_DTTM, SALES_AREA_CD FROM ( SELECT * FROM TB_AR02M01 T WHERE 1=1
AND CO_CD = #{coCd}
AND TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND CLNT_NM = #{clntNm}
AND PRDT_DIV = #{prdtDiv}
AND TAXIVC_COPRT = #{estCoprt}
AND DIRTRS_YN = #{dirtrsYn}
AND TYP_CD = #{typCd}
AND PRJCT_CD = #{prjctCd}
AND PRDT_STKN = #{pdsk}
AND BILG_CERT_NO IS NOT NULL
AND BILG_CERT_NO IS NULL
ORDER BY UDT_DTTM DESC ) T ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}
