mapper.select:
SELECT COUNT(*) FROM ( SELECT T.CO_CD, T.TRST_CLNT_CD, T.CLNT_NM, T.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT) AS TAXIVC_COPRT_NM,
T.SITE_CD, SD09M01.SITE_NM,
SUM(T.BILG_QTY) BILG_QTY, SUM(T.BILG_WT) BILG_WT, SUM(T.BILG_UPR) BILG_UPR, SUM(T.BILG_AMT) BILG_AMT, NVL(SUM(T.ETC_AMT), '0') AS ETC_AMT, NVL(SUM(T.TRANS_AMT), '0') AS TRANS_AMT, NVL(SUM(T.TRST_DC_AMT), '0') AS TRST_DC_AMT, SUM(T.BILG_VAT_AMT) BILG_VAT_AMT, SUM(T.BILG_VAT_AMT) + SUM(T.BILG_AMT) AS TOT_AMT FROM TB_AR02M01 T LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID WHERE SELPCH_CD = #{selpchCd}
AND T.CO_CD = #{coCd}
AND T.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND T.TRST_CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%${clntNm}%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.TAXIVC_COPRT = #{estCoprt}
AND T.DIRTRS_YN = #{dirtrsYn}
AND T.TYP_CD = #{typCd}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_STKN = #{pdsk}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND T.SALES_MNG = #{salesMng}
AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'
AND T.WH_CD = #{whCd}
AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'
GROUP BY T.CO_CD , T.TRST_CLNT_CD , T.CLNT_NM , T.TAXIVC_COPRT
, T.SITE_CD , SD09M01.SITE_NM
)

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM FROM ( SELECT T.CO_CD, T.TRST_CLNT_CD, T.CLNT_NM, T.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT) AS TAXIVC_COPRT_NM,
T.SITE_CD, SD09M01.SITE_NM,
SUM(T.BILG_QTY) BILG_QTY, SUM(T.BILG_WT) BILG_WT, SUM(T.BILG_UPR) BILG_UPR, SUM(T.BILG_AMT) BILG_AMT, NVL(SUM(T.ETC_AMT), '0') AS ETC_AMT, NVL(SUM(T.TRANS_AMT), '0') AS TRANS_AMT, NVL(SUM(T.TRST_DC_AMT), '0') AS TRST_DC_AMT, SUM(T.BILG_VAT_AMT) BILG_VAT_AMT, SUM(T.BILG_VAT_AMT) + SUM(T.BILG_AMT) AS TOT_AMT FROM TB_AR02M01 T LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID WHERE SELPCH_CD = #{selpchCd}
AND T.CO_CD = #{coCd}
AND T.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND T.TRST_CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%${clntNm}%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.TAXIVC_COPRT = #{estCoprt}
AND T.DIRTRS_YN = #{dirtrsYn}
AND T.TYP_CD = #{typCd}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_STKN = #{pdsk}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND T.SALES_MNG = #{salesMng}
AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'
AND T.WH_CD = #{whCd}
AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'
GROUP BY T.CO_CD , T.TRST_CLNT_CD , T.CLNT_NM , T.TAXIVC_COPRT
, T.SITE_CD , SD09M01.SITE_NM
ORDER BY T.CLNT_NM , TAXIVC_COPRT_NM
,SD09M01.SITE_NM
) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT T.CO_CD, T.TRST_CERTI_NO, T.SELPCH_CD, FN_CM05M01_CD_TO_NM(T.SELPCH_CD) AS SELPCH_NM, T.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT) AS TAXIVC_COPRT_NM, TO_CHAR(TO_DATE(T.TRST_DT), 'YYYY-MM-DD') AS TRST_DT, T.TRST_CLNT_CD, T.TRST_CLNT_CD AS CLNT_CD, T.TRST_PRDT_CD, TB_BM01M01.PRDT_NM, T.OWNER_CD, FN_CM05M01_CD_TO_NM(T.OWNER_CD) AS OWNER_NM, T.IMP_YN, T.TYP_CD, FN_CM05M01_CD_TO_NM(T.TYP_CD) AS TYP_NM, T.TRSP_TYP_CD, FN_CM05M01_CD_TO_NM(T.TRSP_TYP_CD) AS TRSP_TYP_NM, T.TRST_RPRC_SEQ, T.TRST_DTL_SEQ, T.PRDT_LEN, T.ODR_NO, T.SALES_MNG, T.PRJCT_CD, SD05M01.PRJCT_NM, T.MAKR_CD, FN_CM05M01_CD_TO_NM(T.MAKR_CD) AS MAKR_NM, T.WH_CD, FN_CM05M01_CD_TO_NM(T.WH_CD) AS WH_NM, T.MNG_TEL, TO_CHAR(T.DLVR_DTTM, 'YYYY-MM-DD') AS DLVR_DTTM, T.DIRTRS_YN, T.PRDT_DIV, T.PRDT_STKN, FN_CM05M01_CD_TO_NM(T.PRDT_STKN) AS PRDT_STKN_NM, T.PRDT_SIZE, NVL(FN_CM05M01_CD_TO_NM(T.PRDT_SIZE), T.PRDT_SIZE) AS PRDT_SIZE_NM, T.PRDT_UNIT, T.PRDT_UPR, T.STOCK_UPR, T.PCHS_UPR, T.SELL_UPR, T.TRST_QTY, T.TRST_WT, T.TRST_UPR, T.TRST_AMT, T.REAL_TRST_QTY, T.REAL_TRST_WT, T.REAL_TRST_UPR, T.REAL_TRST_AMT, T.BILG_QTY, T.BILG_WT, T.BILG_UPR, T.BILG_AMT, NVL(T.TRST_DC_AMT, '0') AS TRST_DC_AMT, NVL(T.ETC_AMT, '0') AS ETC_AMT, T.TRSP_RMK, NVL(T.TRANS_AMT, '0') AS TRANS_AMT, T.SHIP_VEH_NO, T.CLNT_NM, T.BILG_CERT_NO, CASE WHEN T.BILG_CERT_NO IS NOT NULL THEN 'Y' ELSE 'N' END BILG_YN, CASE WHEN T.BILG_AMT > 0 THEN 'Y' ELSE 'N' END RETURN_YN, NVL(T.LOSS_RATE, '0') AS LOSS_RATE, T.ODR_CREAT_DIV, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, T.CREAT_PGM, TO_CHAR(T.CREAT_DTTM, 'yyyy-MM-dd') AS CREAT_DTTM, T.UDT_ID, T.UDT_PGM, TO_CHAR(T.UDT_DTTM, 'yyyy-MM-dd') AS UDT_DTTM, T.SALES_AREA_CD, T.SITE_CD, SD09M01.SITE_NM, T.BILG_VAT_AMT, T.BILG_VAT_AMT + T.BILG_AMT AS TOT_AMT, T.PRDT_SPEC, T.RFF_AEA, T.ORGN_TAX_BILG_NO, T.RFF_APG FROM TB_AR02M01 T LEFT JOIN TB_BM01M01 TB_BM01M01 ON T.TRST_PRDT_CD = TB_BM01M01.PRDT_CD LEFT JOIN TB_SD05M01 SD05M01 ON T.PRJCT_CD = SD05M01.PRJCT_CD LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD WHERE SELPCH_CD = #{selpchCd}
AND T.CO_CD = #{coCd}
AND T.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND T.CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%${clntNm}%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.TAXIVC_COPRT = #{estCoprt}
AND T.DIRTRS_YN = #{dirtrsYn}
AND T.TYP_CD = #{typCd}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_STKN = #{pdsk}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
ORDER BY CLNT_NM, TRST_DT, TYP_CD, TRST_PRDT_CD, PRDT_STKN, PRDT_SIZE, PRDT_LEN

mapper.delete:
DELETE FROM TB_AR02M01
WHERE TRST_RPRC_SEQ = #{trstRprcSeq}
  AND TRST_DTL_SEQ = #{trstDtlSeq}
  AND CREAT_PGM = #{pgmId}

  AND SELPCH_CD     = #{selpchCd}

mapper.select:
SELECT NVL(BILG_CERT_NO, 0) AS BILG_CERT_NO 
FROM TB_AR02M01
WHERE TRST_RPRC_SEQ = #{trstRprcSeq}
  AND TRST_DTL_SEQ = #{trstDtlSeq}
  AND CREAT_PGM = #{pgmId}

  AND SELPCH_CD     = #{selpchCd}

SELECT T1.*, NVL(SELL_PAY_DIV_CD, 'PAYDIV01') AS RFF_CNO, NVL(SELL_VAT_CD, 'VAT01') AS RFF_VA, B.CRN AS TAXIVC_COPRT_NO, B.REPST_NM AS TAXIVC_COPRT_CEO, B.CLNT_NM AS TAXIVC_COPRT_NM, NVL(B.BIZCON_NM, '-') AS TAX_COPRT_BIZCON, NVL(B.BSTY_NM, '-') AS TAXVIC_COPRT_BSTY, (SELECT CRN FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_CRN, (SELECT REPST_NM FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_CEO, (SELECT BIZ_ADDR || ' ' ||BIZ_ADDR_DTL FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_ADDR, (SELECT BIZCON_NM FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_BIZCON, (SELECT BSTY_NM FROM TB_BM02M01 WHERE CLNT_CD = T1.CLNT_CD) AS TAXIVC_RCV_BSTY, '1' AS COMII_EM, B.BIZ_TEL_NO AS COMII_TE, B.BIZ_FAX_NO AS COMII_FX, '01' AS CTAIV_BJ_1 FROM ( SELECT CO_CD, TAXIVC_COPRT AS TAXIVC_COPRT_CD, (SELECT CODE_ETC FROM TB_CM05M01 WHERE CODE_ID = TAXIVC_COPRT) AS TAXIVC_COPRT, TRST_CLNT_CD AS CLNT_CD, CLNT_NM, SELPCH_CD, SUM(TRST_QTY) AS TRST_QTY, NVL(SUM(TRST_WT), 0) AS TRST_WT, SUM(TRST_UPR) AS TRST_UPR, SUM(TRST_AMT) AS TRST_AMT, SUM(REAL_TRST_QTY) AS REAL_TRST_QTY, SUM(REAL_TRST_WT) AS REAL_TRST_WT, SUM(REAL_TRST_UPR) AS REAL_TRST_UPR, SUM(REAL_TRST_AMT) AS REAL_TRST_AMT, SUM(BILG_QTY) AS BILG_QTY, SUM(BILG_WT) AS BILG_WT, SUM(BILG_UPR) AS BILG_UPR, SUM(BILG_AMT) AS BILG_AMT, SUM(TRST_DC_AMT) AS TRST_DC_AMT, SUM(ETC_AMT) AS ETC_AMT, SUM(TRANS_AMT) AS TRANS_AMT, COUNT(*) AS TAX_DMS5_7240, SUM(BILG_AMT) AS TAX_MOA5_23, SUM(BILG_VAT_AMT) AS TAX_MOA5_124, SUM(BILG_AMT) + SUM(BILG_VAT_AMT) AS TAX_MOA5_388, SUM(BILG_AMT) + SUM(BILG_VAT_AMT) AS TAX_MOA5_9, NVL2(MAX(ORGN_TAX_BILG_NO), 'RFFGN102', 'RFFGN101') AS RFF_GN1, 'RFFGN201' AS RFF_GN2, MAX(ORGN_TAX_BILG_NO) AS ORGN_TAX_BILG_NO, MAX(RFF_AEA) AS RFF_AEA, 'BGM12259' AS BGM_1225, '1' AS BGM_4343, 'S' AS XML_FLOW, #{userId} AS TAX_CREAT_ID, #{userNm} AS TAX_CREAT_NM FROM TB_AR02M01 T WHERE TRST_CERTI_NO IN
#{item}
GROUP BY CO_CD, TAXIVC_COPRT, TRST_CLNT_CD, SELPCH_CD, CLNT_NM ) T1 INNER JOIN TB_BM02M01 B ON T1.TAXIVC_COPRT = B.CLNT_CD

mapper.update:
UPDATE TB_AR02M01
SET BILG_CERT_NO = #{bilgCertNo}
WHERE TRST_CERTI_NO = #{trstCertiNo}

UPDATE TB_AR02M01
SET BILG_CERT_NO = ''
WHERE BILG_CERT_NO = #{bilgCertNo}

mapper.select:
SELECT FN_CM05M01_CD_TO_NM(B.PRDT_DIV) AS PRDT_DIV_NM, FN_CM05M01_CD_TO_NM(B.PRDT_STKN) AS PRDT_STKN_NM, A.TRST_PRDT_CD, B.PRDT_NM, CASE WHEN IMP_YN = 'Y' THEN '수입' ELSE '국산' END IMP_YN, FN_CM05M01_CD_TO_NM(MAKR_CD) AS MAKR_NM, B.PRDT_SPEC, A.PRDT_LEN, SUM(BILG_QTY) BILG_QTY, SUM(BILG_WT) BILG_WT, AVG(BILG_UPR) BILG_UPR, SUM(BILG_AMT) BILG_AMT, SUM(BILG_VAT_AMT) TAX_AMT FROM TB_AR02M01 A INNER JOIN TB_BM01M01 B ON A.TRST_PRDT_CD = B.PRDT_CD WHERE SELPCH_CD = #{selpchCd}
AND CO_CD = #{coCd}
AND TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND B.PRDT_DIV = #{prdtDiv}
AND TRST_CLNT_CD = #{clntCd}
AND PRJCT_CD = #{prjctCd}
AND BILG_CERT_NO IS NOT NULL
AND BILG_CERT_NO IS NULL
AND TYP_CD = #{typCd}
GROUP BY B.PRDT_DIV, B.PRDT_NM, A.TRST_PRDT_CD, B.PRDT_STKN, IMP_YN, MAKR_CD, B.PRDT_SPEC, A.PRDT_LEN ORDER BY B.PRDT_DIV, B.PRDT_NM, A.TRST_PRDT_CD, B.PRDT_STKN, IMP_YN, MAKR_CD, B.PRDT_SPEC, A.PRDT_LEN
