mapper.select:
SELECT COUNT(*) FROM ( SELECT T.CO_CD, T.TRST_CLNT_CD, T.CLNT_NM, T.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT) AS TAXIVC_COPRT_NM,
T.SITE_CD, SD09M01.SITE_NM,
(SELECT CLNT_NM FROM TB_BM02M01 BM02 INNER JOIN TB_OD01M01 OD01M01 ON BM02.CLNT_CD = OD01M01.CLNT_CD WHERE T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ AND T.CREAT_PGM LIKE 'OD'||'%') AS PCHS_CLNT_NM,
SUM(T.BILG_QTY) BILG_QTY, SUM(T.BILG_WT) BILG_WT, SUM(T.BILG_UPR) BILG_UPR, SUM(T.BILG_AMT) BILG_AMT, NVL(SUM(T.ETC_AMT), '0') AS ETC_AMT, NVL(SUM(T.TRANS_AMT), '0') AS TRANS_AMT, NVL(SUM(T.TRST_DC_AMT), '0') AS TRST_DC_AMT, SUM(T.BILG_VAT_AMT) BILG_VAT_AMT, SUM(T.BILG_VAT_AMT) + SUM(T.BILG_AMT) AS TOT_AMT FROM TB_AR02M01 T LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID LEFT JOIN TB_BM01M01 BM01 ON T.TRST_PRDT_CD = BM01.PRDT_CD WHERE SELPCH_CD = #{selpchCd}
AND T.CO_CD = #{coCd}
AND T.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND T.TRST_CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%${clntNm}%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.TAXIVC_COPRT = #{estCoprt}
AND T.TAXIVC_COPRT = #{taxivcCoprt}
AND T.DIRTRS_YN = #{dirtrsYn}
AND T.TYP_CD = #{typCd}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_STKN = #{pdsk}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND T.SALES_MNG = #{salesMng}
AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'
AND T.WH_CD = #{whCd}
AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'
AND BM01.PRDT_GRP = #{prdtGrp}
AND T.TRSP_TYP_CD = #{trspTypCd}
AND T.MAKR_CD = #{makrCd}
GROUP BY T.CO_CD , T.TRST_CLNT_CD , T.CLNT_NM , T.TAXIVC_COPRT
, T.SITE_CD , SD09M01.SITE_NM
,T.TRST_RPRC_SEQ ,T.CREAT_PGM
)
WHERE PCHS_CLNT_NM LIKE '%'|| #{pchsClntNm} || '%'

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM FROM ( SELECT T.CO_CD, T.TRST_CLNT_CD, T.CLNT_NM, T.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT) AS TAXIVC_COPRT_NM,
T.SITE_CD, SD09M01.SITE_NM, T.PRJCT_CD,
(SELECT CLNT_NM FROM TB_BM02M01 BM02 INNER JOIN TB_OD01M01 OD01M01 ON BM02.CLNT_CD = OD01M01.CLNT_CD WHERE T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ AND T.CREAT_PGM LIKE 'OD'||'%') AS PCHS_CLNT_NM,
(SELECT CLNT_NM FROM TB_BM02M01 BM02 INNER JOIN TB_OD01M01 OD01M01 ON BM02.CLNT_CD = OD01M01.SELL_CLNT_CD WHERE T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ AND T.CREAT_PGM LIKE 'OD'||'%') AS SELL_CLNT_NM,
SUM(T.BILG_QTY) BILG_QTY, SUM(T.BILG_WT) BILG_WT, SUM(T.BILG_UPR) BILG_UPR, SUM(T.BILG_AMT) BILG_AMT, NVL(SUM(T.ETC_AMT), '0') AS ETC_AMT, NVL(SUM(T.TRANS_AMT), '0') AS TRANS_AMT, NVL(SUM(T.TRST_DC_AMT), '0') AS TRST_DC_AMT, SUM(T.BILG_VAT_AMT) BILG_VAT_AMT, SUM(T.BILG_VAT_AMT) + SUM(T.BILG_AMT) AS TOT_AMT FROM TB_AR02M01 T LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID LEFT JOIN TB_BM01M01 BM01 ON T.TRST_PRDT_CD = BM01.PRDT_CD WHERE SELPCH_CD = #{selpchCd}
AND T.CO_CD = #{coCd}
AND T.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND T.TRST_CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%${clntNm}%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.PRDT_SPEC LIKE '%${prdtSpec}%'
AND T.TAXIVC_COPRT = #{taxivcCoprt}
AND T.DIRTRS_YN = #{dirtrsYn}
AND T.TYP_CD = #{typCd}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_STKN = #{pdsk}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND T.SALES_MNG = #{salesMng}
AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'
AND T.WH_CD = #{whCd}
AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'
AND BM01.PRDT_GRP = #{prdtGrp}
AND T.TRSP_TYP_CD = #{trspTypCd}
AND T.MAKR_CD = #{makrCd}
GROUP BY T.CO_CD , T.TRST_CLNT_CD , T.CLNT_NM , T.TAXIVC_COPRT
, T.SITE_CD , SD09M01.SITE_NM , T.PRJCT_CD
,T.TRST_RPRC_SEQ ,T.CREAT_PGM
ORDER BY T.CLNT_NM , TAXIVC_COPRT_NM
,SD09M01.SITE_NM
) A
WHERE PCHS_CLNT_NM LIKE '%'|| #{pchsClntNm} || '%'
) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT
	COUNT(*)
FROM
(
 	SELECT 
		T.CO_CD

		   , (SELECT CLNT_NM FROM TB_BM02M01  BM02
		                     INNER JOIN  TB_OD01M01 OD01M01  ON BM02.CLNT_CD = OD01M01.CLNT_CD 
		                     WHERE  T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ  AND T.CREAT_PGM LIKE 'OD'||'%' ) AS PCHS_CLNT_NM


		 ,   '' AS PCHS_CLNT_NM


		   , (SELECT CLNT_NM FROM TB_BM02M01  BM02
		                     INNER JOIN  TB_OD01M01 OD01M01  ON BM02.CLNT_CD = OD01M01.SELL_CLNT_CD 
		                     WHERE  T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ  AND T.CREAT_PGM LIKE 'OD'||'%' ) AS SELL_CLNT_NM


		 ,   '' AS SELL_CLNT_NM

	FROM 
		TB_AR02M01 T
		LEFT JOIN TB_BM01M01 BM01M01 ON T.TRST_PRDT_CD = BM01M01.PRDT_CD
		LEFT JOIN TB_BM02M01 BM02M01 ON T.TRST_CLNT_CD = BM02M01.CLNT_CD
		LEFT JOIN TB_SD05M01 SD05M01 ON T.PRJCT_CD = SD05M01.PRJCT_CD
		LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD
		LEFT JOIN TB_CM05M01 CM05M01 ON NVL(SD05M01.CLMN_DIV_CD, BM02M01.SELL_CLMN_DIV_CD) = CM05M01.CODE_ID
		LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID

			LEFT JOIN TB_OD01M01 OD01M01 ON T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ	AND T.CREAT_PGM LIKE 'OD'||'%' 

 	WHERE 
 		SELPCH_CD = #{selpchCd}

			AND T.CO_CD = #{coCd}				


			AND T.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}		


			AND T.REAL_DLVR_DTTM BETWEEN #{realDlvrDtFrom} AND #{realDlvrDtTo}		


			AND T.TRST_CLNT_CD = #{clntCd}


			AND T.CLNT_NM LIKE '%${clntNm}%'


			AND T.PRDT_DIV = #{prdtDiv}				


			AND T.PRDT_SPEC LIKE '%${prdtSpec}%'


			AND T.TAXIVC_COPRT = #{taxivcCoprt}				


			AND T.DIRTRS_YN = #{dirtrsYn}				


			AND T.TYP_CD = #{typCd}				


			AND T.PRJCT_CD = #{prjctCd}				


			AND T.SITE_CD = #{siteCd}				


			AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'				


			AND T.PRDT_STKN = #{pdsk}				




					AND T.BILG_CERT_NO IS NOT NULL 		


					AND T.BILG_CERT_NO IS NULL 




			AND T.SALES_MNG = #{salesMng}				


			AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'				


			AND T.WH_CD = #{whCd}


			AND T.IMP_YN = #{impYn}


			AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'


			AND BM01M01.PRDT_GRP = #{prdtGrp}


			AND T.TRSP_TYP_CD = #{trspTypCd}


		    AND T.MAKR_CD = #{makrCd}


			AND BM01M01.PRDT_NM LIKE '%' || #{prdtNm} || '%'

		ORDER BY TRST_DT DESC, T.CLNT_NM, T.TYP_CD, TRST_PRDT_CD, T.PRDT_STKN, T.PRDT_SIZE, T.PRDT_LEN
) A


		WHERE PCHS_CLNT_NM LIKE '%'|| #{pchsClntNm} || '%'




		WHERE SELL_CLNT_NM LIKE '%'|| #{sellClntNm} || '%'

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM FROM ( SELECT T.CO_CD, T.TRST_CERTI_NO, T.SELPCH_CD, FN_CM05M01_CD_TO_NM(T.SELPCH_CD) AS SELPCH_NM, T.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT) AS TAXIVC_COPRT_NM, TO_CHAR(TO_DATE(T.TRST_DT), 'YYYY-MM-DD') AS TRST_DT, TO_CHAR(T.REAL_DLVR_DTTM, 'YYYY-MM-DD') AS REAL_DLVR_DTTM, BM02M01.SELL_VAT_CD, NVL((SELECT CODE_RPRC FROM TB_CM05M01 WHERE CODE_ID = BM02M01.SELL_VAT_CD), 10) AS SELL_VAT, NVL(SD05M01.CLMN_DIV_CD, BM02M01.SELL_CLMN_DIV_CD) AS PCHS_CLMN_DIV_CD, CASE WHEN SD05M01.CLMN_DIV_CD IS NULL THEN FN_CM05M01_CD_TO_NM(BM02M01.SELL_CLMN_DIV_CD) || ' ' || NVL2(BM02M01.SELL_CLMN_DAY,FN_CM05M01_CD_TO_NM(BM02M01.SELL_CLMN_DAY)||'일','') || FN_CM05M01_CD_TO_NM(BM02M01.SELL_PMNT_MTD_CD) ELSE FN_CM05M01_CD_TO_NM(SD05M01.CLMN_DIV_CD) END AS PCHS_CLMN_DIV_NM , CASE WHEN CM05M01.CODE_RPRC = 'MON' THEN TO_CHAR(LAST_DAY(ADD_MONTHS(TRUNC(TO_DATE(T.TRST_DT),'MM'), NVL(CM05M01.CODE_ETC,0)) -1) + NVL(NVL(SD05M01.CLMN_DAY,BM02M01.SELL_CLMN_DAY),0), 'YYYY-MM-DD') WHEN CM05M01.CODE_RPRC = 'DAY' THEN TO_CHAR(TO_DATE(T.TRST_DT) -1 + NVL(CM05M01.CODE_ETC,0) + NVL(NVL(SD05M01.CLMN_DAY,BM02M01.SELL_CLMN_DAY),0), 'YYYY-MM-DD') END AS PCHS_CLMN_DAY, T.TRST_CLNT_CD, T.TRST_CLNT_CD AS CLNT_CD, T.TRST_PRDT_CD, T.TRST_PRDT_CD AS PRDT_CD, BM01M01.PRDT_NM, BM01M01.PRDT_GRP, FN_CM05M01_CD_TO_NM(BM01M01.PRDT_GRP) PRDT_GRP_NM, T.OWNER_CD, FN_CM05M01_CD_TO_NM(T.OWNER_CD) AS OWNER_NM, T.IMP_YN, T.TYP_CD, FN_CM05M01_CD_TO_NM(T.TYP_CD) AS TYP_NM, T.TRSP_TYP_CD, FN_CM05M01_CD_TO_NM(T.TRSP_TYP_CD) AS TRSP_TYP_NM, T.TRST_RPRC_SEQ, DECODE(T.CREAT_PGM, 'OD0102V01', TRST_RPRC_SEQ, NULL) AS ORDRG_SEQ, DECODE(T.CREAT_PGM, 'AR0102V01', TRST_RPRC_SEQ, NULL) AS SHIP_SEQ, T.TRST_DTL_SEQ, T.PRDT_LEN, T.ODR_NO, T.SALES_MNG, CM06M01.NAME AS SALES_MNG_NM, CM06M01.DEPT_ID AS DEPT_ID, (SELECT DEPT_NM FROM TB_CM04M01 WHERE DEPT_ID = CM06M01.DEPT_ID) AS DEPT_NM, T.PRJCT_CD, SD05M01.PRJCT_NM, T.MAKR_CD, FN_CM05M01_CD_TO_NM(T.MAKR_CD) AS MAKR_NM, T.WH_CD, FN_CM05M01_CD_TO_NM(T.WH_CD) AS WH_NM, T.MNG_TEL, TO_CHAR(T.DLVR_DTTM, 'YYYY-MM-DD') AS DLVR_DTTM, TO_CHAR(T.REAL_DLVR_DTTM, 'YYYY-MM-DD') AS REAL_DLVR_DTTM, T.DIRTRS_YN, T.PRDT_DIV, T.PRDT_STKN, DECODE(BM01M01.PRDT_COIL_YN,'Y',FN_CM05M01_CD_TO_NM(T.PRDT_STKN)||'C', FN_CM05M01_CD_TO_NM(T.PRDT_STKN)) AS PRDT_STKN_NM, T.PRDT_SIZE, NVL(FN_CM05M01_CD_TO_NM(T.PRDT_SIZE), T.PRDT_SIZE) AS PRDT_SIZE_NM, T.PRDT_UNIT, T.PRDT_UPR, T.STOCK_UPR, T.PCHS_UPR, T.SELL_UPR, T.TRST_QTY, T.TRST_WT, T.TRST_UPR, T.TRST_AMT, T.REAL_TRST_QTY, T.REAL_TRST_WT, T.REAL_TRST_UPR, T.REAL_TRST_AMT, T.BILG_QTY, T.BILG_WT, T.BILG_UPR, T.BILG_AMT, NVL(T.TRST_DC_AMT, '0') AS TRST_DC_AMT, NVL(T.ETC_AMT, '0') AS ETC_AMT, T.TRSP_RMK, NVL(T.TRANS_AMT, '0') AS TRANS_AMT, T.SHIP_VEH_NO, T.CLNT_NM, T.BILG_CERT_NO, CASE WHEN T.BILG_CERT_NO IS NOT NULL THEN 'Y' ELSE 'N' END BILG_YN, CASE WHEN T.BILG_AMT < 0 THEN 'Y' ELSE 'N' END RETURN_YN, NVL(T.LOSS_RATE, '0') AS LOSS_RATE, T.ODR_CREAT_DIV, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = T.CREAT_ID) AS CREAT_NM, T.CREAT_PGM, TO_CHAR(T.CREAT_DTTM, 'yyyy-MM-dd') AS CREAT_DTTM, T.UDT_ID, (SELECT NAME FROM TB_CM06M01 WHERE ID = T.UDT_ID) AS UDT_NM, T.UDT_PGM, TO_CHAR(T.UDT_DTTM, 'yyyy-MM-dd') AS UDT_DTTM, T.SALES_AREA_CD, T.SITE_CD, SD09M01.SITE_NM, T.BILG_VAT_AMT, T.BILG_VAT_AMT + T.BILG_AMT AS TOT_AMT, T.PRDT_SPEC, T.RFF_AEA, T.ORGN_TAX_BILG_NO, T.RFF_APG, T.SHIP_CNT, T.SUB_CLNT_CD, T.SUB_CLNT_NM, BM01M01.PRDT_CNVRSN_WT
, DECODE(T.SUB_CLNT_CD, NULL, (SELECT CLNT_NM FROM TB_BM02M01 BM02 INNER JOIN TB_OD01M01 OD01M01 ON BM02.CLNT_CD = OD01M01.CLNT_CD WHERE T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ AND T.CREAT_PGM LIKE 'OD'||'%' ), T.SUB_CLNT_NM) AS PCHS_CLNT_NM
, '' AS PCHS_CLNT_NM
, OD01M01.SELL_CLNT_CD AS SELL_CLNT_CD , DECODE(T.SUB_CLNT_CD, NULL, (SELECT CLNT_NM FROM TB_BM02M01 BM02 WHERE OD01M01.SELL_CLNT_CD = BM02.CLNT_CD), T.SUB_CLNT_NM) AS SELL_CLNT_NM
FROM TB_AR02M01 T LEFT JOIN TB_BM01M01 BM01M01 ON T.TRST_PRDT_CD = BM01M01.PRDT_CD LEFT JOIN TB_BM02M01 BM02M01 ON T.TRST_CLNT_CD = BM02M01.CLNT_CD LEFT JOIN TB_SD05M01 SD05M01 ON T.PRJCT_CD = SD05M01.PRJCT_CD LEFT JOIN TB_SD09M01 SD09M01 ON T.SITE_CD = SD09M01.SITE_CD LEFT JOIN TB_CM05M01 CM05M01 ON NVL(SD05M01.CLMN_DIV_CD, BM02M01.SELL_CLMN_DIV_CD) = CM05M01.CODE_ID LEFT JOIN TB_CM06M01 CM06M01 ON T.SALES_MNG = CM06M01.ID
LEFT JOIN TB_OD01M01 OD01M01 ON T.TRST_RPRC_SEQ = OD01M01.ORDRG_SEQ AND T.CREAT_PGM LIKE 'OD'||'%'
WHERE SELPCH_CD = #{selpchCd}
AND T.CO_CD = #{coCd}
AND T.TRST_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND T.REAL_DLVR_DTTM BETWEEN #{realDlvrDtFrom} AND #{realDlvrDtTo}
AND T.TRST_CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%${clntNm}%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.PRDT_SPEC LIKE '%${prdtSpec}%'
AND T.TAXIVC_COPRT = #{taxivcCoprt}
AND T.DIRTRS_YN = #{dirtrsYn}
AND T.TYP_CD = #{typCd}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09M01.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_STKN = #{pdsk}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND T.SALES_MNG = #{salesMng}
AND CM06M01.NAME LIKE '%'|| #{salesMngNm} || '%'
AND T.WH_CD = #{whCd}
AND T.IMP_YN = #{impYn}
AND T.TRSP_RMK LIKE '%'|| #{trspRmk} || '%'
AND BM01M01.PRDT_GRP = #{prdtGrp}
AND T.TRSP_TYP_CD = #{trspTypCd}
AND T.MAKR_CD = #{makrCd}
AND BM01M01.PRDT_NM LIKE '%' || #{prdtNm} || '%'
ORDER BY TRST_DT DESC, T.CLNT_NM, T.TYP_CD, TRST_RPRC_SEQ, TRST_DTL_SEQ, TRST_PRDT_CD, T.PRDT_STKN, T.PRDT_SIZE, T.PRDT_LEN ) A
WHERE PCHS_CLNT_NM LIKE '%'|| #{pchsClntNm} || '%'
WHERE SELL_CLNT_NM LIKE '%'|| #{sellClntNm} || '%'
)
WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

mapper.update:
BEGIN
INSERT INTO TB_AR05D02 (CO_CD ,ETRDPS_SEQ ,ETRDPS_DTL_SEQ ,BILG_CERT_NO ,TRST_CERTI_NO ,ETRDPS_DT ,TRST_DT ,CLNT_CD ,SELL_CLMN_DIV_CD ,SELL_PMNT_MTD_CD ,SELL_CLMN_DAY ,TAX_BILG_NO ,PRJCT_CD ,ETRDPS_AMT ,PRDT_GRP ,CREAT_ID ,CREAT_PGM ,CREAT_DTTM) SELECT CO_CD ,ETRDPS_SEQ ,1 + (SELECT MAX(ETRDPS_DTL_SEQ) FROM TB_AR05D02 D WHERE ETRDPS_SEQ = #{etrdpsSeqFind}) ,'0' ,'0' ,ETRDPS_DT ,NULL ,CLNT_CD ,NULL ,NULL ,NULL ,'0' ,NULL ,'0' ,PRDT_GRP ,#{userId} ,'MATCH_AR02' ,SYSDATE FROM TB_AR05D02 D02 WHERE ETRDPS_SEQ = #{etrdpsSeqFind} GROUP BY CO_CD, ETRDPS_SEQ, ETRDPS_DT, CLNT_CD, PRDT_GRP;
UPDATE TB_AR05D02 D02 SET ETRDPS_AMT = D02.ETRDPS_AMT + (SELECT SUM(ETRDPS_AMT) FROM TB_AR05D02 WHERE TRST_CERTI_NO = #{trstCertiNo} AND ETRDPS_SEQ = #{etrdpsSeqFind}) WHERE TRST_CERTI_NO = '0' AND ETRDPS_SEQ = #{etrdpsSeqFind};
END;

mapper.delete:
BEGIN
INSERT INTO TB_AR05D02 (CO_CD ,ETRDPS_SEQ ,ETRDPS_DTL_SEQ ,BILG_CERT_NO ,TRST_CERTI_NO ,ETRDPS_DT ,TRST_DT ,CLNT_CD ,SELL_CLMN_DIV_CD ,SELL_PMNT_MTD_CD ,SELL_CLMN_DAY ,TAX_BILG_NO ,PRJCT_CD ,ETRDPS_AMT ,PRDT_GRP ,CREAT_ID ,CREAT_PGM ,CREAT_DTTM) SELECT CO_CD ,ETRDPS_SEQ ,1 + (SELECT MAX(ETRDPS_DTL_SEQ) FROM TB_AR05D02 D WHERE ETRDPS_SEQ = #{etrdpsSeqFind}) ,'0' ,'0' ,ETRDPS_DT ,NULL ,CLNT_CD ,NULL ,NULL ,NULL ,'0' ,NULL ,'0' ,PRDT_GRP ,#{userId} ,'MATCH_AR02' ,SYSDATE FROM TB_AR05D02 D02 WHERE ETRDPS_SEQ = #{etrdpsSeqFind} GROUP BY CO_CD, ETRDPS_SEQ, ETRDPS_DT, CLNT_CD, PRDT_GRP;
UPDATE TB_AR05D02 D02 SET ETRDPS_AMT = D02.ETRDPS_AMT + (SELECT SUM(ETRDPS_AMT) FROM TB_AR05D02 WHERE TRST_CERTI_NO = #{trstCertiNo} AND ETRDPS_SEQ = #{etrdpsSeqFind}) WHERE TRST_CERTI_NO = '0' AND ETRDPS_SEQ = #{etrdpsSeqFind};
END;

BEGIN
	DELETE 
		FROM TB_AR02M01
	WHERE
		TRST_CERTI_NO = #{trstCertiNo};



	DELETE
		FROM TB_AR05D02
			WHERE TRST_CERTI_NO = #{trstCertiNo};
END;

BEGIN



	DELETE
		FROM TB_AR05D02
			WHERE TRST_CERTI_NO = #{trstCertiNo};
END;

BEGIN
	UPDATE 
		TB_AR05D02
	SET   
	 	  BILG_CERT_NO     = 0
		, TRST_CERTI_NO    = 0
		, TRST_DT          = ''
		, SELL_CLMN_DIV_CD = ''
		, SELL_PMNT_MTD_CD = ''
		, SELL_CLMN_DAY    = ''
		, TAX_BILG_NO      = ''
		, PRJCT_CD         = ''
	WHERE 
		TRST_CERTI_NO IN (
							SELECT 
								TRST_CERTI_NO
							FROM 
								TB_AR02M01
							WHERE TRST_RPRC_SEQ = #{trstRprcSeq}
							  AND TRST_DTL_SEQ = #{trstDtlSeq}
							  AND CREAT_PGM = #{pgmId}

							  AND SELPCH_CD     = #{selpchCd}

							 	 );

	DELETE FROM TB_AR02M01
	WHERE TRST_RPRC_SEQ = #{trstRprcSeq}
	  AND TRST_DTL_SEQ = #{trstDtlSeq}
	  AND CREAT_PGM = #{pgmId}

	  AND SELPCH_CD     = #{selpchCd}
	  ;
 END;

mapper.select:
SELECT NVL(BILG_CERT_NO, 0) AS BILG_CERT_NO 
FROM TB_AR02M01
WHERE TRST_RPRC_SEQ = #{trstRprcSeq}
  AND TRST_DTL_SEQ = #{trstDtlSeq}
  AND CREAT_PGM = #{pgmId}

  AND SELPCH_CD     = #{selpchCd}

