mapper.select:
		SELECT
			COUNT(*)
		FROM TB_AR03M01 A		
		 LEFT OUTER JOIN TB_BM02M01 B ON A.TRANS_CO = B.CLNT_CD		
		 LEFT OUTER JOIN TB_BM02M01 C ON A.CLNT_CD  = C.CLNT_CD				 


 			    A.CO_CD = #{coCd}


 			AND A.TRANS_DT BETWEEN #{strtDt} AND #{endDt}


 			AND A.TRANS_CO = #{transCo}


 			AND B.CLNT_NM LIKE '%'|| #{transNm} ||'%'


 			AND A.WH_CD = #{whCd}


 			AND A.DES_NM LIKE '%' || #{desNm} ||'%'


 			AND A.CLNT_CD = #{clntCd}


 			AND C.CLNT_NM LIKE '%'|| #{clntNm}||'%'


 			AND A.PROC_YN = #{procYn}


 			AND A.PRDT_GRP = #{prdtGrp}

SELECT * FROM( SELECT ROWNUM AS RNUM, D.* FROM( SELECT TRANS_SEQ ,A.CO_CD ,FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM ,TO_CHAR(TO_DATE(A.TRANS_DT,'YYYYMMDD'),'YYYY-MM-DD') AS TRANS_DT ,A.TRANS_CO ,B.CLNT_NM AS TRANS_NM ,FN_CM05M01_CD_TO_NM(A.WH_CD) AS WH_NM ,A.WH_CD ,A.DES_CD ,A.DES_NM ,A.DRIVR_NM ,A.SHIP_VEH_NO ,A.SHIP_SEQ ,A.PRJCT_CD ,A.TOT_WT ,A.TRANS_UPR ,A.TRANS_AMT ,A.ARVPAY_YN ,A.PAY_CO ,A.CLNT_CD ,(SELECT CLNT_NM FROM TB_BM02M01 C WHERE A.CLNT_CD = C.CLNT_CD) AS CLNT_NM ,A.PROC_YN ,A.TRANS_RMK ,A.ODR_CREAT_DIV ,A.PRDT_GRP ,FN_CM05M01_CD_TO_NM(A.PRDT_GRP) AS PRDT_GRP_NM ,A.CREAT_ID ,A.CREAT_PGM ,A.CREAT_DTTM ,A.UDT_ID ,A.UDT_PGM ,A.UDT_DTTM ,A.TAXIVC_COPRT ,FN_CM05M01_CD_TO_NM(A.TAXIVC_COPRT) AS TAXIVC_COPRT_NM ,A.SITE_CD , (SELECT SITE_NM FROM TB_SD09M01 WHERE SITE_CD = A.SITE_CD) AS SITE_NM
, NVL( (SELECT SUM(H.BILG_AMT) FROM TB_AR02M01 H INNER JOIN TB_AR01M01 AR01 ON H.TRST_RPRC_SEQ = AR01.SHIP_SEQ AND SUBSTR(H.CREAT_PGM,1,6) = SUBSTR(AR01.CREAT_PGM,1,6) AND AR01.TRANS_SEQ = A.TRANS_SEQ INNER JOIN TB_BM01M01 BM01 ON H.TRST_PRDT_CD = BM01.PRDT_CD AND PRDT_NM LIKE '%운반비%'), 0) + NVL( (SELECT SUM(H.BILG_AMT) FROM TB_AR02M01 H INNER JOIN TB_OD01M01 OD01 ON H.TRST_RPRC_SEQ = OD01.ORDRG_SEQ AND SUBSTR(H.CREAT_PGM,1,6) = SUBSTR(OD01.CREAT_PGM,1,6) AND OD01.TRANS_SEQ = A.TRANS_SEQ INNER JOIN TB_BM01M01 BM01 ON H.TRST_PRDT_CD = BM01.PRDT_CD AND PRDT_NM LIKE '%운반비%'), 0) + NVL( (SELECT SUM(H.BILG_AMT) FROM TB_AR02M01 H INNER JOIN TB_BM01M01 BM01 ON H.TRST_PRDT_CD = BM01.PRDT_CD AND PRDT_NM LIKE '%운반비%' AND H.TRANS_SEQ = A.TRANS_SEQ), 0) AS SALES_TRANS_AMT --매출운반비 FROM TB_AR03M01 A LEFT OUTER JOIN TB_BM02M01 B ON A.TRANS_CO = B.CLNT_CD LEFT OUTER JOIN TB_BM02M01 C ON A.CLNT_CD = C.CLNT_CD
A.CO_CD = #{coCd}
AND A.TRANS_DT BETWEEN #{strtDt} AND #{endDt}
AND A.TRANS_CO = #{transCo}
AND B.CLNT_NM LIKE '%'|| #{transNm} ||'%'
AND A.WH_CD = #{whCd}
AND A.DES_NM LIKE '%' || #{desNm} ||'%'
AND A.CLNT_CD = #{clntCd}
AND C.CLNT_NM LIKE '%'|| #{clntNm}||'%'
AND A.PROC_YN = #{procYn}
AND A.TAXIVC_COPRT = #{taxivcCoprt}
AND A.PRDT_GRP = #{prdtGrp}
ORDER BY A.TRANS_DT,A.SITE_CD,A.TRANS_CO ) D ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

mapper.update:
UPDATE TB_AR03M01
SET
	 CO_CD     = #{coCd}
	,TRANS_DT  = TO_CHAR(TO_DATE(#{transDt},'YYYY-MM-DD'),'YYYYMMDD')
	,TRANS_CO  = #{transCo}
	,WH_CD     = #{whCd}
	,DES_NM    = #{desNm}
	,DRIVR_NM  = #{drivrNm}
	,PRJCT_CD  = #{prjctCd}
	,SITE_CD   = #{siteCd}
	,TOT_WT    = #{totWt}
	,TRANS_UPR = #{transUpr}
	,TRANS_AMT = #{transAmt}
	,ARVPAY_YN = #{arvpayYn}
	,PAY_CO    = #{payCo}
	,CLNT_CD   = #{clntCd}
	,PROC_YN   = #{procYn}
	,TRANS_RMK = #{transRmk}
	,UDT_PGM   = #{pgmId}
	,UDT_ID    = #{userId}
	,UDT_DTTM  = SYSDATE
	,TAXIVC_COPRT = #{taxivcCoprt, jdbcType=VARCHAR}
	,SHIP_VEH_NO  = #{shipVehNo, jdbcType=VARCHAR}
	,PRDT_GRP     = #{prdtGrp, jdbcType=VARCHAR}
WHERE
	TRANS_SEQ = #{transSeq}

UPDATE TB_AR03M01
SET
	 PROC_YN   = #{procYn}
	,UDT_PGM   = #{pgmId}
	,UDT_ID    = #{userId}
	,UDT_DTTM  = SYSDATE
WHERE
	TRANS_SEQ = #{transSeq}
