mapper.select:
SELECT COUNT(*)
FROM TB_AR04M01 A


	   AND A.CO_CD = #{coCd}


	   AND A.SELL_DT BETWEEN REPLACE(#{startDt}, '-', '') AND REPLACE(#{endDt}, '-', '')


	   AND A.CLNT_CD = #{clntCd}


	   AND A.SELPCH_CD = #{selpchCd}


	   AND A.TAX_BILG_NO = #{taxBilgNo}


	   AND A.TAX_ADMS_YN = #{taxAdmsYn}


	   AND A.XML_FLOW = #{xmlFlow}


	   AND A.ACC_SLIP_YN = #{accSlipYn}

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT A.BILG_CERT_NO, A.CO_CD, TO_CHAR(TO_DATE(A.SELL_DT, 'YYYYMMDD'), 'YYYY-MM-DD') SELL_DT, FN_CM05M01_CD_TO_NM(A.SELPCH_CD) SELPCH_CD, A.CLNT_CD, A.CLNT_NM, TO_CHAR(TO_DATE(A.TAX_DT, 'YYYYMMDD'), 'YYYY-MM-DD') TAX_DT, A.TAX_BILG_NO, A.TAX_DMS5_7240, A.TAX_MOA5_23, A.TAX_MOA5_124, A.TAX_MOA5_388, FN_CM05M01_CD_TO_NM(A.RFF_CNO) RFF_CNO, FN_CM05M01_CD_TO_NM(A.RFF_VA) RFF_VA, A.TAX_DIV_CD, FN_CM05M01_CD_TO_NM(A.RFF_GN1) RFF_GN1, FN_CM05M01_CD_TO_NM(A.RFF_GN2) RFF_GN2, FN_CM05M01_CD_TO_NM(A.RFF_AEA) RFF_AEA, FN_CM05M01_CD_TO_NM(A.BGM_1225) BGM_1225, A.BGM_1225 BGM_1225_CD, A.BGM_4343, FN_CM05M01_CD_TO_NM(A.XML_FLOW) XML_FLOW, A.TAX_CREAT_ID, A.TAX_CREAT_NM, A.TAXIVC_COPRT, A.TAXIVC_COPRT_NO, A.TAXIVC_COPRT_CEO, A.TAXIVC_COPRT_NM, A.TAX_COPRT_BIZCON, A.TAXVIC_COPRT_BSTY, A.CTAII_MD_2, A.COMII_AL, A.COMII_EM, A.COMII_TE, A.COMII_FX, A.CTAIV_BJ_1, A.TAXIVC_RCV_CRN, A.TAXIVC_RCV_CEO, A.TAXIVC_RCV_ADDR, A.TAXIVC_RCV_BIZCON, A.TAXIVC_RCV_BSTY, A.TAX_RCV_SN, A.TAX_RCV_NM, A.TAX_RCV_EMAIL, A.TAX_RCV_EMAIL2, A.TAX_RCV_FAX, A.TAX_RCV_TEL, A.TAX_MOBLPHON, A.FTXAC1_1, A.FTXAC2_1, A.FTXAC3_1, A.TAX_MOA5_22, A.TAX_MOA5_212, A.TAX_MOA5_308, A.TAX_MOA5_9, A.TRST_QTY, A.TRST_WT, A.TRST_UPR, A.TRST_AMT, A.REAL_TRST_QTY, A.REAL_TRST_WT, A.REAL_TRST_UPR, A.REAL_TRST_AMT, A.BILG_QTY, A.BILG_WT, A.BILG_UPR, A.BILG_AMT, A.TRANS_AMT, A.TRST_DC_AMT, A.ETC_AMT, A.SND_YN, A.SND_DTTM, A.SND_PROC_ID, A.CSEQ_RCV_YN, TO_CHAR(A.CSEQ_RCV_DTTM, 'YYYY-MM-DD HH24:MI:SS') CSEQ_RCV_DTTM, A.TAX_ADMS_YN, TO_CHAR(A.TAX_ADMS_DTTM, 'YYYY-MM-DD HH24:MI:SS') TAX_ADMS_DTTM, A.RCV_YN, A.RCV_DTTM, A.RCV_PROC_ID, A.ACC_SLIP_NO, A.ACC_SLIP_YN, TO_CHAR(A.ACC_SLIP_DTTM, 'YYYY-MM-DD HH24:MI:SS') ACC_SLIP_DTTM, A.ACC_PROC_ID, A.ETC_FIELD1, A.ETC_FIELD2, A.ETC_FIELD3 FROM TB_AR04M01 A
AND A.CO_CD = #{coCd}
AND A.SELL_DT BETWEEN REPLACE(#{startDt}, '-', '') AND REPLACE(#{endDt}, '-', '')
AND A.CLNT_CD = #{clntCd}
AND A.SELPCH_CD = #{selpchCd}
AND A.TAX_BILG_NO = #{taxBilgNo}
AND A.TAX_ADMS_YN = #{taxAdmsYn}
AND A.XML_FLOW = #{xmlFlow}
AND A.ACC_SLIP_YN = #{accSlipYn}
ORDER BY SELL_DT DESC ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

