mapper.select:
SELECT 
	COUNT(*)
FROM
	TB_AR05M01 D
	LEFT JOIN TB_AR05D01 B ON D.BIL_NO = B.BIL_NO AND D.CO_CD = B.CO_CD AND D.CLNT_CD = B.CLNT_CD 
	INNER JOIN TB_BM02M01 C ON D.CLNT_CD = C.CLNT_CD


		D.CO_CD = #{coCd}


		 AND TO_DATE(D.ETRDPS_DT, 'YYYYMMDD') >= TO_DATE(#{startDt}, 'YYYY-MM-DD') 


		 AND TO_DATE(D.ETRDPS_DT, 'YYYYMMDD') < TO_DATE(#{endDt}, 'YYYY-MM-DD') + 1 


		AND C.CLNT_DIV_CD = #{clntDivCd}


		AND D.SETLE_TYP_CD = #{setleTypCd}


		AND D.ETRDPS_BKAC_CD = #{etrdpsBkacCd}


		AND D.ETRDPS_TYP = #{etrdpsTyp}


		AND C.CLNT_NM LIKE '%'|| #{clntNm} ||'%'


		AND C.CLNT_CD = #{clntCd}


        AND D.TAXIVC_COPRT = #{taxivcCoprt}


		AND D.PRDT_GRP = #{prdtGrp}


		AND D.AUTO_MATCH_FLAG = #{autoMapping}


		AND DECODE(0, D.ETRDPS_AMT - NVL((SELECT SUM(E.ETRDPS_AMT) FROM TB_AR05D02 E WHERE E.ETRDPS_SEQ = D.ETRDPS_SEQ AND TRST_CERTI_NO != 0),0), 'Y', 'N') = #{completeMap}



		 AND D.DZ_SND_SEQ IS NOT NULL


		 AND D.DZ_SND_SEQ IS NULL

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT D.CO_CD, FN_CM05M01_CD_TO_NM(D.CO_CD) AS CO_NM, D.ETRDPS_SEQ, TO_CHAR(TO_DATE(D.ETRDPS_DT, 'YYYYMMDD'), 'YYYY-MM-DD') ETRDPS_DT, C.CLNT_CD, C.CLNT_NM, D.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(D.SETLE_TYP_CD) AS SETLE_TYP_NM, D.ETRDPS_AMT, FN_CM05M01_CD_TO_NM(D.ETRDPS_BKAC_CD) AS ETRDPS_BKAC_NM, D.SUMRY, FN_CM05M01_CD_TO_NM(B.BIL_TYP_CD) AS BIL_TYP_NM, B.BIL_NO, TO_CHAR(TO_DATE(B.EXPRTN_DT, 'YYYYMMDD'), 'YYYY-MM-DD') EXPRTN_DT, FN_CM05M01_CD_TO_NM(B.PYMNT_BANK_CD) AS PYMNT_BANK_NM, B.ENDRS_PSN, ETRDPS_TYP, FN_CM05M01_CD_TO_NM(D.ETRDPS_TYP) AS ETRDPS_TYP_NM, D.TAXIVC_COPRT, FN_CM05M01_CD_TO_NM(D.TAXIVC_COPRT) AS TAXIVC_COPRT_NM, D.MSCL_PRFT_YN, D.AUTO_MATCH_FLAG, D.ETRDPS_AMT - NVL((SELECT SUM(E.ETRDPS_AMT) FROM TB_AR05D02 E WHERE E.ETRDPS_SEQ = D.ETRDPS_SEQ AND TRST_CERTI_NO != 0),0) AS REMAIN_AMT, DECODE(0, D.ETRDPS_AMT - NVL((SELECT SUM(E.ETRDPS_AMT) FROM TB_AR05D02 E WHERE E.ETRDPS_SEQ = D.ETRDPS_SEQ AND TRST_CERTI_NO != 0),0), 'Y', 'N') AS REMAIN_YN, (SELECT dz_code FROM TB_CM05M01 WHERE CODE_ID = ETRDPS_TYP) AS DZ_CODE, /*더존 코드 추가 2022 KDM*/ (SELECT dz_code FROM TB_CM05M01 WHERE CODE_ID = D.SETLE_TYP_CD) AS DZ_CODE2, /*더존 코드 추가 2022 KDM*/ (SELECT CODE_DESC FROM TB_CM05M01 WHERE CODE_ID = ETRDPS_BKAC_CD) AS BK_ACT, /*은행계좌 추가 2022 KDM*/ (SELECT CRN FROM TB_BM02M01 WHERE CLNT_CD = C.CLNT_CD) AS CRN,/*사업자 번호 추가 2022 KDM*/ D.SETLE_TYP_CD,/*거래 code 2022 KDM*/ (SELECT dz_code FROM TB_CM05M01 WHERE CODE_ID = D.CO_CD) AS DZ_CO , /*법인 코드 추가 2022 KDM*/ DECODE(D.DZ_SND_SEQ,NULL,'N','Y') AS DZ_SND_YN, D.DZ_SND_SEQ, (SELECT dz_code FROM TB_CM05M01 WHERE CODE_ID = D.TAXIVC_COPRT) AS DIV_CD /*발행법인 코드 추가 2022 KDM*/ FROM TB_AR05M01 D LEFT JOIN TB_AR05D01 B ON D.BIL_NO = B.BIL_NO AND D.CO_CD = B.CO_CD AND D.CLNT_CD = B.CLNT_CD INNER JOIN TB_BM02M01 C ON D.CLNT_CD = C.CLNT_CD
D.CO_CD = #{coCd}
AND TO_DATE(D.ETRDPS_DT, 'YYYYMMDD') >= TO_DATE(#{startDt}, 'YYYY-MM-DD')
AND TO_DATE(D.ETRDPS_DT, 'YYYYMMDD') < TO_DATE(#{endDt}, 'YYYY-MM-DD') + 1
AND C.CLNT_DIV_CD = #{clntDivCd}
AND C.CLNT_NM LIKE '%'|| #{clntNm} ||'%'
AND C.CLNT_CD = #{clntCd}
AND D.SETLE_TYP_CD = #{setleTypCd}
AND D.ETRDPS_BKAC_CD = #{etrdpsBkacCd}
AND D.ETRDPS_TYP = #{etrdpsTyp}
AND D.TAXIVC_COPRT = #{taxivcCoprt}
AND D.PRDT_GRP = #{prdtGrp}
AND D.AUTO_MATCH_FLAG = #{autoMapping}
AND DECODE(0, D.ETRDPS_AMT - NVL((SELECT SUM(E.ETRDPS_AMT) FROM TB_AR05D02 E WHERE E.ETRDPS_SEQ = D.ETRDPS_SEQ AND TRST_CERTI_NO != 0),0), 'Y', 'N') = #{completeMap}
AND D.DZ_SND_SEQ IS NOT NULL
AND D.DZ_SND_SEQ IS NULL
ORDER BY D.CO_CD, D.TAXIVC_COPRT, D.ETRDPS_DT, C.CLNT_NM, D.CREAT_DTTM DESC ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

mapper.update:

UPDATE TB_AR02M01
   SET ETRDPS_AMT = NVL(ETRDPS_AMT, 0) + #{etrdpsAmt}
     , ETRDPS_YN = CASE WHEN #{etrdpsAmt} = (NVL(BILG_VAT_AMT, 0) + NVL(BILG_AMT, 0) - NVL(ETRDPS_AMT, 0)) THEN 'Y' ELSE 'N' END
 WHERE CO_CD = #{coCd}
   AND TRST_CERTI_NO = #{trstCertiNo}

mapper.insert:
INSERT INTO TB_AR05D02
(
	CO_CD
	, ETRDPS_SEQ
	, ETRDPS_DTL_SEQ
	, BILG_CERT_NO
	, TRST_CERTI_NO
	, ETRDPS_DT
	, TRST_DT
	, CLNT_CD
	, SELL_CLMN_DIV_CD
	, SELL_PMNT_MTD_CD
	, SELL_CLMN_DAY
	, TAX_BILG_NO
	, PRJCT_CD
	, ETRDPS_AMT
	, CREAT_ID
	, CREAT_PGM
	, CREAT_DTTM
	, PRDT_GRP
	, EXPRTN_DT
)
VALUES
(
	#{coCd}
	, #{etrdpsSeq}
	, (SELECT NVL(MAX(ETRDPS_DTL_SEQ), 0) + 1 FROM TB_AR05D02 WHERE ETRDPS_SEQ = #{etrdpsSeq})
	, #{bilgCertNo}
	, #{trstCertiNo}
	, REPLACE(#{etrdpsDt}, '-', '')
	, REPLACE(#{trstDt}, '-', '')
	, #{trstClntCd, jdbcType=VARCHAR}
	, #{clmnDivCd, jdbcType=VARCHAR}
	, #{pmntMtdCd, jdbcType=VARCHAR}
	, REPLACE(#{clmnDay, jdbcType=VARCHAR}, '-', '')
	, #{taxBilgNo}
	, NVL(#{prjctCd, jdbcType=VARCHAR},0)
	, #{etrdpsAmt, jdbcType=VARCHAR}
	, #{userId}
	, #{pgmId}
	, SYSDATE
	, NVL(#{prdtGrp}, 'PRDTGRP1')
	, #{exprtnDt, jdbcType=VARCHAR}
)

mapper.update:
 /* ar05. updateDzSndSeq 더존 dzsndseq 값 update */
	UPDATE TB_AR05M01
 	   SET
	  	   DZ_SND_SEQ	   = #{dzSndSeq}
	 WHERE CO_CD 		   = #{coCd} 
	   AND ETRDPS_DT       = REPLACE(#{etrdpsDt}, '-', '')  
	   AND ETRDPS_SEQ      = #{etrdpsSeq}
	   AND CLNT_CD         = #{clntCd}
