mapper.select:
	SELECT COUNT(*)
  	 FROM TB_AR07M01 C
	  LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = C.CLNT_CD 



		  AND C.CO_CD = #{coCd}				


		  AND C.CLNT_CD = #{clntCd}				


		   AND BM02.CLNT_NM  LIKE '%' || #{clntNm} ||'%'


		   AND  C.CLOSE_YM = SUBSTRING(REPLACE(#{baseDt}, '-', ''),0,6)


		   AND  C.CLOSE_YM = REPLACE(#{closeYm}, '-', '')


		   AND C.TAXIVC_COPRT = #{taxivcCoprt}


		   AND C.SELPCH_CD = #{selpchCd}

	SELECT COUNT(*)
  	 FROM TB_AR07M01 C
	  LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = C.CLNT_CD 
	  WHERE  C.CLOSE_YM = TO_CHAR(ADD_MONTHS(SYSDATE,-1),'YYYYMM')

		  AND C.CO_CD = #{coCd}				


		  AND C.CLNT_CD = #{clntCd}				


		   AND BM02.CLNT_NM  LIKE '%' || #{clntNm} ||'%'


		   AND  C.CLOSE_YM = REPLACE(#{closeYm}, '-', '')


		   AND C.TAXIVC_COPRT = #{taxivcCoprt}


		   AND C.SELPCH_CD = #{selpchCd}

SELECT COUNT(*) FROM (SELECT ROWNUM AS RNUM , A.* FROM ( SELECT
CO_CD ,
CLNT_NM , PRDT_GRP , PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , ETRDPS_YM , CLOSE_YM , CLNT_CD , SUM(BASIS_AMT) AS BASIS_AMT , SUM(CLMN_AMT) AS CLMN_AMT , SUM(TOT_AMT) AS TOT_AMT
, SUM(BFR_BASIS_AMT) AS BFR_BASIS_AMT , SUM(CLOSE_YM1_CLMN_AMT) AS CLOSE_YM1_CLMN_AMT , SUM(CLOSE_YM1_TOT_AMT) AS CLOSE_YM1_TOT_AMT , SUM(CLOSE_YM1) AS CLOSE_YM1 , SUM(BASIS_CREDIT_AMT) AS BASIS_CREDIT_AMT , SUM(CLOSE_YM2_CLMN_AMT) AS CLOSE_YM2_CLMN_AMT , SUM(CLOSE_YM2_TOT_AMT) AS CLOSE_YM2_TOT_AMT , SUM(CLOSE_YM2) AS CLOSE_YM2 FROM ( SELECT 1 AS TYPE1 , AR05.CO_CD , BM02.CLNT_NM , AR05.PRDT_GRP , NVL(AR05.PRJCT_CD,0) AS PRJCT_CD , BM02.SELL_PAY_DIV_CD , NVL(AR05.SELL_CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(AR05.SELL_PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , BM02.SELL_VAT_CD , M01.TAXIVC_COPRT AS TAXIVC_COPRT , NVL(AR05.SELL_CLMN_DAY, BM02.SELL_CLMN_DAY) AS SELL_CLMN_DAY , SUBSTRING(AR05.ETRDPS_DT, 0, 6) ETRDPS_YM , SUBSTRING(AR05.ETRDPS_DT, 0, 4) || '-' || SUBSTRING( AR05.ETRDPS_DT, 5,2) CLOSE_YM , AR05.CLNT_CD , 0 AS BASIS_AMT , SUM(AR05.ETRDPS_AMT) CLMN_AMT , 0 AS TOT_AMT , 0 AS TRMEND_AMT , 0 AS BFR_BASIS_AMT , 0 AS CLOSE_YM1_CLMN_AMT , 0 AS CLOSE_YM1_TOT_AMT , 0 AS CLOSE_YM1 , 0 AS BASIS_CREDIT_AMT , 0 AS CLOSE_YM2_CLMN_AMT , 0 AS CLOSE_YM2_TOT_AMT , 0 AS CLOSE_YM2 FROM TB_AR05D02 AR05 INNER JOIN TB_AR05M01 M01 ON M01.CO_CD = AR05.CO_CD AND M01.ETRDPS_SEQ = AR05.ETRDPS_SEQ LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR05.CLNT_CD WHERE 'SELPCH2' = (SELECT NVL(CODE_RPRC,#{selpchCd}) FROM TB_CM05M01 WHERE CODE_ID= M01.ETRDPS_TYP)
AND AR05.CO_CD = #{coCd}
AND AR05.CLNT_CD = #{clntCd}
AND AR05.ETRDPS_DT BETWEEN TO_CHAR(TO_DATE(REPLACE(#{baseDt}, '-', ''),'YYYYMMDD'),'YYYYMM') ||'01' AND REPLACE(#{baseDt}, '-', '')
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND AR05.PRDT_GRP = #{prdtGrp}
AND M01.TAXIVC_COPRT = #{taxivcCoprt}
GROUP BY AR05.CO_CD, SUBSTRING(AR05.ETRDPS_DT, 0, 6), AR05.CLNT_CD, SUBSTRING(AR05.ETRDPS_DT, 0, 4) || '-' || SUBSTRING(AR05.ETRDPS_DT, 5,2) , BM02.CLNT_NM , AR05.PRDT_GRP , NVL(AR05.PRJCT_CD,0) , BM02.SELL_PAY_DIV_CD , NVL(AR05.SELL_CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) , NVL(AR05.SELL_PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) , BM02.SELL_VAT_CD , M01.TAXIVC_COPRT , NVL(AR05.SELL_CLMN_DAY, BM02.SELL_CLMN_DAY) , AR05.ETRDPS_SEQ UNION ALL SELECT 2 AS TYPE1 , AR02.CO_CD , BM02.CLNT_NM , (SELECT PRDT_GRP FROM TB_BM01M01 BM01 WHERE BM01.PRDT_CD = AR02.TRST_PRDT_CD ) AS PRDT_GRP , AR02.PRJCT_CD , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) AS SELL_CLMN_DAY , SUBSTRING(TRST_DT, 0, 6) TRST_YM , SUBSTRING(TRST_DT, 0, 4) || '-' || SUBSTRING(TRST_DT, 5,2) CLOSE_YM , TRST_CLNT_CD AS CLNT_CD , 0 AS BASIS_AMT , 0 AS CLMN_AMT , SUM(NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0)) AS TOT_AMT , 0 AS TRMEND_AMT , 0 AS BFR_BASIS_AMT , 0 AS CLOSE_YM1_CLMN_AMT , 0 AS CLOSE_YM1_TOT_AMT , 0 AS CLOSE_YM1 , 0 AS BASIS_CREDIT_AMT , 0 AS CLOSE_YM2_CLMN_AMT , 0 AS CLOSE_YM2_TOT_AMT , 0 AS CLOSE_YM2 FROM TB_AR02M01 AR02 LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR02.TRST_CLNT_CD LEFT JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = AR02.PRJCT_CD WHERE SELPCH_CD = 'SELPCH2'
AND AR02.CO_CD = #{coCd}
AND AR02.TRST_CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND TRST_DT BETWEEN TO_CHAR(TO_DATE(REPLACE(#{baseDt}, '-', ''),'YYYYMMDD'),'YYYYMM') ||'01' AND REPLACE(#{baseDt}, '-', '')
AND AR02.TAXIVC_COPRT = #{taxivcCoprt}
AND AR02.PRDT_DIV IN (SELECT DISTINCT PRDT_DIV FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} )
GROUP BY AR02.CO_CD, SUBSTRING(TRST_DT, 0, 6), TRST_CLNT_CD , SUBSTRING(TRST_DT, 0, 4) || '-' || SUBSTRING(TRST_DT, 5,2) , BM02.CLNT_NM , AR02.TRST_PRDT_CD , AR02.PRJCT_CD , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) , SELL_VAT_CD , TAXIVC_COPRT , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) UNION ALL SELECT 3 AS TYPE1 , C.CO_CD , BM02.CLNT_NM , C.PRDT_GRP , C.PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 6) AS ETRDPS_YM , SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 4) || '-' || SUBSTRING(REPLACE(#{baseDt}, '-', ''), 5,2) CLOSE_YM , C.CLNT_CD , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) BASIS_AMT , 0 CLMN_AMT , 0 TOT_AMT , 0 TRMEND_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.BASIS_CREDIT_AMT, 0))) BFR_BASIS_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.CELL_CLMN_AMT, 0))) CLOSE_YM1_CLMN_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.CELL_TOT_AMT, 0))) CLOSE_YM1_TOT_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) CLOSE_YM1 , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.BASIS_CREDIT_AMT, 0))) BASIS_CREDIT_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.CELL_CLMN_AMT, 0))) CLOSE_YM2_CLMN_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.CELL_TOT_AMT, 0))) CLOSE_YM2_TOT_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) CLOSE_YM2 FROM TB_AR07M01 C LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = C.CLNT_CD WHERE 1 = 1
AND SELPCH_CD = #{selpchCd}
AND C.CO_CD = #{coCd}
AND C.CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND C.CLOSE_YM BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') AND TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM')
AND TAXIVC_COPRT = #{taxivcCoprt}
AND PRDT_GRP = #{prdtGrp}
GROUP BY C.CO_CD , C.CLNT_CD , BM02.CLNT_NM , C.PRDT_GRP , C.PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY ) GROUP BY
CO_CD,
CLNT_NM , PRDT_GRP , PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , ETRDPS_YM , CLOSE_YM , CLNT_CD
) A )

SELECT * FROM (SELECT ROWNUM AS RNUM , A.* , (SELECT CRN FROM TB_BM02M01 BM02M WHERE BM02M.CLNT_CD = A.CLNT_CD) CRN_NO
, FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM
, FN_CM05M01_CD_TO_NM(A.PRDT_GRP) AS PRDT_GRP_NM , (SELECT PRJCT_NM FROM TB_SD05M01 SD05 WHERE SD05.PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM , FN_CM05M01_CD_TO_NM(A.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM , FN_CM05M01_CD_TO_NM(A.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM , FN_CM05M01_CD_TO_NM(A.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_NM , FN_CM05M01_CD_TO_NM(A.SELL_VAT_CD) AS SELL_VAT_NM , A.TAXIVC_COPRT , FN_CM05M01_CD_TO_NM(A.TAXIVC_COPRT) AS TAXIVC_COPRT_NM , BASIS_AMT + TOT_AMT - CLMN_AMT AS TRMEND_AMT
, F_CREDIT_LOAN2('T', A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-',''),0) AS CRDT_LMT , F_CREDIT_PRDTGRP_NON_RECV_AMT(A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS NON_RECV_AMT , F_CREDIT_PRDTGRP_UNSETL_BIL_AMT(A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS UNSETL_BIL_AMT , F_CREDIT_PRDTGRP_NON_PAY_AMT(A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS NON_PAY_AMT , F_CREDIT_LOAN2('C', A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-',''),0) AS CRDT_LOAN
, F_CREDIT_LOAN2('T', A.CLNT_CD, '' ,A.PRDT_GRP, REPLACE(#{baseDt},'-',''),0) AS CRDT_LMT , F_CREDIT_PRDTGRP_NON_RECV_AMT(A.CLNT_CD, '',A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS NON_RECV_AMT , F_CREDIT_PRDTGRP_UNSETL_BIL_AMT(A.CLNT_CD, '',A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS UNSETL_BIL_AMT , F_CREDIT_PRDTGRP_NON_PAY_AMT(A.CLNT_CD, '',A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS NON_PAY_AMT , F_CREDIT_LOAN2('C', A.CLNT_CD, '' ,A.PRDT_GRP, REPLACE(#{baseDt},'-',''),0) AS CRDT_LOAN
FROM ( SELECT
CO_CD,
CLNT_NM , PRDT_GRP , PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , ETRDPS_YM , CLOSE_YM , CLNT_CD , SUM(BASIS_AMT) AS BASIS_AMT , SUM(CLMN_AMT) AS CLMN_AMT , SUM(TOT_AMT) AS TOT_AMT
, SUM(BFR_BASIS_AMT) AS BFR_BASIS_AMT , SUM(CLOSE_YM1_CLMN_AMT) AS CLOSE_YM1_CLMN_AMT , SUM(CLOSE_YM1_TOT_AMT) AS CLOSE_YM1_TOT_AMT , SUM(CLOSE_YM1) AS CLOSE_YM1 , SUM(BASIS_CREDIT_AMT) AS BASIS_CREDIT_AMT , SUM(CLOSE_YM2_CLMN_AMT) AS CLOSE_YM2_CLMN_AMT , SUM(CLOSE_YM2_TOT_AMT) AS CLOSE_YM2_TOT_AMT , SUM(CLOSE_YM2) AS CLOSE_YM2 FROM ( SELECT 1 AS TYPE1 , AR05.CO_CD , BM02.CLNT_NM , AR05.PRDT_GRP , NVL(AR05.PRJCT_CD,0) AS PRJCT_CD , BM02.SELL_PAY_DIV_CD , NVL(AR05.SELL_CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(AR05.SELL_PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , BM02.SELL_VAT_CD , M01.TAXIVC_COPRT AS TAXIVC_COPRT , NVL(AR05.SELL_CLMN_DAY, BM02.SELL_CLMN_DAY) AS SELL_CLMN_DAY , SUBSTRING(AR05.ETRDPS_DT, 0, 6) ETRDPS_YM , SUBSTRING(AR05.ETRDPS_DT, 0, 4) || '-' || SUBSTRING(AR05.ETRDPS_DT, 5,2) CLOSE_YM , AR05.CLNT_CD , 0 AS BASIS_AMT , SUM(AR05.ETRDPS_AMT) CLMN_AMT , 0 AS TOT_AMT , 0 AS TRMEND_AMT , 0 AS BFR_BASIS_AMT , 0 AS CLOSE_YM1_CLMN_AMT , 0 AS CLOSE_YM1_TOT_AMT , 0 AS CLOSE_YM1 , 0 AS BASIS_CREDIT_AMT , 0 AS CLOSE_YM2_CLMN_AMT , 0 AS CLOSE_YM2_TOT_AMT , 0 AS CLOSE_YM2 FROM TB_AR05D02 AR05 INNER JOIN TB_AR05M01 M01 ON M01.CO_CD = AR05.CO_CD AND M01.ETRDPS_SEQ = AR05.ETRDPS_SEQ LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR05.CLNT_CD WHERE #{selpchCd} = (SELECT NVL(CODE_RPRC,#{selpchCd}) FROM TB_CM05M01 WHERE CODE_ID= M01.ETRDPS_TYP)
AND NVL(AR05.ETRDPS_AMT,0) != 0
AND AR05.CO_CD = #{coCd}
AND AR05.CLNT_CD = #{clntCd}
AND AR05.ETRDPS_DT BETWEEN TO_CHAR(TO_DATE(REPLACE(#{baseDt}, '-', ''),'YYYYMMDD'),'YYYYMM') ||'01' AND REPLACE(#{baseDt}, '-', '')
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND AR05.PRDT_GRP = #{prdtGrp}
AND M01.TAXIVC_COPRT = #{taxivcCoprt}
GROUP BY AR05.CO_CD , SUBSTRING(AR05.ETRDPS_DT, 0, 6), AR05.CLNT_CD, SUBSTRING(AR05.ETRDPS_DT, 0, 4) || '-' || SUBSTRING(AR05.ETRDPS_DT, 5,2) , BM02.CLNT_NM , AR05.PRDT_GRP , NVL(AR05.PRJCT_CD,0) , BM02.SELL_PAY_DIV_CD , NVL(AR05.SELL_CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) , NVL(AR05.SELL_PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) , BM02.SELL_VAT_CD , M01.TAXIVC_COPRT , NVL(AR05.SELL_CLMN_DAY, BM02.SELL_CLMN_DAY) , AR05.ETRDPS_SEQ UNION ALL SELECT 2 AS TYPE1 , AR02.CO_CD , BM02.CLNT_NM , (SELECT PRDT_GRP FROM TB_BM01M01 BM01 WHERE BM01.PRDT_CD = AR02.TRST_PRDT_CD ) AS PRDT_GRP , AR02.PRJCT_CD , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) AS SELL_CLMN_DAY , SUBSTRING(TRST_DT, 0, 6) TRST_YM , SUBSTRING(TRST_DT, 0, 4) || '-' || SUBSTRING(TRST_DT, 5,2) CLOSE_YM , TRST_CLNT_CD AS CLNT_CD , 0 AS BASIS_AMT , 0 AS CLMN_AMT , SUM(NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0)) AS TOT_AMT , 0 AS TRMEND_AMT , 0 AS BFR_BASIS_AMT , 0 AS CLOSE_YM1_CLMN_AMT , 0 AS CLOSE_YM1_TOT_AMT , 0 AS CLOSE_YM1 , 0 AS BASIS_CREDIT_AMT , 0 AS CLOSE_YM2_CLMN_AMT , 0 AS CLOSE_YM2_TOT_AMT , 0 AS CLOSE_YM2 FROM TB_AR02M01 AR02 LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR02.TRST_CLNT_CD LEFT JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = AR02.PRJCT_CD WHERE 1 = 1
AND SELPCH_CD = #{selpchCd}
AND AR02.CO_CD = #{coCd}
AND AR02.TRST_CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND TRST_DT BETWEEN TO_CHAR(TO_DATE(REPLACE(#{baseDt}, '-', ''),'YYYYMMDD'),'YYYYMM') ||'01' AND REPLACE(#{baseDt}, '-', '')
AND AR02.TAXIVC_COPRT = #{taxivcCoprt}
AND AR02.PRDT_DIV IN (SELECT DISTINCT PRDT_DIV FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} )
GROUP BY AR02.CO_CD , SUBSTRING(TRST_DT, 0, 6), TRST_CLNT_CD , SUBSTRING(TRST_DT, 0, 4) || '-' || SUBSTRING(TRST_DT, 5,2) , BM02.CLNT_NM , AR02.TRST_PRDT_CD , AR02.PRJCT_CD , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) , SELL_VAT_CD , TAXIVC_COPRT , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) UNION ALL SELECT 3 AS TYPE1 , C.CO_CD , BM02.CLNT_NM , C.PRDT_GRP , C.PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 6) AS ETRDPS_YM , SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 4) || '-' || SUBSTRING(REPLACE(#{baseDt}, '-', ''), 5,2) CLOSE_YM , C.CLNT_CD , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) BASIS_AMT , 0 CLMN_AMT , 0 TOT_AMT , 0 TRMEND_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.BASIS_CREDIT_AMT, 0))) BFR_BASIS_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.CELL_CLMN_AMT, 0))) CLOSE_YM1_CLMN_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.CELL_TOT_AMT, 0))) CLOSE_YM1_TOT_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) CLOSE_YM1 , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.BASIS_CREDIT_AMT, 0))) BASIS_CREDIT_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.CELL_CLMN_AMT, 0))) CLOSE_YM2_CLMN_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.CELL_TOT_AMT, 0))) CLOSE_YM2_TOT_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) CLOSE_YM2 FROM TB_AR07M01 C LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = C.CLNT_CD WHERE 1 = 1
AND SELPCH_CD = #{selpchCd}
AND C.CO_CD = #{coCd}
AND C.CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND C.CLOSE_YM BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') AND TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM')
AND TAXIVC_COPRT = #{taxivcCoprt}
AND PRDT_GRP = #{prdtGrp}
GROUP BY C.CO_CD , C.CLNT_CD , BM02.CLNT_NM , C.PRDT_GRP , C.PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY ) GROUP BY
CO_CD,
CLNT_NM , PRDT_GRP , PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , ETRDPS_YM , CLOSE_YM , CLNT_CD ORDER BY
CO_CD,
CLNT_NM, PRDT_GRP, PRJCT_CD
) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(*) FROM ( SELECT
C.CO_CD,
C.CLOSE_YM AS ETRDPS_YM, SUBSTRING(C.CLOSE_YM, 0, 4) || '-' || SUBSTRING( C.CLOSE_YM, 5,2) CLOSE_YM, C.CLNT_CD , BM02.CLNT_NM, C.PRJCT_CD, SELL_PAY_DIV_CD, SELL_CLMN_DIV_CD, SELL_PMNT_MTD_CD, SELL_VAT_CD, SELL_CLMN_DAY, SUM(DECODE(C.CLOSE_YM, TO_CHAR(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'),'YYYYMM') ,NVL(C.BASIS_CREDIT_AMT, 0))) BASIS_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), 'YYYYMM') ,NVL(C.CELL_CLMN_AMT, 0))) CLMN_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), 'YYYYMM') ,NVL(C.CELL_TOT_AMT, 0))) TOT_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) TRMEND_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.BASIS_CREDIT_AMT, 0))) BFR_BASIS_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.CELL_CLMN_AMT, 0))) CLOSE_YM1_CLMN_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.CELL_TOT_AMT, 0))) CLOSE_YM1_TOT_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) CLOSE_YM1, SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.BASIS_CREDIT_AMT, 0))) BASIS_CREDIT_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.CELL_CLMN_AMT, 0))) CLOSE_YM2_CLMN_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.CELL_TOT_AMT, 0))) CLOSE_YM2_TOT_AMT, SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) CLOSE_YM2 FROM TB_AR07M01 C LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = C.CLNT_CD WHERE 1 = 1
AND SELPCH_CD = #{selpchCd}
AND C.CLOSE_YM BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') AND TO_CHAR(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), 'YYYYMM')
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND C.CO_CD = #{coCd}
AND C.CLNT_CD = #{clntCd}
AND C.TAXIVC_COPRT = #{taxivcCoprt}
AND PRDT_GRP = #{prdtGrp}
GROUP BY
C.CO_CD,
C.CLOSE_YM , C.CLNT_CD , BM02.CLNT_NM, C.PRJCT_CD, SELL_PAY_DIV_CD, SELL_CLMN_DIV_CD, SELL_PMNT_MTD_CD, SELL_VAT_CD, SELL_CLMN_DAY ) A

SELECT AR07.* , (SELECT NAME FROM TB_CM06M01 WHERE ID = AR07.SALES_EMP_ID ) AS SALES_EMP_NM , (SELECT PRJCT_NM FROM TB_SD05M01 SD05 WHERE SD05.PRJCT_CD = AR07.PRJCT_CD ) AS PRJCT_NM , FN_CM05M01_CD_TO_NM(AR07.TAXIVC_COPRT) AS TAXIVC_COPRT_NM , FN_CM05M01_CD_TO_NM(AR07.PRDT_GRP) AS PRDT_GRP_NM , FN_CM05M01_CD_TO_NM(AR07.CO_CD) AS CO_NM , FN_CM05M01_CD_TO_NM(AR07.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM , FN_CM05M01_CD_TO_NM(AR07.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM , FN_CM05M01_CD_TO_NM(AR07.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_NM , FN_CM05M01_CD_TO_NM(AR07.SELL_VAT_CD) AS SELL_VAT_NM , (SELECT SALES_EMP_ID FROM TB_BM02D01 BM02D01 WHERE BM02D01.CLNT_CD = AR07.CLNT_CD AND BM02D01.SALES_CO_CD = AR07.CO_CD AND BM02D01.USE_YN = 'Y' AND ROWNUM = 1 ) SALES_EMP_ID , F_CREDIT_LOAN2('T',AR07.CLNT_CD,AR07.CO_CD,AR07.PRDT_GRP,REPLACE(#{baseDt},'-',''),0) AS CRDT_LMT -- 총여신금액 , F_CREDIT_LOAN2('C',AR07.CLNT_CD,AR07.CO_CD,AR07.PRDT_GRP,REPLACE(#{baseDt},'-',''),0) AS CRDT_LOAN -- 여신잔액
,F_CREDIT_UNSETL_BIL_AMT(AR07.CLNT_CD, AR07.CO_CD, REPLACE(#{baseDt},'-','')) AS UNSETL_BIL_AMT -- 미결제어음 ,F_CREDIT_NON_PAY_AMT(AR07.CLNT_CD, AR07.CO_CD, REPLACE(#{baseDt},'-','')) AS NON_PAY_AMT -- 미지급금 ,(SELECT SUM(NVL(PLDG_AMT,0) /100 * NVL(PLDG_RCOGN_RATE,1)) FROM TB_BM02D02 BM02D02 WHERE NOT PLDG_DIV_CD IN('PLDG04', 'PLDG03') AND BM02D02.CLNT_CD = AR07.CLNT_CD AND BM02D02.CO_CD = AR07.CO_CD AND BM02D02.USE_YN = 'Y' AND NVL(EXPRTN_DT,REPLACE(#{baseDt},'-','')) >= REPLACE(#{baseDt},'-','')) AS PLDG_AMT -- 담보금액 FROM ( SELECT CO_CD , CRN_TYP , CLNT_CD , CLNT_NM , CRN_NO , PRDT_GRP , PRJCT_CD , TAXIVC_COPRT , (SELECT SALES_EMP_ID FROM TB_BM02D01 BM02D01 WHERE BM02D01.CLNT_CD = A.CLNT_CD AND BM02D01.SALES_CO_CD = A.CO_CD AND BM02D01.USE_YN = 'Y' AND ROWNUM = 1 ) SALES_EMP_ID , SELL_CLMN_DAY , SELL_CLMN_RMK , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , MIN(PCHS_CLMN_DAY) AS PCHS_CLMN_DAY , MIN(DECODE(SUBSTRING(TRST_DT,0,6),TO_CHAR(ADD_MONTHS(TO_DATE(#{baseDt},'YYYYMMDD'),-0),'YYYYMM'), PCHS_CLMN_DAY,'99991231')) AS PRE_0_PCHS_CLMN_DAY , MIN(DECODE(SUBSTRING(TRST_DT,0,6),TO_CHAR(ADD_MONTHS(TO_DATE(#{baseDt},'YYYYMMDD'),-1),'YYYYMM'), PCHS_CLMN_DAY,'99991231')) AS PRE_1_PCHS_CLMN_DAY , MIN(DECODE(SUBSTRING(TRST_DT,0,6),TO_CHAR(ADD_MONTHS(TO_DATE(#{baseDt},'YYYYMMDD'),-2),'YYYYMM'), PCHS_CLMN_DAY,'99991231')) AS PRE_2_PCHS_CLMN_DAY , MIN(CASE WHEN TO_DATE(TRST_DT,'YYYYMMDD') <= LAST_DAY(ADD_MONTHS(TO_DATE(#{baseDt},'YYYYMMDD'),-3)) THEN PCHS_CLMN_DAY ELSE '99991231' END) AS PRE_3_PCHS_CLMN_DAY
, SUM(DECODE(SUBSTRING(TRST_DT,0,6),TO_CHAR(ADD_MONTHS(TO_DATE(#{baseDt},'YYYYMMDD'),-0),'YYYYMM'), TOT_AMT,0)) AS CUR_CREDIT_AMT , SUM(DECODE(SUBSTRING(TRST_DT,0,6),TO_CHAR(ADD_MONTHS(TO_DATE(#{baseDt},'YYYYMMDD'),-1),'YYYYMM'), TOT_AMT,0)) AS PRE1_CREDIT_AMT , SUM(DECODE(SUBSTRING(TRST_DT,0,6),TO_CHAR(ADD_MONTHS(TO_DATE(#{baseDt},'YYYYMMDD'),-2),'YYYYMM'), TOT_AMT,0)) AS PRE2_CREDIT_AMT , SUM(CASE WHEN TO_DATE(TRST_DT,'YYYYMMDD') <= LAST_DAY(ADD_MONTHS(TO_DATE(#{baseDt},'YYYYMMDD'),-3)) THEN TOT_AMT ELSE 0 END) AS PRE3_CREDIT_AMT , SUM(TOT_AMT) AS CUR_TRMEND_CREDIT_AMT
, SUM(CASE WHEN PCHS_CLMN_DAY <= REPLACE(#{baseDt}, '-', '') THEN TOT_AMT ELSE 0 END) AS DELAY_AMT
FROM ( SELECT 0 AS TYPE1 , AR02.CO_CD , TRST_CLNT_CD AS CLNT_CD , BM02.CRN_TYP , BM02.CLNT_NM , BM02.CRN AS CRN_NO , AR02.TRST_DT , AR02.TRST_CERTI_NO , AR02.TAXIVC_COPRT , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE BM01.PRDT_GRP END AS PRDT_GRP , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE AR02.PRJCT_CD END AS PRJCT_CD , NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) AS SELL_CLMN_DAY , BM02.SELL_CLMN_RMK , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(SD05.PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , SELL_VAT_CD , CASE WHEN CM05M01.CODE_RPRC = 'MON' THEN TO_CHAR(LAST_DAY(ADD_MONTHS(TRUNC(TO_DATE(AR02.TRST_DT,'YYYYMMDD'),'MM'), NVL(CM05M01.CODE_ETC,0)) -1) + NVL(NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) ,0), 'YYYYMMDD') WHEN CM05M01.CODE_RPRC = 'DAY' THEN TO_CHAR(TO_DATE(AR02.TRST_DT,'YYYYMMDD')+ NVL(NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) ,0), 'YYYYMMDD') END AS PCHS_CLMN_DAY ,NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0) - SUM(NVL(AR05D02.ETRDPS_AMT,0)) AS TOT_AMT -- 매출채권금액 ,SUM(NVL(AR05D02.ETRDPS_AMT,0)) AS ETRDPS_AMT -- 수금금액 FROM TB_AR02M01 AR02 INNER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = AR02.TRST_PRDT_CD INNER JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR02.TRST_CLNT_CD LEFT JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = AR02.PRJCT_CD LEFT JOIN TB_CM05M01 CM05M01 ON NVL(SD05.CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) = CM05M01.CODE_ID LEFT JOIN TB_AR05D02 AR05D02 ON AR02.TRST_CERTI_NO = AR05D02.TRST_CERTI_NO AND AR05D02.ETRDPS_DT <= REPLACE(#{baseDt},'-','') WHERE SELPCH_CD = 'SELPCH2' AND AR02.TRST_DT <= REPLACE(#{baseDt},'-','') AND (AR02.ETRDPS_YN = 'N' OR AR02.TRST_CERTI_NO IN (SELECT DISTINCT D02.TRST_CERTI_NO FROM TB_AR05D02 D02 WHERE D02.ETRDPS_DT > REPLACE(#{baseDt},'-','')))
AND AR02.CO_CD = #{coCd}
AND AR02.TRST_CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND AR02.TAXIVC_COPRT = #{taxivcCoprt}
AND BM01.PRDT_GRP = #{prdtGrp}
GROUP BY AR02.CO_CD , TRST_CLNT_CD , BM02.CRN_TYP , BM02.CLNT_NM , BM02.CRN , AR02.TRST_DT , AR02.TRST_CERTI_NO , AR02.TAXIVC_COPRT , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE BM01.PRDT_GRP END , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE AR02.PRJCT_CD END , NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) , BM02.SELL_CLMN_RMK , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) , NVL(SD05.PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) , SELL_VAT_CD , CASE WHEN CM05M01.CODE_RPRC = 'MON' THEN TO_CHAR(LAST_DAY(ADD_MONTHS(TRUNC(TO_DATE(AR02.TRST_DT,'YYYYMMDD'),'MM'), NVL(CM05M01.CODE_ETC,0)) -1) + NVL(NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) ,0), 'YYYYMMDD') WHEN CM05M01.CODE_RPRC = 'DAY' THEN TO_CHAR(TO_DATE(AR02.TRST_DT,'YYYYMMDD')+ NVL(NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) ,0), 'YYYYMMDD') END ,NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0) ) A WHERE TOT_AMT != 0 GROUP BY CO_CD , CRN_TYP , CLNT_CD , CLNT_NM , CRN_NO , PRDT_GRP , PRJCT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , SELL_CLMN_RMK , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD
) AR07 WHERE DELAY_AMT > 0 AND PCHS_CLMN_DAY <= REPLACE(#{baseDt},'-','') ORDER BY CO_CD , CLNT_CD , CLNT_NM , PRDT_GRP , PRJCT_CD

SELECT T.* , NVL(BFR_BASIS_AMT,0) + NVL(CUR_CREDIT_AMT,0) + NVL(CLOSE_YM1_CREDIT_AMT,0) + NVL(CLOSE_YM2_CREDIT_AMT,0) AS TOT_TRMEND_AMT FROM (SELECT ROWNUM AS RNUM , A.* , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM , FN_CM05M01_CD_TO_NM(A.PRDT_GRP) AS PRDT_GRP_NM , (SELECT PRJCT_NM FROM TB_SD05M01 SD05 WHERE SD05.PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM , FN_CM05M01_CD_TO_NM(A.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM , FN_CM05M01_CD_TO_NM(A.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM , FN_CM05M01_CD_TO_NM(A.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_NM , FN_CM05M01_CD_TO_NM(A.SELL_VAT_CD) AS SELL_VAT_NM
, F_CREDIT_LOAN2('T', A.CLNT_CD, A.CO_CD, A.PRDT_GRP, REPLACE(#{baseDt},'-',''),0) AS CRDT_LMT , F_CREDIT_PRDTGRP_NON_RECV_AMT(A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS NON_RECV_AMT , F_CREDIT_PRDTGRP_UNSETL_BIL_AMT(A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS UNSETL_BIL_AMT , F_CREDIT_PRDTGRP_NON_PAY_AMT(A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS NON_PAY_AMT , F_CREDIT_LOAN2('C', A.CLNT_CD, A.CO_CD, A.PRDT_GRP, REPLACE(#{baseDt},'-',''),0) AS CRDT_LOAN , TOT_AMT - CLMN_AMT AS CUR_CREDIT_AMT , CLOSE_YM1_TOT_AMT - CLOSE_YM1_CLMN_AMT AS CLOSE_YM1_CREDIT_AMT , CLOSE_YM2_TOT_AMT - CLOSE_YM2_CLMN_AMT AS CLOSE_YM2_CREDIT_AMT , BASIS_CREDIT_AMT - ( ETRDPS_ALL_AMT - CLMN_AMT - CLOSE_YM1_CLMN_AMT - CLOSE_YM2_CLMN_AMT ) AS BASIS_CREDIT_CLNT_AMT , BASIS_AMT + TOT_AMT - CUR_CLMN_AMT AS TRMEND_AMT , ETRDPS_ALL_AMT - TOT_MATCH_AMT AS NOT_MAPPNG_AMT FROM ( SELECT CO_CD , CLNT_NM , MIN(PRDT_GRP) AS PRDT_GRP , MIN(PRJCT_CD) AS PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD
, DECODE(SELL_CLMN_DAY,0,'',SELL_CLMN_DAY) AS SELL_CLMN_DAY
, CLNT_CD , SUM(BASIS_AMT) AS BASIS_AMT , SUM(CLMN_AMT) AS CLMN_AMT , SUM(TOT_AMT) AS TOT_AMT
, SUM(BFR_BASIS_AMT) AS BFR_BASIS_AMT , SUM(CLOSE_YM1_CLMN_AMT) AS CLOSE_YM1_CLMN_AMT , SUM(CLOSE_YM1_TOT_AMT) AS CLOSE_YM1_TOT_AMT , SUM(CLOSE_YM1) AS CLOSE_YM1 , SUM(BASIS_CREDIT_AMT) AS BASIS_CREDIT_AMT , SUM(CLOSE_YM2_CLMN_AMT) AS CLOSE_YM2_CLMN_AMT , SUM(CLOSE_YM2_TOT_AMT) AS CLOSE_YM2_TOT_AMT , SUM(CLOSE_YM2) AS CLOSE_YM2 , SUM(ETRDPS_AMT) AS ETRDPS_ALL_AMT , SUM(CUR_CLMN_AMT) AS CUR_CLMN_AMT , SUM(TOT_MATCH_AMT) AS TOT_MATCH_AMT , SUM(OVER_MON_MATCH_AMT) AS OVER_MON_MATCH_AMT , SUM(UNDER_MON_MATCH_AMT) AS UNDER_MON_MATCH_AMT FROM ( SELECT 1 AS TYPE1 , AR05.CO_CD , BM02.CLNT_NM , CASE WHEN NVL(AR05.SELL_CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(AR05.SELL_CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(AR05.SELL_PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE AR05.PRDT_GRP END AS PRDT_GRP , CASE WHEN NVL(AR05.SELL_CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(AR05.SELL_CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(AR05.SELL_PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE NVL(AR05.PRJCT_CD,0) END AS PRJCT_CD , BM02.SELL_PAY_DIV_CD , NVL(AR05.SELL_CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(AR05.SELL_PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , BM02.SELL_VAT_CD , M01.TAXIVC_COPRT AS TAXIVC_COPRT , NVL(AR05.SELL_CLMN_DAY, BM02.SELL_CLMN_DAY) AS SELL_CLMN_DAY , SUBSTRING(AR05.TRST_DT, 0, 6) ETRDPS_YM , SUBSTRING(AR05.TRST_DT, 0, 4) || '-' || SUBSTRING(AR05.TRST_DT, 5,2) CLOSE_YM , AR05.CLNT_CD , 0 AS BASIS_AMT , SUM( DECODE(SUBSTRING(AR05.TRST_DT,0,6), SUBSTRING(REPLACE(#{baseDt}, '-', ''),0,6) ,NVL(AR05.ETRDPS_AMT, 0))) AS CLMN_AMT , 0 AS TOT_AMT , 0 AS TRMEND_AMT , 0 AS BFR_BASIS_AMT , SUM(DECODE(SUBSTRING(AR05.TRST_DT, 0, 6), TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(AR05.ETRDPS_AMT, 0))) AS CLOSE_YM1_CLMN_AMT , 0 AS CLOSE_YM1_TOT_AMT , 0 AS CLOSE_YM1 , 0 AS BASIS_CREDIT_AMT , SUM(DECODE(SUBSTRING(AR05.TRST_DT, 0, 6), TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(AR05.ETRDPS_AMT, 0))) AS CLOSE_YM2_CLMN_AMT , 0 AS CLOSE_YM2_TOT_AMT , 0 AS CLOSE_YM2 , SUM(NVL(AR05.ETRDPS_AMT, 0)) AS ETRDPS_AMT , SUM(DECODE(SUBSTRING(AR05.ETRDPS_DT, 0, 6), SUBSTRING(REPLACE(#{baseDt}, '-', ''),0,6) ,NVL(AR05.ETRDPS_AMT, 0))) AS CUR_CLMN_AMT , SUM(DECODE(NVL(AR05.TRST_DT, 0),0,0 ,NVL(AR05.ETRDPS_AMT, 0))) AS TOT_MATCH_AMT , SUM( CASE WHEN SUBSTR(AR05.TRST_DT,0,6) > SUBSTRING(REPLACE(#{baseDt}, '-', ''),0,6) THEN NVL(AR05.ETRDPS_AMT, 0) ELSE 0 END ) AS OVER_MON_MATCH_AMT , SUM( CASE WHEN SUBSTR(AR05.TRST_DT,0,6) < TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') THEN NVL(AR05.ETRDPS_AMT, 0) ELSE 0 END ) AS UNDER_MON_MATCH_AMT FROM TB_AR05D02 AR05 INNER JOIN TB_AR05M01 M01 ON M01.CO_CD = AR05.CO_CD AND M01.ETRDPS_SEQ = AR05.ETRDPS_SEQ LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR05.CLNT_CD WHERE #{selpchCd} = (SELECT NVL(CODE_RPRC,#{selpchCd}) FROM TB_CM05M01 WHERE CODE_ID= M01.ETRDPS_TYP)
AND NVL(AR05.ETRDPS_AMT,0) != 0
AND AR05.CO_CD = #{coCd}
AND AR05.CLNT_CD = #{clntCd}
AND AR05.ETRDPS_DT BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ||'01' AND REPLACE(#{baseDt}, '-', '')
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND AR05.PRDT_GRP = #{prdtGrp}
AND M01.TAXIVC_COPRT = #{taxivcCoprt}
GROUP BY AR05.CO_CD, SUBSTRING(AR05.TRST_DT, 0, 6), AR05.CLNT_CD, SUBSTRING(AR05.TRST_DT, 0, 4) || '-' || SUBSTRING(AR05.TRST_DT, 5,2) , BM02.CLNT_NM , CASE WHEN NVL(AR05.SELL_CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(AR05.SELL_CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(AR05.SELL_PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE AR05.PRDT_GRP END ,CASE WHEN NVL(AR05.SELL_CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(AR05.SELL_CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(AR05.SELL_PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE NVL(AR05.PRJCT_CD,0) END , BM02.SELL_PAY_DIV_CD , NVL(AR05.SELL_CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) , NVL(AR05.SELL_PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) , BM02.SELL_VAT_CD , M01.TAXIVC_COPRT , NVL(AR05.SELL_CLMN_DAY, BM02.SELL_CLMN_DAY) UNION ALL SELECT 2 AS TYPE1 , AR02.CO_CD , BM02.CLNT_NM , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE BM01.PRDT_GRP END AS PRDT_GRP ,CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE NVL(AR02.PRJCT_CD,0) END AS PRJCT_CD , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) AS SELL_CLMN_DAY , SUBSTRING(TRST_DT, 0, 6) TRST_YM , SUBSTRING(TRST_DT, 0, 4) || '-' || SUBSTRING(TRST_DT, 5,2) CLOSE_YM , TRST_CLNT_CD AS CLNT_CD , 0 AS BASIS_AMT , 0 AS CLMN_AMT , SUM( DECODE(SUBSTRING(AR02.TRST_DT,0,6 ),SUBSTRING(REPLACE(#{baseDt}, '-', ''),0,6), NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0),0)) AS TOT_AMT , 0 AS TRMEND_AMT , 0 AS BFR_BASIS_AMT , 0 AS CLOSE_YM1_CLMN_AMT , SUM( DECODE(SUBSTRING(AR02.TRST_DT,0,6 ),TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1),'YYYYMM'), NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0))) AS CLOSE_YM1_TOT_AMT , 0 AS CLOSE_YM1 , 0 AS BASIS_CREDIT_AMT , 0 AS CLOSE_YM2_CLMN_AMT , SUM( DECODE(SUBSTRING(AR02.TRST_DT,0,6 ),TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2),'YYYYMM'), NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0))) AS CLOSE_YM2_TOT_AMT , 0 AS CLOSE_YM2 , 0 AS ETRDPS_AMT , 0 AS CUR_AMT , 0 AS TOT_MATCH_AMT , 0 AS OVER_MON_MATCH_AMT , 0 AS UNDER_MON_MATCH_AMT FROM TB_AR02M01 AR02 INNER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = AR02.TRST_PRDT_CD LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR02.TRST_CLNT_CD LEFT JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = AR02.PRJCT_CD WHERE 1 = 1
AND SELPCH_CD = #{selpchCd}
AND AR02.CO_CD = #{coCd}
AND AR02.TRST_CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
