AND AR02.TAXIVC_COPRT = #{taxivcCoprt}
AND AR02.PRDT_DIV IN (SELECT DISTINCT PRDT_DIV FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} )
GROUP BY AR02.CO_CD, SUBSTRING(TRST_DT, 0, 6), TRST_CLNT_CD , SUBSTRING(TRST_DT, 0, 4) || '-' || SUBSTRING(TRST_DT, 5,2) , BM02.CLNT_NM , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE BM01.PRDT_GRP END , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE NVL(AR02.PRJCT_CD,0) END , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) , SELL_VAT_CD , TAXIVC_COPRT , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) UNION ALL SELECT 3 AS TYPE1 , C.CO_CD , BM02.CLNT_NM , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE C.PRDT_GRP END PRDT_GRP ,CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE NVL(C.PRJCT_CD,0) END AS PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , SUBSTRING(REPLACE(#{baseDt}, '-', ''),0,6) AS ETRDPS_YM , SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 4) || '-' || SUBSTRING(REPLACE(#{baseDt}, '-', ''), 5,2) CLOSE_YM , C.CLNT_CD , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) BASIS_AMT , 0 CLMN_AMT , 0 TOT_AMT , 0 TRMEND_AMT , 0 AS BFR_BASIS_AMT , 0 AS CLOSE_YM1_CLMN_AMT , 0 AS CLOSE_YM1_TOT_AMT , 0 AS CLOSE_YM1 , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.BASIS_CREDIT_AMT, 0))) BASIS_CREDIT_AMT , 0 AS CLOSE_YM2_CLMN_AMT , 0 AS CLOSE_YM2_TOT_AMT , 0 AS CLOSE_YM2 , 0 AS ETRDPS_AMT , 0 AS CUR_AMT , 0 AS TOT_MATCH_AMT , 0 AS OVER_MON_MATCH_AMT , 0 AS UNDER_MON_MATCH_AMT FROM TB_AR07M01 C LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = C.CLNT_CD LEFT JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = C.PRJCT_CD WHERE 1 = 1
AND SELPCH_CD = #{selpchCd}
AND C.CO_CD = #{coCd}
AND C.CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND C.CLOSE_YM BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') AND TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM')
AND TAXIVC_COPRT = #{taxivcCoprt}
AND PRDT_GRP = #{prdtGrp}
GROUP BY C.CO_CD , C.CLNT_CD , BM02.CLNT_NM , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE C.PRDT_GRP END ,CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE NVL(C.PRJCT_CD,0) END , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY UNION ALL SELECT 4 AS TYPE1 , AR02.CO_CD , BM02.CLNT_NM , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE BM01.PRDT_GRP END AS PRDT_GRP ,CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE NVL(AR02.PRJCT_CD,0) END AS PRJCT_CD , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) AS SELL_CLMN_DAY , TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -3), 'YYYYMM') AS TRST_YM , SUBSTRING(TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -3), 'YYYYMM'), 0, 4) || '-' || SUBSTRING(TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -3), 'YYYYMM'), 5,2) CLOSE_YM , TRST_CLNT_CD AS CLNT_CD , 0 AS BASIS_AMT , 0 AS CLMN_AMT , 0 AS TOT_AMT , 0 AS TRMEND_AMT , SUM(NVL(AR02.BILG_AMT,0) + NVL(AR02.BILG_VAT_AMT,0) -
( SELECt SUM(ETRDPS_AMT) FROM TB_AR05D02 AR05D02 WHERE AR02.TRST_CERTI_NO = AR05D02.TRST_CERTI_NO AND AR05D02.ETRDPS_DT <= REPLACE(#{baseDt},'-','')))
AS BFR_BASIS_AMT , 0 AS CLOSE_YM1_CLMN_AMT , 0 AS CLOSE_YM1_TOT_AMT , 0 AS CLOSE_YM1 , 0 AS BASIS_CREDIT_AMT , 0 AS CLOSE_YM2_CLMN_AMT , 0 AS CLOSE_YM2_TOT_AMT , 0 AS CLOSE_YM2 , 0 AS ETRDPS_AMT , 0 AS CUR_AMT , 0 AS TOT_MATCH_AMT , 0 AS OVER_MON_MATCH_AMT , 0 AS UNDER_MON_MATCH_AMT FROM TB_AR02M01 AR02 INNER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = AR02.TRST_PRDT_CD INNER JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR02.TRST_CLNT_CD LEFT JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = AR02.PRJCT_CD WHERE 1=1 AND AR02.TRST_DT < TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM')||'01'
AND (AR02.ETRDPS_YN = 'N' OR AR02.TRST_CERTI_NO IN (SELECT DISTINCT D02.TRST_CERTI_NO FROM TB_AR05D02 D02 WHERE D02.ETRDPS_DT > REPLACE(#{baseDt},'-','')))
AND SELPCH_CD = #{selpchCd}
AND AR02.CO_CD = #{coCd}
AND AR02.TRST_CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND AR02.TAXIVC_COPRT = #{taxivcCoprt}
AND AR02.PRDT_DIV IN (SELECT DISTINCT PRDT_DIV FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} )
GROUP BY AR02.CO_CD , TRST_CLNT_CD , BM02.CLNT_NM , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE BM01.PRDT_GRP END , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE NVL(AR02.PRJCT_CD,0) END , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) , SELL_VAT_CD , TAXIVC_COPRT , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) ) GROUP BY CO_CD , CLNT_NM
, SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD -- , TAXIVC_COPRT , DECODE(SELL_CLMN_DAY,0,'',SELL_CLMN_DAY) , CLNT_CD
) A ) T

SELECT COUNT(*) FROM (SELECT ROWNUM AS RNUM , A.*
FROM ( SELECT CO_CD , CLNT_NM , PRDT_GRP , PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , ETRDPS_YM , CLOSE_YM , CLNT_CD
, SUM(CLMN_AMT) AS CLMN_AMT , SUM(TOT_AMT) AS TOT_AMT
, SUM(BFR_BASIS_AMT) AS BFR_BASIS_AMT , SUM(CLOSE_YM1_CLMN_AMT) AS CLOSE_YM1_CLMN_AMT , SUM(CLOSE_YM1_TOT_AMT) AS CLOSE_YM1_TOT_AMT
, SUM(BASIS_CREDIT_AMT) AS BASIS_CREDIT_AMT , SUM(CLOSE_YM2_CLMN_AMT) AS CLOSE_YM2_CLMN_AMT , SUM(CLOSE_YM2_TOT_AMT) AS CLOSE_YM2_TOT_AMT , SUM(CLOSE_YM2) AS CLOSE_YM2 FROM ( SELECT 1 AS TYPE1 , AR05.CO_CD , BM02.CLNT_NM , AR05.PRDT_GRP , NVL(AR05.PRJCT_CD,0) AS PRJCT_CD , BM02.SELL_PAY_DIV_CD , NVL(AR05.SELL_CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(AR05.SELL_PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , BM02.SELL_VAT_CD , M01.TAXIVC_COPRT AS TAXIVC_COPRT , NVL(AR05.SELL_CLMN_DAY, BM02.SELL_CLMN_DAY) AS SELL_CLMN_DAY
, SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 6) AS ETRDPS_YM , SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 4) || '-' || SUBSTRING(REPLACE(#{baseDt}, '-', ''), 5,2) CLOSE_YM , AR05.CLNT_CD , 0 AS BASIS_AMT , SUM(DECODE(SUBSTRING(AR05.ETRDPS_DT, 0, 6),SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 6), AR05.ETRDPS_AMT,0)) CLMN_AMT , 0 AS TOT_AMT , 0 AS TRMEND_AMT , 0 AS BFR_BASIS_AMT , SUM(DECODE(SUBSTRING(AR05.ETRDPS_DT, 0, 6),TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1),'YYYYMM'), AR05.ETRDPS_AMT,0)) AS CLOSE_YM1_CLMN_AMT , 0 AS CLOSE_YM1_TOT_AMT , 0 AS CLOSE_YM1 , 0 AS BASIS_CREDIT_AMT , 0 AS CLOSE_YM2_CLMN_AMT , 0 AS CLOSE_YM2_TOT_AMT , 0 AS CLOSE_YM2 FROM TB_AR05D02 AR05 INNER JOIN TB_AR05M01 M01 ON M01.CO_CD = AR05.CO_CD AND M01.ETRDPS_SEQ = AR05.ETRDPS_SEQ LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR05.CLNT_CD WHERE #{selpchCd} = (SELECT NVL(CODE_RPRC,#{selpchCd}) FROM TB_CM05M01 WHERE CODE_ID= M01.ETRDPS_TYP)
