AND AR05.CO_CD = #{coCd}
AND AR05.CLNT_CD = #{clntCd}
AND AR05.ETRDPS_DT BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -1), 'YYYYMM')||'01' AND REPLACE(#{baseDt}, '-', '')
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND AR05.PRDT_GRP = #{prdtGrp}
AND M01.TAXIVC_COPRT = #{taxivcCoprt}
