) A )

SELECT * FROM (SELECT ROWNUM AS RNUM , A.* , (SELECT CRN FROM TB_BM02M01 BM02M WHERE BM02M.CLNT_CD = A.CLNT_CD) CRN_NO , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM , FN_CM05M01_CD_TO_NM(A.PRDT_GRP) AS PRDT_GRP_NM , (SELECT PRJCT_NM FROM TB_SD05M01 SD05 WHERE SD05.PRJCT_CD = A.PRJCT_CD ) AS PRJCT_NM , FN_CM05M01_CD_TO_NM(A.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM , FN_CM05M01_CD_TO_NM(A.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM , FN_CM05M01_CD_TO_NM(A.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_NM , FN_CM05M01_CD_TO_NM(A.SELL_VAT_CD) AS SELL_VAT_NM , A.TAXIVC_COPRT , FN_CM05M01_CD_TO_NM(A.TAXIVC_COPRT) AS TAXIVC_COPRT_NM , BFR_BASIS_AMT + CLOSE_YM1_TOT_AMT - CLOSE_YM1_CLMN_AMT + TOT_AMT - CLMN_AMT AS TRMEND_AMT , BFR_BASIS_AMT + CLOSE_YM1_TOT_AMT - CLOSE_YM1_CLMN_AMT AS CLOSE_YM1 , F_CREDIT_LOAN_PRDTGRP('T', A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-',''),0) AS CRDT_LMT , F_CREDIT_PRDTGRP_NON_RECV_AMT(A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS NON_RECV_AMT , F_CREDIT_PRDTGRP_UNSETL_BIL_AMT(A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS UNSETL_BIL_AMT , F_CREDIT_PRDTGRP_NON_PAY_AMT(A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-','')) AS NON_PAY_AMT , F_CREDIT_LOAN_PRDTGRP('C', A.CLNT_CD, A.CO_CD,A.PRDT_GRP, REPLACE(#{baseDt},'-',''),0) AS CRDT_LOAN
FROM ( SELECT CO_CD , CLNT_NM , PRDT_GRP , PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , ETRDPS_YM , CLOSE_YM , CLNT_CD
