AND AR02.PRDT_DIV IN (SELECT DISTINCT PRDT_DIV FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} )
GROUP BY AR02.CO_CD, TRST_CLNT_CD , BM02.CLNT_NM , AR02.TRST_PRDT_CD , AR02.PRJCT_CD , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) , SELL_VAT_CD , TAXIVC_COPRT , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) UNION ALL SELECT 3 AS TYPE1 , C.CO_CD , BM02.CLNT_NM , C.PRDT_GRP , C.PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 6) AS ETRDPS_YM , SUBSTRING(REPLACE(#{baseDt}, '-', ''), 0, 4) || '-' || SUBSTRING(REPLACE(#{baseDt}, '-', ''), 5,2) CLOSE_YM , C.CLNT_CD , 0 BASIS_AMT , 0 CLMN_AMT , 0 TOT_AMT , 0 TRMEND_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) AS BFR_BASIS_AMT , 0 CLOSE_YM1_CLMN_AMT , 0 CLOSE_YM1_TOT_AMT , 0 CLOSE_YM1 , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.BASIS_CREDIT_AMT, 0))) BASIS_CREDIT_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.CELL_CLMN_AMT, 0))) CLOSE_YM2_CLMN_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.CELL_TOT_AMT, 0))) CLOSE_YM2_TOT_AMT , SUM(DECODE(C.CLOSE_YM, TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt}, '-', ''), 'YYYYMMDD'), -2), 'YYYYMM') ,NVL(C.TRMEND_CREDIT_AMT, 0))) CLOSE_YM2 FROM TB_AR07M01 C LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = C.CLNT_CD WHERE 1 = 1
