GROUP BY C.CO_CD , C.CLNT_CD , BM02.CLNT_NM , C.PRDT_GRP , C.PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY ) GROUP BY CO_CD , CLNT_NM , PRDT_GRP , PRJCT_CD , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , TAXIVC_COPRT , SELL_CLMN_DAY , ETRDPS_YM , CLOSE_YM , CLNT_CD
) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT AR07.*, (SELECT NAME FROM TB_CM06M01 WHERE ID = AR07.SALES_EMP_ID ) AS SALES_EMP_NM , (SELECT PRJCT_NM FROM TB_SD05M01 SD05 WHERE SD05.PRJCT_CD = AR07.PRJCT_CD ) AS PRJCT_NM , FN_CM05M01_CD_TO_NM(AR07.PRDT_GRP) AS PRDT_GRP_NM ,F_CREDIT_LOAN2('T',AR07.CLNT_CD,AR07.CO_CD,AR07.PRDT_GRP,TO_CHAR(LAST_DAY(TO_DATE(REPLACE(#{baseDt},'-',''), 'YYYYMMDD')), 'YYYYMMDD'),0) AS CRDT_LMT ,F_CREDIT_LOAN2('C',AR07.CLNT_CD,AR07.CO_CD,AR07.PRDT_GRP,TO_CHAR(LAST_DAY(TO_DATE(REPLACE(#{baseDt},'-',''), 'YYYYMMDD')), 'YYYYMMDD'),0) AS CRDT_LOAN ,NVL((SELECT SUM(ETRDPS_AMT) FROM TB_AR05M01 AR05 INNER JOIN TB_AR05D01 AR05D ON AR05D.CO_CD = AR05.CO_CD AND AR05D.CLNT_CD = AR05.CLNT_CD AND AR05D.BIL_NO = AR05.BIL_NO WHERE AR05.ETRDPS_DT <= TO_CHAR(LAST_DAY(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD')),'YYYYMMDD') AND AR05D.EXPRTN_DT > TO_CHAR(LAST_DAY(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD')),'YYYYMMDD') AND AR05.CO_CD = AR07.CO_CD AND AR05.CLNT_CD = AR07.CLNT_CD),0) AS UNSETL_BIL_AMT ,(SELECT SUM(NVL(PLDG_AMT,0) /100 * NVL(PLDG_RCOGN_RATE,1)) FROM TB_BM02D02 BM02D02 WHERE BM02D02.CLNT_CD = AR07.CLNT_CD AND BM02D02.CO_CD = AR07.CO_CD AND BM02D02.USE_YN = 'Y' AND NVL(EXPRTN_DT,TO_CHAR(LAST_DAY(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD')),'YYYYMMDD')) <= TO_CHAR(LAST_DAY(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD')),'YYYYMMDD')) AS PLDG_AMT ,(SELECT CRN FROM TB_BM02M01 BM02M WHERE BM02M.CLNT_CD = AR07.CLNT_CD) CRN_NO FROM (
SELECT A.CO_CD , FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM , A.CLNT_CD , A.CLNT_NM , MIN(A.PRDT_GRP) AS PRDT_GRP , MIN(A.PRJCT_CD) AS PRJCT_CD , DECODE(A.SELL_CLMN_DAY,0,'',A.SELL_CLMN_DAY) AS SELL_CLMN_DAY , A.SELL_CLMN_RMK , A.SELL_PAY_DIV_CD , FN_CM05M01_CD_TO_NM(A.SELL_PAY_DIV_CD) AS SELL_PAY_DIV_NM , A.SELL_CLMN_DIV_CD , FN_CM05M01_CD_TO_NM(A.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_NM , A.PMNT_MTD_CD , FN_CM05M01_CD_TO_NM(A.PMNT_MTD_CD) AS SELL_PMNT_MTD_NM , A.SELL_VAT_CD , FN_CM05M01_CD_TO_NM(A.SELL_VAT_CD) AS SELL_VAT_NM , (SELECT SALES_EMP_ID FROM TB_BM02D01 BM02D01 WHERE BM02D01.CLNT_CD = A.CLNT_CD AND BM02D01.SALES_CO_CD = A.CO_CD AND BM02D01.USE_YN = 'Y' AND ROWNUM = 1 ) SALES_EMP_ID , PRE3_PCHS_CLMN_DAY , PRE2_PCHS_CLMN_DAY , PRE1_PCHS_CLMN_DAY , PRE0_PCHS_CLMN_DAY , SUM(PRE3_TRMEND_AMT) AS PRE3_TRMEND_AMT , SUM(PRE2_CELL_CLMN_AMT) AS PRE2_CELL_CLMN_AMT , SUM(PRE2_CELL_TOT_AMT) AS PRE2_CELL_TOT_AMT , SUM(PRE2_TRMEND_AMT) AS PRE2_TRMEND_AMT , SUM(PRE1_CELL_CLMN_AMT) AS PRE1_CELL_CLMN_AMT , SUM(PRE1_CELL_TOT_AMT) AS PRE1_CELL_TOT_AMT , SUM(PRE1_TRMEND_AMT) AS PRE1_TRMEND_AMT , SUM(CUR_CELL_TOT_AMT) AS CUR_CELL_TOT_AMT , SUM(CUR_CELL_CLMN_AMT) AS CUR_CELL_CLMN_AMT , SUM(CUR_TRMEND_CREDIT_AMT) AS CUR_TRMEND_CREDIT_AMT
FROM ( SELECT 0 AS TYPE1 ,AR07.CO_CD ,AR07.CLNT_CD ,BM02.CLNT_NM ,CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE AR07.PRDT_GRP END AS PRDT_GRP ,CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE AR07.PRJCT_CD END AS PRJCT_CD ,NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) AS SELL_CLMN_DAY ,BM02.SELL_CLMN_RMK ,BM02.SELL_PAY_DIV_CD ,NVL(SD05.CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD ,NVL(SD05.PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) AS PMNT_MTD_CD ,BM02.SELL_VAT_CD ,CASE WHEN CM05M01.CODE_RPRC = 'MON' THEN TO_CHAR(LAST_DAY(ADD_MONTHS(TRUNC(ADD_MONTHS(TO_DATE(CLOSE_YM,'YYYYMM'),-1),'MM'), NVL(CM05M01.CODE_ETC,0)) -1) + NVL(SD05.CLMN_DAY, NVL(BM02.SELL_CLMN_DAY,0)), 'YYYYMMDD') WHEN CM05M01.CODE_RPRC = 'DAY' THEN TO_CHAR(TO_DATE(CLOSE_YM,'YYYYMM') + NVL(SD05.CLMN_DAY, NVL(BM02.SELL_CLMN_DAY,0)), 'YYYYMMDD') END AS PRE3_PCHS_CLMN_DAY ,'' AS PRE2_PCHS_CLMN_DAY ,'' AS PRE1_PCHS_CLMN_DAY ,'' AS PRE0_PCHS_CLMN_DAY ,SUM(AR07.BASIS_CREDIT_AMT) AS PRE3_TRMEND_AMT ,0 AS PRE2_CELL_CLMN_AMT ,0 AS PRE2_CELL_TOT_AMT ,0 AS PRE2_TRMEND_AMT ,0 AS PRE1_CELL_CLMN_AMT ,0 AS PRE1_CELL_TOT_AMT ,0 AS PRE1_TRMEND_AMT ,0 AS CUR_CELL_CLMN_AMT ,0 AS CUR_CELL_TOT_AMT ,0 AS CUR_TRMEND_CREDIT_AMT FROM TB_AR07M01 AR07 INNER JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR07.CLNT_CD LEFT JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = AR07.PRJCT_CD LEFT JOIN TB_CM05M01 CM05M01 ON NVL(SD05.CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) = CM05M01.CODE_ID WHERE CLOSE_YM BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-2),'YYYYMM') AND SUBSTRING(REPL