AND AR07.CO_CD = #{coCd}
AND AR07.SELPCH_CD = 'SELPCH2'
AND BM02.CLNT_NM like '%'||#{clntNm} ||'%'
AND AR07.CLNT_CD = #{clntCd}
AND AR07.PRDT_GRP = #{prdtGrp}
AND AR07.TAXIVC_COPRT = #{taxivcCoprt}
GROUP BY AR07.CO_CD ,AR07.CLNT_CD ,BM02.CLNT_NM ,CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE AR07.PRDT_GRP END ,CASE WHEN NVL(SD05.CLMN_DAY,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD,'@') THEN 0 ELSE AR07.PRJCT_CD END ,BM02.SELL_VAT_CD ,NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) ,BM02.SELL_CLMN_RMK ,BM02.SELL_PAY_DIV_CD ,NVL(SD05.CLMN_DIV_CD,BM02.SELL_CLMN_DIV_CD) ,NVL(SD05.PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) ,CASE WHEN CM05M01.CODE_RPRC = 'MON' THEN TO_CHAR(LAST_DAY(ADD_MONTHS(TRUNC(ADD_MONTHS(TO_DATE(CLOSE_YM,'YYYYMM'),-1),'MM'), NVL(CM05M01.CODE_ETC,0)) -1) + NVL(SD05.CLMN_DAY, NVL(BM02.SELL_CLMN_DAY,0)), 'YYYYMMDD') WHEN CM05M01.CODE_RPRC = 'DAY' THEN TO_CHAR(TO_DATE(CLOSE_YM,'YYYYMM') + NVL(SD05.CLMN_DAY, NVL(BM02.SELL_CLMN_DAY,0)), 'YYYYMMDD') END UNION ALL SELECT 1 AS TYPE1 , AR05.CO_CD , AR05.CLNT_CD , BM02.CLNT_NM , CASE WHEN NVL(AR05.SELL_CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(AR05.SELL_CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(AR05.SELL_PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE AR05.PRDT_GRP END AS PRDT_GRP , CASE WHEN NVL(AR05.SELL_CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(AR05.SELL_CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(AR05.SELL_PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE AR05.PRJCT_CD END AS PRJCT_CD , NVL(AR05.SELL_CLMN_DAY, BM02.SELL_CLMN_DAY) AS SELL_CLMN_DAY , BM02.SELL_CLMN_RMK , BM02.SELL_PAY_DIV_CD , NVL(AR05.SELL_CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(AR05.SELL_PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , BM02.SELL_VAT_CD ,'' AS PRE3_PCHS_CLMN_DAY ,'' AS PRE2_PCHS_CLMN_DAY ,'' AS PRE1_PCHS_CLMN_DAY ,'' AS PRE0_PCHS_CLMN_DAY , 0 AS PRE3_TRMEND_AMT , SUM(DECODE(SUBSTRING(AR05.ETRDPS_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-2),'YYYYMM'), AR05.ETRDPS_AMT,0)) AS PRE2_CELL_CLMN_AMT , 0 AS PRE2_CELL_TOT_AMT , 0 AS PRE2_TRMEND_AMT , SUM(DECODE(SUBSTRING(AR05.ETRDPS_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-1),'YYYYMM'), AR05.ETRDPS_AMT,0)) AS PRE1_CELL_CLMN_AMT , 0 AS PRE1_CELL_TOT_AMT , 0 AS PRE1_TRMEND_AMT , SUM(DECODE(SUBSTRING(AR05.ETRDPS_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),0),'YYYYMM'), AR05.ETRDPS_AMT,0)) AS CUR_CELL_CLMN_AMT , 0 AS CUR_CELL_TOT_AMT , 0 AS CUR_TRMEND_CREDIT_AMT FROM TB_AR05D02 AR05 INNER JOIN TB_AR05M01 M01 ON M01.CO_CD = AR05.CO_CD AND M01.ETRDPS_SEQ = AR05.ETRDPS_SEQ LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR05.CLNT_CD WHERE 'SELPCH2' = (SELECT NVL(CODE_RPRC,'SELPCH2') FROM TB_CM05M01 WHERE CODE_ID= M01.ETRDPS_TYP) AND AR05.ETRDPS_DT BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-2),'YYYYMM') || '01' AND REPLACE(#{baseDt}, '-', '')
AND AR05.CO_CD = #{coCd}
AND NVL(AR05.ETRDPS_AMT,0) != 0
AND AR05.CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND AR05.PRDT_GRP = #{prdtGrp}
AND M01.TAXIVC_COPRT = #{taxivcCoprt}
GROUP BY AR05.CO_CD , AR05.CLNT_CD , BM02.CLNT_NM , CASE WHEN NVL(AR05.SELL_CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(AR05.SELL_CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(AR05.SELL_PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE AR05.PRDT_GRP END , CASE WHEN NVL(AR05.SELL_CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(AR05.SELL_CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(AR05.SELL_PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE AR05.PRJCT_CD END , NVL(AR05.SELL_CLMN_DAY, BM02.SELL_CLMN_DAY) , BM02.SELL_CLMN_RMK , BM02.SELL_PAY_DIV_CD , NVL(AR05.SELL_CLMN_DIV_CD, BM02.SELL_CLMN_DIV_CD) , NVL(AR05.SELL_PMNT_MTD_CD, BM02.SELL_PMNT_MTD_CD) , BM02.SELL_VAT_CD UNION ALL SELECT 2 AS TYPE1 , CO_CD , CLNT_CD , CLNT_NM , PRDT_GRP , PRJCT_CD , SELL_CLMN_DAY , SELL_CLMN_RMK , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , '' AS PRE3_PCHS_CLMN_DAY , DECODE(SUBSTRING(TRST_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-2),'YYYYMM') ,PCHS_CLMN_DAY) AS PRE2_PCHS_CLMN_DAY , DECODE(SUBSTRING(TRST_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-1),'YYYYMM') ,PCHS_CLMN_DAY) AS PRE1_PCHS_CLMN_DAY , DECODE(SUBSTRING(TRST_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'), 0),'YYYYMM') ,PCHS_CLMN_DAY) AS PRE0_PCHS_CLMN_DAY , 0 AS PRE3_TRMEND_AMT , 0 AS PRE2_CELL_CLMN_AMT , SUM(PRE2_CELL_TOT_AMT) AS PRE2_CELL_TOT_AMT , 0 AS PRE2_TRMEND_AMT , 0 AS PRE1_CELL_CLMN_AMT , SUM(PRE1_CELL_TOT_AMT) AS PRE1_CELL_TOT_AMT , 0 AS PRE1_TRMEND_AMT , 0 AS CUR_CELL_CLMN_AMT , SUM(CUR_CELL_TOT_AMT) AS CUR_CELL_TOT_AMT , 0 AS CUR_TRMEND_CREDIT_AMT FROM ( SELECT AR02.CO_CD , AR02.TRST_DT , TRST_CLNT_CD AS CLNT_CD , BM02.CLNT_NM , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN '' ELSE BM01.PRDT_GRP END AS PRDT_GRP , CASE WHEN NVL(SD05.CLMN_DAY ,NVL(BM02.SELL_CLMN_DAY,0)) = NVL(BM02.SELL_CLMN_DAY,0) AND NVL(SD05.CLMN_DIV_CD,NVL(BM02.SELL_CLMN_DIV_CD ,'@')) = NVL(BM02.SELL_CLMN_DIV_CD ,'@') AND NVL(SD05.PMNT_MTD_CD,NVL(BM02.SELL_PMNT_MTD_CD ,'@')) = NVL(BM02.SELL_PMNT_MTD_CD ,'@') THEN 0 ELSE AR02.PRJCT_CD END AS PRJCT_CD , NVL(SD05.CLMN_DAY, SELL_CLMN_DAY) AS SELL_CLMN_DAY , BM02.SELL_CLMN_RMK , SELL_PAY_DIV_CD , NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) AS SELL_CLMN_DIV_CD , NVL(SD05.PMNT_MTD_CD, SELL_PMNT_MTD_CD) AS SELL_PMNT_MTD_CD , SELL_VAT_CD ,CASE WHEN CM05M01.CODE_RPRC = 'MON' THEN TO_CHAR(LAST_DAY(ADD_MONTHS(TRUNC(TO_DATE(TRST_DT,'YYYYMMDD'),'MM'), NVL(CM05M01.CODE_ETC,0)) -1) + NVL(NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) ,0), 'YYYYMMDD') WHEN CM05M01.CODE_RPRC = 'DAY' THEN TO_CHAR(TO_DATE(TRST_DT,'YYYYMMDD')+ NVL(NVL(SD05.CLMN_DAY, BM02.SELL_CLMN_DAY) ,0), 'YYYYMMDD') END AS PCHS_CLMN_DAY , DECODE(SUBSTRING(TRST_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-2),'YYYYMM'),NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0),0) AS PRE2_CELL_TOT_AMT , DECODE(SUBSTRING(TRST_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-1),'YYYYMM'),NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0),0) AS PRE1_CELL_TOT_AMT , DECODE(SUBSTRING(TRST_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'), 0),'YYYYMM'),NVL(BILG_AMT, 0 ) + NVL(BILG_VAT_AMT, 0),0) AS CUR_CELL_TOT_AMT FROM TB_AR02M01 AR02 INNER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = AR02.TRST_PRDT_CD LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = AR02.TRST_CLNT_CD LEFT JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = AR02.PRJCT_CD LEFT JOIN TB_CM05M01 CM05M01 ON NVL(SD05.CLMN_DIV_CD, SELL_CLMN_DIV_CD) = CM05M01.CODE_ID WHERE SELPCH_CD = 'SELPCH2'
AND AR02.CO_CD = #{coCd}
AND AR02.TRST_CLNT_CD = #{clntCd}
AND BM02.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND TRST_DT BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-2),'YYYYMM') || '01' AND REPLACE(#{baseDt}, '-', '')
AND AR02.TAXIVC_COPRT = #{taxivcCoprt}
AND AR02.PRDT_DIV IN (SELECT DISTINCT PRDT_DIV FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} )
) AR02 GROUP BY CO_CD , CLNT_CD , CLNT_NM , PRDT_GRP , PRJCT_CD , SELL_CLMN_DAY , SELL_CLMN_RMK , SELL_PAY_DIV_CD , SELL_CLMN_DIV_CD , SELL_PMNT_MTD_CD , SELL_VAT_CD , DECODE(SUBSTRING(TRST_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-2),'YYYYMM') ,PCHS_CLMN_DAY) , DECODE(SUBSTRING(TRST_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'),-1),'YYYYMM') ,PCHS_CLMN_DAY) , DECODE(SUBSTRING(TRST_DT,0,6) ,TO_CHAR(ADD_MONTHS(TO_DATE(REPLACE(#{baseDt},'-',''),'YYYYMMDD'), 0),'YYYYMM') ,PCHS_CLMN_DAY)
) A GROUP BY A.CO_CD , A.CLNT_CD , A.CLNT_NM , PRDT_GRP , PRJCT_CD , DECODE(A.SELL_CLMN_DAY,0,'',A.SELL_CLMN_DAY) , A.SELL_CLMN_RMK , A.SELL_PAY_DIV_CD , A.SELL_CLMN_DIV_CD , A.PMNT_MTD_CD , A.SELL_VAT_CD , PRE3_PCHS_CLMN_DAY , PRE2_PCHS_CLMN_DAY , PRE1_PCHS_CLMN_DAY , PRE0_PCHS_CLMN_DAY ) AS AR07 ORDER BY CO_CD , CLNT_CD , PRDT_GRP , PRJCT_CD , SELL_PAY_DIV_NM , SELL_CLMN_DIV_NM , SELL_PMNT_MTD_NM , SELL_CLMN_DAY
