mapper.select:
SELECT ROWNUM AS RNUM, B.*
,FN_CM05M01_CD_TO_NM(B.TAXIVC_COPRT) AS TAXIVC_COPRT_NM
FROM ( SELECT * FROM ( SELECT 1 AS TYPE , ROWNUM AS RN
, A.TAXIVC_COPRT
, TO_CHAR(TO_DATE(A.TRST_DT, 'YYYYMMDD'), 'YYYY-MM') AS TRST_YM , TO_CHAR(TO_DATE(A.TRST_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS TRST_DT , A.CO_CD , A.TRST_CLNT_CD AS CLNT_CD , B.CLNT_NM , DECODE(A.SELPCH_CD, 'SELPCH1', '외상매입금', 'SELPCH2', '외상매출금','SELPCH3', '외상매입금','SELPCH4', '외상매출금') AS GUBUN_NM , A.SELPCH_CD AS GUBUN_CD , DECODE(A.SELPCH_CD, 'SELPCH1' , (SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = A.TRST_PRDT_CD ) || ' ' || FN_CM05M01_CD_TO_NM(B.PCHS_PMNT_MTD_CD)|| DECODE(B.PCHS_CLMN_DIV_CD, NULL, NULL, ' [' || FN_CM05M01_CD_TO_NM(B.PCHS_CLMN_DIV_CD)|| ']') || DECODE(B.PCHS_CLMN_DAY, NULL, NULL, ' 결제일: ' || B.PCHS_CLMN_DAY) , 'SELPCH2' , DECODE(A.PRJCT_CD,0,(SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = A.TRST_PRDT_CD ) || ' ' || FN_CM05M01_CD_TO_NM(B.SELL_PMNT_MTD_CD)|| DECODE(B.SELL_CLMN_DIV_CD, NULL, NULL, ' [' || FN_CM05M01_CD_TO_NM(B.SELL_CLMN_DIV_CD)|| ']') || DECODE(B.SELL_CLMN_DAY, NULL, NULL, ' 결제일: ' || B.SELL_CLMN_DAY) ,(SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = A.TRST_PRDT_CD ) || ' ' || FN_CM05M01_CD_TO_NM(B.SELL_PMNT_MTD_CD)|| DECODE(SD05.CLMN_DIV_CD, NULL, NULL, ' [' || FN_CM05M01_CD_TO_NM(SD05.CLMN_DIV_CD)|| ']') || DECODE(SD05.CLMN_DAY, NULL, NULL, ' 결제일: ' || SD05.CLMN_DAY))) || A.TRSP_RMK AS BIGO , DECODE(A.SELPCH_CD, 'SELPCH1', NVL(A.BILG_AMT,0) + NVL(A.BILG_VAT_AMT,0), 'SELPCH3', NVL(A.BILG_AMT,0) + NVL(A.BILG_VAT_AMT,0), 0) AS PCHS_AMT , DECODE(A.SELPCH_CD, 'SELPCH2', NVL(A.BILG_AMT,0) + NVL(A.BILG_VAT_AMT,0), 'SELPCH4', NVL(A.BILG_AMT,0) + NVL(A.BILG_VAT_AMT,0), 0) AS SELL_AMT , 0 AS BASIS_AMT , '미확정' AS RMK FROM TB_AR02M01 A LEFT JOIN TB_BM02M01 B ON A.TRST_CLNT_CD = B.CLNT_CD LEFT JOIN TB_SD05M01 SD05 ON SD05.PRJCT_CD = A.PRJCT_CD WHERE REPLACE(A.TRST_DT, '-', '') BETWEEN REPLACE(#{strtDt}, '-', '') AND REPLACE(#{endDt}, '-', '') AND BILG_CERT_NO IS NULL
AND 'N' = #{bilgYn}
AND A.TRST_CLNT_CD = #{clntCd}
AND A.CO_CD = #{coCd}
AND B.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND A.TAXIVC_COPRT = #{taxivcCoprt}
AND ( A.SELPCH_CD = #{selpchCd} OR A.SELPCH_CD ='SELPCH3')
AND ( A.SELPCH_CD = #{selpchCd} OR A.SELPCH_CD ='SELPCH4')
AND PRDT_DIV IN (SELECT DISTINCT PRDT_DIV FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} --제품 그룹을 통해 제품 구분 조회 )
UNION ALL SELECT 2 AS TYPE , ROWNUM AS RN
, #{taxivcCoprt}
,TO_CHAR(TO_DATE(AR04.SELL_DT, 'YYYYMMDD'), 'YYYY-MM') AS TRST_YM ,TO_CHAR(TO_DATE(AR04.SELL_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS TRST_DT , AR04.CO_CD , AR04.CLNT_CD , AR04.CLNT_NM , DECODE(AR04.SELPCH_CD, 'SELPCH1', '외상매입금', 'SELPCH2', '외상매출금','SELPCH3', '외상매입금','SELPCH4', '외상매출금') AS GUBUN_NM , AR04.SELPCH_CD AS GUBUN_CD , (SELECT ARD_5006 FROM TB_AR04D01 D WHERE D.BILG_CERT_NO = AR04.BILG_CERT_NO AND ARD_5006 IS NOT NULL AND BILG_CERT_NO_SEQ = 1) || ' '|| FTXAC1_1 AS BIGO , DECODE(AR04.SELPCH_CD, 'SELPCH1', NVL(AR04.TAX_MOA5_23,0) + NVL(AR04.TAX_MOA5_124,0), 'SELPCH3', NVL(AR04.TAX_MOA5_23,0) + NVL(AR04.TAX_MOA5_124,0), 0) AS PCHS_AMT , DECODE(AR04.SELPCH_CD, 'SELPCH2', NVL(AR04.TAX_MOA5_23,0) + NVL(AR04.TAX_MOA5_124,0), 'SELPCH4', NVL(AR04.TAX_MOA5_23,0) + NVL(AR04.TAX_MOA5_124,0), 0) AS SELL_AMT , 0 AS BASIS_AMT , NVL2(TAX_BILG_NO,'완료','계산서미처리') AS RMK FROM TB_AR04M01 AR04 WHERE AR04.SELL_DT BETWEEN REPLACE(#{strtDt}, '-', '') AND REPLACE(#{endDt}, '-', '')
AND AR04.CLNT_CD = #{clntCd}
AND AR04.CO_CD = #{coCd}
AND AR04.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND ( AR04.SELPCH_CD = #{selpchCd} OR AR04.SELPCH_CD ='SELPCH3')
AND ( AR04.SELPCH_CD = #{selpchCd} OR AR04.SELPCH_CD ='SELPCH4')
AND AR04.BILG_CERT_NO IN (SELECT AR02.BILG_CERT_NO FROM TB_AR02M01 AR02 WHERE AR02.PRDT_DIV IN (SELECT DISTINCT PRDT_DIV FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} --제품 그룹을 통해 제품 구분 조회 ) )
UNION ALL SELECT 3 AS TYPE , ROWNUM AS RN
, A.TAXIVC_COPRT
, TO_CHAR(TO_DATE(A.ETRDPS_DT, 'YYYYMMDD'), 'YYYY-MM') AS TRST_YM , TO_CHAR(TO_DATE(A.ETRDPS_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS TRST_DT , A.CO_CD , A.CLNT_CD , B.CLNT_NM , FN_CM05M01_CD_TO_NM(A.ETRDPS_TYP) ||':'|| FN_CM05M01_CD_TO_NM(A.SETLE_TYP_CD) AS GUBUN_NM , A.ETRDPS_TYP AS GUBUN_CD , A.SUMRY AS BIGO , DECODE(NVL(CM05.CODE_RPRC,#{selpchCd}),'SELPCH1', 0,NVL(A.ETRDPS_AMT,0)) AS PCHS_AMT , DECODE(NVL(CM05.CODE_RPRC,#{selpchCd}),'SELPCH1', NVL(A.ETRDPS_AMT,0),0) AS SELL_AMT
, 0 , '' AS rmk FROM TB_AR05M01 A LEFT JOIN TB_BM02M01 B ON A.CLNT_CD = B.CLNT_CD LEFT JOIN TB_CM05M01 CM05 ON CM05.CODE_ID = A.ETRDPS_TYP WHERE A.ETRDPS_DT BETWEEN REPLACE(#{strtDt}, '-', '') AND REPLACE(#{endDt}, '-', '')
AND A.CLNT_CD = #{clntCd}
AND A.CO_CD = #{coCd}
AND B.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND A.TAXIVC_COPRT = #{taxivcCoprt}
AND #{selpchCd} = 'SELPCH1' AND (CM05.CODE_RPRC = #{selpchCd} OR CM05.CODE_RPRC IS NULL )
AND A.PRDT_GRP = #{prdtGrp}
UNION ALL SELECT 4 AS TYPE , ROWNUM AS RN
, A.TAXIVC_COPRT
, TO_CHAR(TO_DATE(A.ETRDPS_DT, 'YYYYMMDD'), 'YYYY-MM') AS TRST_YM , TO_CHAR(TO_DATE(A.ETRDPS_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS TRST_DT , A.CO_CD , A.CLNT_CD , B.CLNT_NM , FN_CM05M01_CD_TO_NM(A.ETRDPS_TYP) ||':'|| FN_CM05M01_CD_TO_NM(A.SETLE_TYP_CD) AS GUBUN_NM , A.ETRDPS_TYP AS GUBUN_CD , A.SUMRY AS BIGO , DECODE(NVL(CM05.CODE_RPRC,#{selpchCd}),'SELPCH2', NVL(A.ETRDPS_AMT,0),0) AS PCHS_AMT , DECODE(NVL(CM05.CODE_RPRC,#{selpchCd}),'SELPCH2', 0,NVL(A.ETRDPS_AMT,0)) AS SELL_AMT
, 0 , '' AS rmk FROM TB_AR05M01 A LEFT JOIN TB_BM02M01 B ON A.CLNT_CD = B.CLNT_CD LEFT JOIN TB_CM05M01 CM05 ON CM05.CODE_ID = A.ETRDPS_TYP WHERE A.ETRDPS_DT BETWEEN REPLACE(#{strtDt}, '-', '') AND REPLACE(#{endDt}, '-', '')
AND A.CLNT_CD = #{clntCd}
AND A.CO_CD = #{coCd}
AND B.CLNT_NM LIKE '%' || #{clntNm} ||'%'
AND A.TAXIVC_COPRT = #{taxivcCoprt}
AND #{selpchCd} = 'SELPCH2' AND (CM05.CODE_RPRC = #{selpchCd} OR CM05.CODE_RPRC IS NULL )
AND A.PRDT_GRP = #{prdtGrp}
UNION SELECT 0 AS TYPE ,0
,#{taxivcCoprt}
,TO_CHAR(TO_DATE(REPLACE(#{strtDt}, '-', ''),'YYYYMMDD'),'YYYY-MM') ,TO_CHAR(TO_DATE(REPLACE(#{strtDt}, '-', ''),'YYYYMMDD'),'YYYY-MM-DD')
,#{coCd}
,'' AS CO_CD
,TO_NUMBER(#{clntCd}) AS CLNT_CD
, 0 AS CLNT_CD
, #{clntNm} AS CLNT_NM
,(SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = #{clntCd}) AS CLNT_NM
,'이월' AS GUBUN_NM ,'' AS GUBUN_CD ,'전기 이월' AS BIGO , 0 AS PCHS_AMT , 0 AS SELL_AMT , 0 AS BASIS_AMT
, '' AS rmk FROM DUAL ) A
ORDER BY A.TRST_DT,TYPE, DECODE(RN,0,0,1), A.CLNT_NM ) B
