mapper.select:
SELECT
	COUNT(*)
FROM
	TB_AR10M01 T
WHERE
		T.CO_CD = #{coCd}	

		AND T.SELPCH_CD = #{selpchCd}				


		AND T.TRST_DT >= #{trstDtFrom}


		 AND T.TRST_DT <= #{trstDtTo} 


		AND T.CLNT_NM LIKE '%'|| #{clntNm} || '%'


		AND T.TAXIVC_COPRT_CD = #{taxivcCoprtCd}


		AND T.SITE_NM LIKE '%'|| #{siteNm} || '%'


		AND T.PRDT_NM LIKE '%'|| #{prdtNm} || '%'


		AND T.PRDT_SIZE LIKE '%'|| #{prdtSize} || '%'


		AND T.PRDT_LEN LIKE '%'|| #{prdtLen} || '%'


		AND T.RMK LIKE '%'|| #{rmk} || '%'

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM
FROM ( SELECT T.CO_CD, FN_CM05M01_CD_TO_NM(T.SELPCH_CD) AS SELPCH_NM, T.TRST_SEQ, T.SELPCH_CD, TO_CHAR(TO_DATE(T.TRST_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS TRST_DT, T.TRST_DT, T.CLNT_CD, T.CLNT_NM, T.SITE_CD, T.SITE_NM, T.PRDT_CD, T.PRDT_NM, T.PRDT_SPEC, T.PRDT_SIZE, T.PRDT_LEN, T.MAKR_CD, T.MAKR_NM, T.SELL_CLNT_NM, T.BILG_QTY, T.BILG_WT, T.BILG_UPR, T.BILG_AMT, T.BILG_VAT_AMT, T.BILG_TOT_AMT, T.RMK, T.USE_YN, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, T.CREAT_PGM, T.CREAT_DTTM, T.UDT_ID, T.UPD_PGM, T.UDT_DTTM, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT_CD) AS TAXIVC_COPRT_NM, T.TAXIVC_COPRT_CD FROM TB_AR10M01 T WHERE T.CO_CD = #{coCd}
AND T.SELPCH_CD = #{selpchCd}
AND T.TRST_DT >= #{trstDtFrom}
AND T.TRST_DT <= #{trstDtTo}
AND T.CLNT_NM LIKE '%'|| #{clntNm} || '%'
AND T.TAXIVC_COPRT_CD = #{taxivcCoprtCd}
AND T.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_NM LIKE '%'|| #{prdtNm} || '%'
AND T.PRDT_SIZE LIKE '%'|| #{prdtSize} || '%'
AND T.PRDT_LEN LIKE '%'|| #{prdtLen} || '%'
AND T.RMK LIKE '%'|| #{rmk} || '%'
) A ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}
