mapper.select:
SELECT COUNT(*) FROM TB_AR10M01 T WHERE T.CO_CD = #{coCd}
AND T.SELPCH_CD = #{selpchCd}
AND T.TRST_DT >= #{trstDtFrom}
AND T.TRST_DT <= #{trstDtTo}
AND T.RMK LIKE '%'|| #{rmk} || '%'
AND T.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.CLNT_NM LIKE '%'|| #{clntNm} || '%'
AND T.PRDT_NM LIKE '%'|| #{prdtNm} || '%'
AND T.PRDT_SIZE LIKE '%'|| #{prdtSize} || '%'
AND T.PRDT_LEN LIKE '%'|| #{prdtLen} || '%'

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM
FROM ( SELECT T.SELPCH_CD, FN_CM05M01_CD_TO_NM(T.SELPCH_CD) AS SELPCH_NM, TO_CHAR(TO_DATE(T.TRST_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS TRST_DT, T.TRST_DT, T.CLNT_NM, T.PRDT_NM, T.PRDT_SIZE, T.PRDT_LEN, T.SITE_NM, T.RMK, T.BILG_QTY, T.BILG_WT, T.BILG_UPR, T.BILG_AMT, T.BILG_VAT_AMT, T.BILG_TOT_AMT, T.MAKR_NM FROM TB_AR10M01 T WHERE T.CO_CD = #{coCd}
AND T.SELPCH_CD = #{selpchCd}
AND T.TRST_DT >= #{trstDtFrom}
AND T.TRST_DT <= #{trstDtTo}
AND T.CLNT_NM LIKE '%'|| #{clntNm} || '%'
AND T.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.RMK LIKE '%'|| #{rmk} || '%'
AND T.PRDT_NM LIKE '%'|| #{prdtNm} || '%'
AND T.PRDT_SIZE LIKE '%'|| #{prdtSize} || '%'
AND T.PRDT_LEN LIKE '%'|| #{prdtLen} || '%'
) A ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}
