mapper.select:
SELECT
	COUNT(*)
FROM
	TB_AR10M01 T
WHERE
		T.CO_CD = #{coCd}	

		AND T.SELPCH_CD = #{selpchCd}				


		AND T.TRST_DT >= #{trstDtFrom}


		 AND T.TRST_DT <= #{trstDtTo} 


		AND T.CLNT_NM LIKE '%'|| #{clntNm} || '%'


		AND T.TAXIVC_COPRT_CD = #{taxivcCoprtCd}


		AND T.SITE_NM LIKE '%'|| #{siteNm} || '%'


		AND T.PRDT_NM LIKE '%'|| #{prdtNm} || '%'


		AND T.PRDT_SIZE LIKE '%'|| #{prdtSize} || '%'


		AND T.PRDT_LEN LIKE '%'|| #{prdtLen} || '%'


		AND T.RMK LIKE '%'|| #{rmk} || '%'

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM
FROM ( SELECT T.CO_CD, FN_CM05M01_CD_TO_NM(T.SELPCH_CD) AS SELPCH_NM, T.TRST_SEQ, T.SELPCH_CD, TO_CHAR(TO_DATE(T.TRST_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS TRST_DT, T.TRST_DT, T.CLNT_CD, T.CLNT_NM, T.SITE_CD, T.SITE_NM, T.PRDT_CD, T.PRDT_NM, T.PRDT_SPEC, T.PRDT_SIZE, T.PRDT_LEN, T.MAKR_CD, T.MAKR_NM, T.SELL_CLNT_NM, T.BILG_QTY, T.BILG_WT, T.BILG_UPR, T.BILG_AMT, T.BILG_VAT_AMT, T.BILG_TOT_AMT, T.RMK, T.USE_YN, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, T.CREAT_PGM, T.CREAT_DTTM, T.UDT_ID, T.UPD_PGM, T.UDT_DTTM, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT_CD) AS TAXIVC_COPRT_NM, T.TAXIVC_COPRT_CD FROM TB_AR10M01 T WHERE T.CO_CD = #{coCd}
AND T.SELPCH_CD = #{selpchCd}
AND T.TRST_DT >= #{trstDtFrom}
AND T.TRST_DT <= #{trstDtTo}
AND T.CLNT_NM LIKE '%'|| #{clntNm} || '%'
AND T.TAXIVC_COPRT_CD = #{taxivcCoprtCd}
AND T.SITE_NM LIKE '%'|| #{siteNm} || '%'
AND T.PRDT_NM LIKE '%'|| #{prdtNm} || '%'
AND T.PRDT_SIZE LIKE '%'|| #{prdtSize} || '%'
AND T.PRDT_LEN LIKE '%'|| #{prdtLen} || '%'
AND T.RMK LIKE '%'|| #{rmk} || '%'
ORDER BY T.CO_CD, T.TAXIVC_COPRT_CD, T.TRST_DT, T.CLNT_NM, T.SELPCH_CD ) A ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM
FROM ( WITH t AS ( SELECT CO_CD, TAXIVC_COPRT_CD, SELPCH_CD, CLNT_NM, SUBSTR(TRST_DT,1,6) YYMM, SUM(BILG_TOT_AMT) BILG_TOT_AMT, COUNT(*) CNT FROM TB_AR10M01 WHERE CO_CD = #{coCd}
AND SELPCH_CD = #{selpchCd}
AND TAXIVC_COPRT_CD = #{taxivcCoprtCd}
AND TRST_DT BETWEEN #{searchYear} || '0101' AND #{searchYear} || '1231'
AND CLNT_NM LIKE '%'|| #{clntNm} || '%'
AND SITE_NM LIKE '%'|| #{siteNm} || '%'
AND PRDT_NM LIKE '%'|| #{prdtNm} || '%'
AND PRDT_SIZE LIKE '%'|| #{prdtSize} || '%'
AND PRDT_LEN LIKE '%'|| #{prdtLen} || '%'
AND RMK LIKE '%'|| #{rmk} || '%'
GROUP BY CO_CD, TAXIVC_COPRT_CD, SELPCH_CD, CLNT_NM, SUBSTR(TRST_DT,1,6) ) SELECT CO_CD, FN_CM05M01_CD_TO_NM(CO_CD) AS CO_NM, TAXIVC_COPRT_CD, FN_CM05M01_CD_TO_NM(TAXIVC_COPRT_CD) AS TAXIVC_COPRT_NM, SELPCH_CD, FN_CM05M01_CD_TO_NM(SELPCH_CD) AS SELPCH_NM, CLNT_NM, round(sum(BILG_TOT_AMT)/#{searchItem}) TOT_AMT, sum(CNT) TOT_CNT, round(sum(CASE WHEN substr(yymm,5,2) = 01 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "01월합계", round(sum(CASE WHEN substr(yymm,5,2) = 02 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "02월합계", round(sum(CASE WHEN substr(yymm,5,2) = 03 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "03월합계", round(sum(CASE WHEN substr(yymm,5,2) = 04 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "04월합계", round(sum(CASE WHEN substr(yymm,5,2) = 05 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "05월합계", round(sum(CASE WHEN substr(yymm,5,2) = 06 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "06월합계", round(sum(CASE WHEN substr(yymm,5,2) = 07 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "07월합계", round(sum(CASE WHEN substr(yymm,5,2) = 08 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "08월합계", round(sum(CASE WHEN substr(yymm,5,2) = 09 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "09월합계", round(sum(CASE WHEN substr(yymm,5,2) = 10 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "10월합계", round(sum(CASE WHEN substr(yymm,5,2) = 11 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "11월합계", round(sum(CASE WHEN substr(yymm,5,2) = 12 THEN BILG_TOT_AMT ELSE 0 END)/#{searchItem}) "12월합계", sum(CASE WHEN substr(yymm,5,2) = 01 THEN cnt ELSE 0 END) "01월건수", sum(CASE WHEN substr(yymm,5,2) = 02 THEN cnt ELSE 0 END) "02월건수", sum(CASE WHEN substr(yymm,5,2) = 03 THEN cnt ELSE 0 END) "03월건수", sum(CASE WHEN substr(yymm,5,2) = 04 THEN cnt ELSE 0 END) "04월건수", sum(CASE WHEN substr(yymm,5,2) = 05 THEN cnt ELSE 0 END) "05월건수", sum(CASE WHEN substr(yymm,5,2) = 06 THEN cnt ELSE 0 END) "06월건수", sum(CASE WHEN substr(yymm,5,2) = 07 THEN cnt ELSE 0 END) "07월건수", sum(CASE WHEN substr(yymm,5,2) = 08 THEN cnt ELSE 0 END) "08월건수", sum(CASE WHEN substr(yymm,5,2) = 09 THEN cnt ELSE 0 END) "09월건수", sum(CASE WHEN substr(yymm,5,2) = 10 THEN cnt ELSE 0 END) "10월건수", sum(CASE WHEN substr(yymm,5,2) = 11 THEN cnt ELSE 0 END) "11월건수", sum(CASE WHEN substr(yymm,5,2) = 12 THEN cnt ELSE 0 END) "12월건수" FROM t GROUP BY CO_CD, TAXIVC_COPRT_CD, SELPCH_CD, CLNT_NM ORDER BY CO_CD, TAXIVC_COPRT_CD, SELPCH_CD, CLNT_NM ) A ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}
