mapper.select:
SELECT
	COUNT(*)
FROM
	TB_AR11M01 T
WHERE
			T.CO_CD = #{coCd}	

			AND ETRDPS_DT BETWEEN #{trstDtFrom} AND #{trstDtTo}


			AND T.CLNT_NM LIKE '%'|| #{clntNm} || '%'


			AND T.TAXIVC_COPRT_CD = #{taxivcCoprtCd}


			AND T.ACC_NM LIKE '%'|| #{accNm} || '%'


			AND T.ETRDPS_TYP = #{etrdpsTyp}


			AND T.SUMRY LIKE '%'|| #{sumry} || '%'


			AND T.PYMNT_BANK_CD LIKE '%'|| #{pymntBankCd} || '%'

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM
FROM ( SELECT T.CO_CD, T.CLNT_CD, T.CLNT_NM, TO_CHAR(TO_DATE(T.ETRDPS_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS ETRDPS_DT, T.ETRDPS_DT, T.ETRDPS_TYP, T.ETRDPS_SEQ, FN_CM05M01_CD_TO_NM(T.ETRDPS_TYP) AS ETRDPS_NM, T.ACC_NM, T.SUMRY, T.DE_AMT, T.CR_AMT, T.BIL_NO, T.BIL_PBLS_PSN, T.PYMNT_BANK_CD, T.EXPRTN_DT, T.ETC_FIELD1, T.ETC_FIELD2, T.ETC_FIELD3, T.CREAT_ID, T.CREAT_PGM, T.CREAT_DTTM, T.UDT_ID, T.UPD_PGM, T.UDT_DTTM, FN_CM05M01_CD_TO_NM(T.TAXIVC_COPRT_CD) AS TAXIVC_COPRT_NM, T.TAXIVC_COPRT_CD FROM TB_AR11M01 T WHERE T.CO_CD = #{coCd}
AND ETRDPS_DT BETWEEN #{trstDtFrom} AND #{trstDtTo}
AND T.CLNT_NM LIKE '%'|| #{clntNm} || '%'
AND T.TAXIVC_COPRT_CD = #{taxivcCoprtCd}
AND T.ACC_NM LIKE '%'|| #{accNm} || '%'
AND T.ETRDPS_TYP = #{etrdpsTyp}
AND T.SUMRY LIKE '%'|| #{sumry} || '%'
AND T.PYMNT_BANK_CD LIKE '%'|| #{pymntBankCd} || '%'
ORDER BY T.CO_CD, T.TAXIVC_COPRT_CD, T.ETRDPS_DT, T.CLNT_NM, T.ETRDPS_TYP ) A ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}
