mapper.select:
	SELECT COUNT(*)
  	 FROM TB_AR14M01
  	 	WHERE 1=1 

			AND CO_CD = #{coCd}		


			AND TAXIVC_COPRT= #{taxivcCoprt}				


			AND CLNT_CD = #{clntCd}				


			AND CLNT_NM LIKE '%${clntNm}%'	


			AND TO_CHAR(TO_DATE(INST_DT, 'YYYYMMDD'),'YYYY-MM-DD') <= #{baseDt}

SELECT AR14.CO_CD , FN_CM05M01_CD_TO_NM(AR14.CO_CD) AS CO_NM , AR14.TAXIVC_COPRT , FN_CM05M01_CD_TO_NM(AR14.TAXIVC_COPRT) AS TAXIVC_COPRT_NM , TO_CHAR(TO_DATE(AR14.INST_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS INST_DT , AR14.CLNT_CD , AR14.CLNT_NM , AR14.REPST_NM , AR14.CRN , AR14.MNG_CD , AR14.MNG_NM , AR14.MNG_TEL , AR14.ASSORT_CD , AR14.DEBT_AMT , AR14.DEBT_RMK , AR14.USE_YN , AR14.CREAT_ID , AR14.CREAT_PGM , AR14.DEBT_NO , DECODE((SELECT COUNT(*) FROM TB_CM08M01 CM08 WHERE FILE_TRGT_KEY = AR14.DEBT_NO), 0, 'N', 'Y') AS FILE_YN FROM TB_AR14M01 AR14 WHERE 1=1
AND AR14.CO_CD = #{coCd}
AND AR14.TAXIVC_COPRT= #{taxivcCoprt}
AND AR14.CLNT_CD = #{clntCd}
AND AR14.CLNT_NM LIKE '%${clntNm}%'
AND TO_CHAR(TO_DATE(AR14.INST_DT,'YYYYMMDD'),'YYYY-MM-DD') <= #{baseDt}
ORDER BY AR14.INST_DT

