mapper.update:
UPDATE
	TB_AR14M01
		SET
			  CO_CD 		= #{coCd}
			, TAXIVC_COPRT 	= #{taxivcCoprt}
			, INST_DT 		= TO_CHAR(TO_DATE(#{instDt}, 'YYYY-MM-DD'), 'YYYYMMDD')
			, CLNT_CD 		= #{clntCd}
			, CLNT_NM 		= #{clntNm}

			, REPST_NM 		= #{repstNm}


			, CRN 			= #{crn}


			, MNG_CD 		= #{mngCd}


			, MNG_NM 		= #{mngNm}


			, MNG_TEL 		= #{mngTel}

			, ASSORT_CD 	= #{assortCd}
			, DEBT_AMT 		= #{debtAmt}

			, DEBT_RMK 		= #{debtRmk}

			, UDT_ID 		= #{userId}
			, UDT_PGM 		= #{pgmId}
		WHERE 
			  DEBT_NO 		= #{debtNo}

mapper.select:
SELECT COUNT(*) FROM ( SELECT * FROM ( SELECT CO_CD , FN_CM05M01_CD_TO_NM(CO_CD) AS CO_NM , TAXIVC_COPRT , FN_CM05M01_CD_TO_NM(TAXIVC_COPRT) AS TAXIVC_COPRT_NM , CLNT_CD , CLNT_NM , REPST_NM , CRN , SUM(DEBT_AMT) AS DEBT_AMT , SUM(REPAY_AMT) AS REPAY_AMT FROM ( SELECT CO_CD , TAXIVC_COPRT , CLNT_CD , CLNT_NM , REPST_NM , CRN , DECODE(ASSORT_CD, 'ASSORTCD1', DEBT_AMT) AS DEBT_AMT , DECODE(ASSORT_CD, 'ASSORTCD2', DEBT_AMT) AS REPAY_AMT FROM TB_AR14M01 WHERE 1=1
AND CO_CD = #{coCd}
AND TAXIVC_COPRT= #{taxivcCoprt}
AND CLNT_CD = #{clntCd}
AND CLNT_NM LIKE '%${clntNm}%'
AND TO_CHAR(TO_DATE(INST_DT, 'YYYYMMDD'),'YYYY-MM-DD') <= #{baseDt}
) GROUP BY CO_CD, TAXIVC_COPRT, CLNT_CD, CLNT_NM, REPST_NM, CRN ) T )

SELECT * FROM ( SELECT ROWNUM AS RNUM, T.* FROM ( SELECT CO_CD , FN_CM05M01_CD_TO_NM(CO_CD) AS CO_NM , TAXIVC_COPRT , FN_CM05M01_CD_TO_NM(TAXIVC_COPRT) AS TAXIVC_COPRT_NM , CLNT_CD , CLNT_NM , REPST_NM , CRN , NVL(SUM(DEBT_AMT),0) AS DEBT_AMT , NVL(SUM(REPAY_AMT1),0) AS REPAY_AMT1 , NVL(SUM(REPAY_AMT2),0) AS REPAY_AMT2 , NVL(SUM(REPAY_AMT3),0) AS REPAY_AMT3 , NVL(SUM(REPAY_AMT4),0) AS REPAY_AMT4 , NVL(SUM(DEBT_AMT),0) - NVL(SUM(REPAY_AMT1),0) - NVL(SUM(REPAY_AMT2),0) - NVL(SUM(REPAY_AMT3),0) - NVL(SUM(REPAY_AMT4),0) AS BALANCE FROM ( SELECT CO_CD , TAXIVC_COPRT , CLNT_CD , CLNT_NM , REPST_NM , CRN , DECODE(ASSORT_CD, 'ASSORTCD1', DEBT_AMT) AS DEBT_AMT , DECODE(ASSORT_CD, 'ASSORTCD2', DEBT_AMT) AS REPAY_AMT1 , DECODE(ASSORT_CD, 'ASSORTCD3', DEBT_AMT) AS REPAY_AMT2 , DECODE(ASSORT_CD, 'ASSORTCD4', DEBT_AMT) AS REPAY_AMT3 , DECODE(ASSORT_CD, 'ASSORTCD5', DEBT_AMT) AS REPAY_AMT4 FROM TB_AR14M01 WHERE 1=1
AND CO_CD = #{coCd}
AND TAXIVC_COPRT= #{taxivcCoprt}
AND CLNT_CD = #{clntCd}
AND CLNT_NM LIKE '%${clntNm}%'
AND TO_CHAR(TO_DATE(INST_DT, 'YYYYMMDD'),'YYYY-MM-DD') <= #{baseDt}
) GROUP BY CO_CD, TAXIVC_COPRT, CLNT_CD, CLNT_NM, REPST_NM, CRN ) T ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
