mapper.select:
SELECT * FROM( SELECT ROWNUM AS RNUM, D.* FROM ( SELECT T.SALES_MNG
,(SELECT NAME FROM TB_CM06M01 WHERE ID = T.SALES_MNG) AS SALES_MNG_NM ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '01', REAL_TRST_WT, 0)) AS REAL_TRST_WT_01 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '02', REAL_TRST_WT, 0)) AS REAL_TRST_WT_02 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '03', REAL_TRST_WT, 0)) AS REAL_TRST_WT_03 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '04', REAL_TRST_WT, 0)) AS REAL_TRST_WT_04 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '05', REAL_TRST_WT, 0)) AS REAL_TRST_WT_05 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '06', REAL_TRST_WT, 0)) AS REAL_TRST_WT_06 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '07', REAL_TRST_WT, 0)) AS REAL_TRST_WT_07 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '08', REAL_TRST_WT, 0)) AS REAL_TRST_WT_08 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '09', REAL_TRST_WT, 0)) AS REAL_TRST_WT_09 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '10', REAL_TRST_WT, 0)) AS REAL_TRST_WT_10 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '11', REAL_TRST_WT, 0)) AS REAL_TRST_WT_11 ,SUM(DECODE(SUBSTRING(TRST_DT,5,2), '12', REAL_TRST_WT, 0)) AS REAL_TRST_WT_12 ,SUM(REAL_TRST_WT) AS REAL_TRST_WT ,ROUND(SUM(REAL_TRST_WT)/12) AS REAL_TRST_WT_AVG FROM TB_AR02M01 T LEFT OUTER JOIN TB_SD09M01 SD09 ON SD09.SITE_CD = T.SITE_CD WHERE 1=1 AND T.SELPCH_CD = 'SELPCH2' AND TO_CHAR(TO_DATE(T.TRST_DT, 'YYYYMMDD'), 'YYYY') = #{salesYy}
AND T.CO_CD = #{coCd}
AND T.SALES_MNG = #{salesMng}
AND T.BILG_CERT_NO IS NOT NULL
AND T.BILG_CERT_NO IS NULL
AND T.TRST_CLNT_CD = #{clntCd}
AND T.CLNT_NM LIKE '%' || #{clntNm} || '%'
AND T.TRSP_RMK LIKE '%' || #{trspRmk} || '%'
AND T.PRDT_DIV = #{prdtDiv}
AND T.PRDT_STKN = #{prdtStkn}
AND T.PRJCT_CD = #{prjctCd}
AND T.SITE_CD = #{siteCd}
AND SD09.SITE_NM like '%'|| #{siteNm} || '%'
AND T.PRDT_DIV IN (SELECT DISTINCT PRDT_DIV FROM TB_BM01M01 WHERE PRDT_GRP = #{prdtGrp} )
AND T.TAXIVC_COPRT = #{taxivcCoprt}
GROUP BY T.SALES_MNG ) D
D.SALES_MNG_NM LIKE '%' || #{salesMngNm} ||'%'
) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}
