mapper.select:
SELECT COUNT(*) FROM ( SELECT A.CODE_ID AS CO_CD , A.CODE_NM AS CO_NM , B.* FROM TB_CM05M01 A , (SELECT BM.CLNT_CD, BM.CLNT_NM, C.* FROM TB_BM02M01 BM , (SELECT CODE_ID AS PRDT_GRP, FN_CM05M01_CD_TO_NM(CODE_ID) AS PRDT_GRP_NM FROM TB_CM05M01 WHERE CODE_KIND = 'PRDTGRP') C WHERE 1=1
AND BM.CLNT_NM LIKE '%${clntNm}%'
AND BM.CLNT_CD = #{clntCd}
AND BM.CRN LIKE '%${crn}%'
AND BM.USE_YN = #{useYn}
AND BM.CLNT_DIV_CD = #{clntDivCd}
) B WHERE CODE_KIND = 'CO' AND CODE_ID != 'GGS' ORDER BY B.CLNT_NM, CO_CD, B.PRDT_GRP )

SELECT * FROM( SELECT ROWNUM AS RNUM, T.* FROM( SELECT A.CODE_ID AS CO_CD , A.CODE_NM AS CO_NM , B.* , F_CREDIT_LOAN_PRDTGRP2(B.CLNT_CD, A.CODE_ID, B.PRDT_GRP, #{baseDt}) AS LOAN_AMT FROM TB_CM05M01 A , (SELECT BM.CLNT_CD, BM.CLNT_NM, C.* FROM TB_BM02M01 BM , (SELECT CODE_ID AS PRDT_GRP, FN_CM05M01_CD_TO_NM(CODE_ID) AS PRDT_GRP_NM FROM TB_CM05M01 WHERE CODE_KIND = 'PRDTGRP') C WHERE 1=1
AND BM.CLNT_NM LIKE '%${clntNm}%'
AND BM.CLNT_CD = #{clntCd}
AND BM.CRN LIKE '%${crn}%'
AND BM.USE_YN = #{useYn}
AND BM.CLNT_DIV_CD = #{clntDivCd}
) B WHERE CODE_KIND = 'CO' AND CODE_ID != 'GGS' ORDER BY B.CLNT_NM, CO_CD, B.PRDT_GRP ) T ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
