mapper.select:
	SELECT 
 		COUNT(*) 
	  FROM TB_FI01M01 T


	 	AND CO_CD = #{coCd}


	 	AND USE_YN = #{useYn}

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT CO_CD , FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM , BASE_DEPT , UPPER_DEPT , LAST_DEPT , USE_YN , RMK FROM TB_FI01M01 T
AND CO_CD = #{coCd}
AND USE_YN = #{useYn}
ORDER BY CO_CD ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT CO_CD , FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM , BASE_DEPT , UPPER_DEPT , LAST_DEPT , USE_YN , RMK FROM TB_FI01M01 T
AND CO_CD = #{coCd}
AND BASE_DEPT = #{baseDept}

mapper.insert:
INSERT INTO TB_FI01M01
(
CO_CD
 , BASE_DEPT
 , UPPER_DEPT
 , LAST_DEPT
 , USE_YN
 , RMK
 , CREAT_ID
 , CREAT_PGM
 , CREAT_DTTM
 ) VALUES (
 #{coCd}
 , #{baseDept}
 , #{upperDept}
 , #{lastDept}
 , #{useYn}
 , #{rmk}
 , #{userId}
 , #{pgmId}
 , SYSDATE
 )

mapper.update:
UPDATE TB_FI01M01
SET BASE_DEPT      = #{baseDept}
  , UPPER_DEPT       = #{upperDept}
  , LAST_DEPT = #{lastDept}
  , USE_YN     = #{useYn}
  , RMK     = #{rmk}
  , UDT_ID        = #{userId}
  , UDT_PGM       = #{pgmId}
  , UDT_DTTM      = SYSDATE
WHERE CO_CD     = #{coCd}
  AND BASE_DEPT = #{baseDept}

mapper.delete:
DELETE FROM TB_FI01M01 
 WHERE CO_CD = #{coCd}
  AND BASE_DEPT = #{baseDept}
