mapper.select:
SELECT COUNT(*) FROM TB_FI02M01 T
AND CO_CD = #{coCd}
AND PRFT_YM = REPLACE(#{prftYm}, '-', '')

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT CO_CD , FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM , TO_CHAR(TO_DATE(PRFT_YM, 'YYYYMM'), 'YYYY-MM') PRFT_YM , BASE_DEPT , UPPER_DEPT , LAST_DEPT , SELL_AMT , SELL_PRFT_AMT , SELL_MNG_AMT , SALES_PRFT_AMT , BFRX_PRFT_AMT , RMK , CREAT_ID FROM TB_FI02M01 T
AND CO_CD = #{coCd}
AND PRFT_YM = REPLACE(#{prftYm}, '-', '')
ORDER BY PRFT_YM ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT CO_CD , FN_CM05M01_CD_TO_NM(T.CO_CD) AS CO_NM , PRFT_YM , BASE_DEPT , UPPER_DEPT , LAST_DEPT , SELL_AMT , SELL_PRFT_AMT , SELL_MNG_AMT , SALES_PRFT_AMT , BFRX_PRFT_AMT , RMK FROM TB_FI02M01 T
AND CO_CD = #{coCd}
AND PRFT_YM = REPLACE(#{prftYm}, '-', '')

mapper.insert:
INSERT INTO TB_FI02M01
