mapper.select:
	SELECT 
 		COUNT(*) 
 	FROM TB_OD01M01 OD01
	INNER JOIN TB_BM02M01  BM02
	  	    ON BM02.CLNT_CD = OD01.CLNT_CD
 	WHERE 1=1

		AND ODR_SEQ = #{odrSeq}				


		AND DLVR_DTTM BETWEEN #{reqDtFrom} AND #{reqDtTo}		


		AND CO_CD = #{coCd}				


		AND OWNER_CD = #{ownerCd}			


		AND TYP_CD = #{typCd}				


		AND BM02.CLNT_CD = #{clntCd}				


		AND BM02.CLNT_NM like '%${clntNm}%'	


		AND DIRTRS_YN = #{dirtrsYn}				


		AND PRJCT_CD = #{prjctCd}				


		AND WH_CD = #{whCd}


		AND MAKR_CD = #{makrCd}


		AND IMP_YN = #{impYn}


		AND ORDRG_YN = #{ordrgYn}


		AND SHIP_YN = #{shipYn}

SELECT * FROM ( SELECT ROWNUM RNUM ,T.ORDRG_SEQ ,T.CO_CD ,TO_CHAR(TO_DATE(REQ_DT), 'YYYY-MM-DD') AS REQ_DT ,T.CLNT_CD ,(SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.CLNT_CD) AS CLNT_NM ,T.PRJCT_CD ,(SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = T.PRJCT_CD) AS PRJCT_NM ,T.MAKR_CD ,FN_CM05M01_CD_TO_NM(T.MAKR_CD) AS MAKR_NM ,T.WH_CD ,FN_CM05M01_CD_TO_NM(T.WH_CD) AS WH_NM ,T.TYP_CD ,FN_CM05M01_CD_TO_NM(T.TYP_CD) AS TYP_NM ,T.OWNER_CD ,T.IMP_YN ,T.SALES_AREA_CD ,T.SELL_CLNT_CD ,(SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.SELL_CLNT_CD) AS SELL_CLNT_NM ,T.MNG_TEL ,TO_CHAR(T.DLVR_DTTM, 'yyyy-MM-dd') AS DLVR_DTTM ,T.DLVR_RMK ,T.DIRTRS_YN ,T.ADDR_ZIP ,T.ADDR_MAIN ,T.ADDR_SUB ,T.ORDRG_RMK ,T.TOT_QTY ,T.TOT_WT ,T.TOT_AMT ,(SELECT NVL(SUM(REAL_DLVR_QTY), 0) FROM TB_OD01D01 WHERE ORDRG_YN = 'Y' AND ORDRG_SEQ = T.ORDRG_SEQ) AS REAL_TOT_QTY ,(SELECT NVL(SUM(REAL_DLVR_WT), 0) FROM TB_OD01D01 WHERE ORDRG_YN = 'Y' AND ORDRG_SEQ = T.ORDRG_SEQ) AS REAL_TOT_WT ,(SELECT NVL(SUM(REAL_DLVR_AMT), 0) FROM TB_OD01D01 WHERE ORDRG_YN = 'Y' AND ORDRG_SEQ = T.ORDRG_SEQ) AS REAL_TOT_AMT ,T.TRANS_AMT ,T.TRANS_SEQ ,T.ODR_SEQ ,T.PCHS_NO ,T.SALES_MNG ,(SELECT NAME FROM TB_CM06M01 WHERE ID = T.SALES_MNG) AS SALES_MNG_NM ,T.ORDRG_YN ,T.ORDRG_PROC_ID ,(SELECT NAME FROM TB_CM06M01 WHERE ID = T.ORDRG_PROC_ID) AS ORDRG_PROC_NM ,TO_CHAR(T.ORDRG_PROC_DTTM, 'yyyy-MM-dd') AS ORDRG_PROC_DTTM ,T.SHIP_YN ,T.SHIP_PROC_ID ,(SELECT NAME FROM TB_CM06M01 WHERE ID = T.SHIP_PROC_ID) AS SHIP_PROC_NM ,TO_CHAR(T.SHIP_PROC_DTTM, 'yyyy-MM-dd') AS SHIP_PROC_DTTM ,T.ETC_FIELD1 ,T.ETC_FIELD2 ,T.ETC_FIELD3 ,T.CREAT_ID ,T.CREAT_PGM ,T.CREAT_DTTM ,T.UDT_ID ,T.UDT_PGM ,T.UDT_DTTM ,ADDR_MAIN || ' ' || ADDR_SUB AS ADDR ,DECODE(ORDRG_YN||DIRTRS_YN, 'YY', 'Y', 'N') AS SELL_YN ,SITE_CD ,TAXIVC_COPRT ,(SELECT SITE_NM FROM TB_SD09M01 WHERE SITE_CD = T.SITE_CD) AS SITE_NM ,(SELECT CODE_ETC FROM TB_CM05M01 WHERE CODE_ID = (SELECT CODE_ETC FROM TB_CM05M01 WHERE CODE_ID = T.WH_CD)) AS WH_CLNT_CD ,(SELECT CODE_RPRC FROM TB_CM05M01 WHERE CODE_ID = T.WH_CD) AS WH_TYPE ,T.REQ_SEQ ,T.TRSP_TYP_CD ,FN_CM05M01_CD_TO_NM(T.TRSP_TYP_CD) AS TRSP_TYP_NM ,T.ODR_CREAT_DIV ,FN_CM05M01_CD_TO_NM(T.ODR_CREAT_DIV) AS ODR_CREAT_DIV_NM FROM ( SELECT OD01.* FROM TB_OD01M01 OD01 INNER JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = OD01.CLNT_CD WHERE 1=1
AND ODR_SEQ = #{odrSeq}
AND DLVR_DTTM BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND CO_CD = #{coCd}
AND OWNER_CD = #{ownerCd}
AND TYP_CD = #{typCd}
AND BM02.CLNT_CD = #{clntCd}
AND BM02.CLNT_NM like '%${clntNm}%'
AND DIRTRS_YN = #{dirtrsYn}
AND PRJCT_CD = #{prjctCd}
AND SITE_CD = #{siteCd}
AND WH_CD = #{whCd}
AND MAKR_CD = #{makrCd}
AND IMP_YN = #{impYn}
AND ORDRG_YN = #{ordrgYn}
AND SHIP_YN = #{shipYn}
AND OD01.ORDRG_SEQ IN (SELECT OD01D.ORDRG_SEQ FROM TB_OD01D01 OD01D INNER JOIN TB_BM01M01 BM01 ON OD01D.PRDT_CD = BM01.PRDT_CD WHERE OD01.ORDRG_SEQ = OD01D.ORDRG_SEQ
AND OD01D.PRDT_DIV = #{prdtDiv}
AND BM01.PRDT_GRP = #{prdtGrp}
)
ORDER BY DLVR_DTTM DESC, ORDRG_SEQ DESC ) T ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT D.ODR_SEQ, D.ODR_DTL_SEQ, D.PRDT_CD, B.PRDT_NM, B.PRDT_DIV, B.PRDT_STKN, FN_CM05M01_CD_TO_NM(B.PRDT_STKN) PRDT_STKN_NM, B.PRDT_SIZE, B.PRDT_SPEC, D.PRDT_LEN, D.PRDT_UNIT, FN_CM05M01_CD_TO_NM(D.PRDT_UNIT) AS PRDT_UNIT_NM, D.PRDT_UPR, D.ODR_UPR, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', M.IMP_YN, B.PRDT_CD, M.CLNT_CD, M.PRJCT_CD), 0) AS SHIP_UPR, D.ODR_QTY AS ORDRG_QTY, D.ODR_QTY AS REAL_DLVR_QTY, B.PRDT_CNVRSN_WT, B.PRDT_STOCK_CD, D.ODR_WT AS ORDRG_WT, D.ODR_WT AS REAL_DLVR_WT, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH1', M.IMP_YN, B.PRDT_CD, M.CLNT_CD, M.PRJCT_CD), 0) AS ORDRG_UPR, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH1', M.IMP_YN, B.PRDT_CD, M.CLNT_CD, M.PRJCT_CD), 0) AS REAL_DLVR_UPR, D.ODR_AMT AS ORDRG_AMT, D.ODR_AMT AS REAL_DLVR_AMT, D.ODR_DTL_RMK AS ORDRG_DTL_RMK FROM TB_SD04M01 M INNER JOIN TB_SD04D01 D ON M.ODR_SEQ = D.ODR_SEQ INNER JOIN TB_BM01M01 B ON D.PRDT_CD = B.PRDT_CD WHERE M.ODR_SEQ = #{odrSeq}
AND D.ODR_DTL_SEQ IN
#{odrDtlSeq}
ORDER BY D.ODR_DTL_SEQ

mapper.update:
		UPDATE TB_OD01D01 T
		SET ORDRG_UPR       = #{ordrgUpr}
          , ORDRG_AMT       = #{ordrgAmt}
          , REAL_DLVR_AMT   = #{realDlvrAmt}
          , REAL_DLVR_UPR   = #{realDlvrUpr}
          , SHIP_UPR        = #{shipUpr}
          , ORDRG_DTL_RMK   = #{ordrgDtlRmk}
		  , SHIP_VEH_NO     = #{shipVehNo,  jdbcType=VARCHAR}    
		  , UDT_ID          = #{userId}
		  , UDT_PGM         = #{pgmId}
		  , UDT_DTTM        = SYSDATE		    
		WHERE ORDRG_SEQ = #{ordrgSeq} AND ORDRG_DTL_SEQ = #{ordrgDtlSeq}
