mapper.select:
	SELECT 
 		COUNT(*) 
 	FROM TB_OD01M01
 	WHERE 1=1

		AND ODR_SEQ = #{odrSeq}				


		AND REQ_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}		


		AND CO_CD = #{coCd}				


		AND OWNER_CD = #{ownerCd}			


		AND TYP_CD = #{typCd}				


		AND CLNT_CD = #{clntCd}				


		AND DIRTRS_YN = #{dirtrsYn}				


		AND PRJCT_CD = #{prjctCd}				


		AND WH_CD = #{whCd}


		AND MAKR_CD = #{makrCd}


		AND IMP_YN = #{impYn}


		AND ORDRG_YN = #{ordrgYn}

SELECT * FROM ( SELECT ROWNUM RNUM ,T.ORDRG_SEQ ,T.CO_CD ,TO_CHAR(TO_DATE(REQ_DT), 'YYYY-MM-DD') AS REQ_DT ,T.CLNT_CD ,(SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.CLNT_CD) AS CLNT_NM ,T.PRJCT_CD ,(SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = T.PRJCT_CD) AS PRJCT_NM ,T.MAKR_CD ,FN_CM05M01_CD_TO_NM(T.MAKR_CD) AS MAKR_NM ,T.WH_CD ,FN_CM05M01_CD_TO_NM(T.WH_CD) AS WH_NM ,T.TYP_CD ,FN_CM05M01_CD_TO_NM(T.TYP_CD) AS TYP_NM ,T.OWNER_CD ,T.IMP_YN ,T.SALES_AREA_CD ,T.SELL_CLNT_CD ,(SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.SELL_CLNT_CD) AS SELL_CLNT_NM ,T.MNG_TEL ,TO_CHAR(T.DLVR_DTTM, 'yyyy-MM-dd') AS DLVR_DTTM ,T.DLVR_RMK ,T.DIRTRS_YN ,T.ADDR_ZIP ,T.ADDR_MAIN ,T.ADDR_SUB ,T.ORDRG_RMK ,T.TOT_QTY ,T.TOT_WT ,T.TOT_AMT ,T.TRANS_AMT ,T.ODR_SEQ ,T.PCHS_NO ,T.SALES_MNG ,(SELECT NAME FROM TB_CM06M01 WHERE ID = T.SALES_MNG) AS SALES_MNG_NM ,T.ORDRG_YN ,T.ORDRG_PROC_ID ,(SELECT NAME FROM TB_CM06M01 WHERE ID = T.ORDRG_PROC_ID) AS ORDRG_PROC_NM ,TO_CHAR(T.ORDRG_PROC_DTTM, 'yyyy-MM-dd') AS ORDRG_PROC_DTTM ,T.ETC_FIELD1 ,T.ETC_FIELD2 ,T.ETC_FIELD3 ,T.CREAT_ID ,T.CREAT_PGM ,T.CREAT_DTTM ,T.UDT_ID ,T.UDT_PGM ,T.UDT_DTTM ,ADDR_MAIN || ' ' || ADDR_SUB AS ADDR ,DECODE(ORDRG_YN||DIRTRS_YN, 'YY', 'Y', 'N') AS SELL_YN FROM ( SELECT * FROM TB_OD01M01 WHERE 1=1
AND ODR_SEQ = #{odrSeq}
AND REQ_DT BETWEEN #{reqDtFrom} AND #{reqDtTo}
AND CO_CD = #{coCd}
AND OWNER_CD = #{ownerCd}
AND TYP_CD = #{typCd}
AND CLNT_CD = #{clntCd}
AND DIRTRS_YN = #{dirtrsYn}
AND PRJCT_CD = #{prjctCd}
AND WH_CD = #{whCd}
AND MAKR_CD = #{makrCd}
AND IMP_YN = #{impYn}
AND ORDRG_YN = #{ordrgYn}
ORDER BY UDT_DTTM DESC ) T ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT T.ODR_SEQ, T.ODR_DTL_SEQ, T.PRDT_CD, (SELECT PRDT_NM FROM TB_BM01M01 WHERE PRDT_CD = T.PRDT_CD) AS PRDT_NM, FN_CM05M01_CD_TO_NM(B.PRDT_STKN) PRDT_STKN_NM, T.PRDT_UNIT AS PRDT_UNIT_NM, T.PRDT_LEN, T.PRDT_UPR, T.ODR_QTY AS ORDRG_QTY, T.ODR_WT AS ORDRG_WT, T.ODR_UPR AS ORDRG_UPR, T.ODR_AMT AS ORDRG_AMT, T.ODR_DTL_RMK AS ORDRG_DTL_RMK FROM TB_SD04D01 T INNER JOIN TB_BM01M01 B ON T.PRDT_CD = B.PRDT_CD WHERE ODR_SEQ = #{odrSeq}
AND ODR_DTL_SEQ IN
#{odrDtlSeq}
ORDER BY T.ODR_DTL_SEQ
