mapper.select:
	SELECT
		COUNT(*)
	FROM TB_OD02M01 A
	   , TB_SD09M01 B
   WHERE A.SITE_CD = B.SITE_CD(+)

   		 AND A.REQ_SEQ IN (SELECT REQ_SEQ FROM TB_OD02D01 
					   		 WHERE PRDT_DIV = #{prdtDiv}
					     	 GROUP BY REQ_SEQ)


		   AND A.CO_CD = #{coCd}


		   AND A.REQ_DT BETWEEN #{startDt} AND #{endDt}


		   AND A.WH_CD = #{whCd}


		   AND A.TYP_CD = #{typCd}


		   AND B.SITE_NM LIKE '%' || #{siteNm} || '%'


		   AND A.SITE_CD = #{siteCd}


		   AND A.IMP_YN = #{impYn}

SELECT * FROM( SELECT ROWNUM AS RNUM, D.* FROM( SELECT A.CO_CD , FN_CM05M01_CD_TO_NM(A.CO_CD) CO_NM , A.REQ_DT , A.REQ_SEQ , A.PRJCT_CD , FN_CM05M01_CD_TO_NM(A.PRJCT_CD) PRJCT_NM , A.SITE_CD , B.SITE_NM , A.WH_CD , FN_CM05M01_CD_TO_NM(A.WH_CD) WH_NM , A.TYP_CD , FN_CM05M01_CD_TO_NM(A.TYP_CD) TYP_NM , A.IMP_YN , A.DLVR_DTTM , A.REQ_RMK , A.TOT_QTY , A.TOT_WT , A.REQ_MNG_ID , CASE WHEN (SELECT COUNT(*) FROM TB_OD02D01 WHERE A.REQ_SEQ = REQ_SEQ AND ORDRG_YN = 'Y') > 0 THEN 'Y' ELSE 'N' END DTL_ORDRG_YN , A.ORDRG_YN , A.REQ_CREAT_DIV , A.CREAT_ID , A.CREAT_PGM , A.CREAT_DTTM , A.UDT_ID , A.UDT_PGM , A.UDT_DTTM FROM TB_OD02M01 A , TB_SD09M01 B WHERE A.SITE_CD = B.SITE_CD(+)
AND A.REQ_SEQ IN (SELECT REQ_SEQ FROM TB_OD02D01 WHERE PRDT_DIV = #{prdtDiv} GROUP BY REQ_SEQ)
AND A.CO_CD = #{coCd}
AND A.REQ_DT BETWEEN #{startDt} AND #{endDt}
AND A.WH_CD = #{whCd}
AND A.TYP_CD = #{typCd}
AND B.SITE_NM LIKE '%' || #{siteNm} || '%'
AND A.SITE_CD = #{siteCd}
AND A.IMP_YN = #{impYn}
) D ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

SELECT REQ_SEQ , REQ_DTL_SEQ , A.PRDT_CD , B.PRDT_NM PRDT_NM , A.PRDT_DIV , A.PRDT_STKN , FN_CM05M01_CD_TO_NM(A.PRDT_STKN) PRDT_STKN_NM , A.PRDT_ERQK_YN , A.PRDT_SIZE , A.PRDT_SPEC , A.PRDT_COIL_YN , A.PRDT_LEN , A.PRDT_UNIT , FN_CM05M01_CD_TO_NM(A.PRDT_UNIT) PRDT_UNIT_NM , B.PRDT_CNVRSN_WT PRDT_CNVRSN_WT , DECODE(B.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH1', #{impYn}, B.PRDT_CD, #{clntCd}), 0)) AS ORDRG_UPR , DECODE(B.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', #{impYn}, B.PRDT_CD, #{clntCd}), 0)) AS SHIP_UPR , REQ_QTY * DECODE(B.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH1', #{impYn}, B.PRDT_CD, #{clntCd}), 0)) AS ORDRG_AMT , REQ_QTY * DECODE(B.PRDT_STOCK_CD, 'N', 0, NVL(FN_SD06_UPR_FROM_PRDT_CD(TO_CHAR(SYSDATE,'YYYYMMDD'), 'SELPCH2', #{impYn}, B.PRDT_CD, #{clntCd}), 0)) SHIP_AMT , REQ_QTY ORDRG_QTY , REQ_WT ORDRG_WT , ORDRG_SEQ , ORDRG_DTL_SEQ , ORDRG_YN , ORDRG_PROC_ID , ORDRG_PROC_DTTM , A.CREAT_ID , A.CREAT_PGM , A.CREAT_DTTM , A.UDT_ID , A.UDT_PGM , A.UDT_DTTM FROM TB_OD02D01 A , TB_BM01M01 B WHERE A.PRDT_CD = B.PRDT_CD(+) AND REQ_SEQ = #{reqSeq}
AND A.REQ_DTL_SEQ IN
#{reqDtlSeq}

mapper.insert:

		SELECT TB_OD02D01_SQ01.NEXTVAL FROM DUAL

INSERT INTO TB_OD02M01
(
	CO_CD
	,REQ_DT
	,REQ_SEQ
	,PRJCT_CD
	,SITE_CD
	,WH_CD
	,TYP_CD
	,IMP_YN
	,DLVR_DTTM
	,REQ_RMK
	,TOT_QTY
	,TOT_WT
	,REQ_MNG_ID
	,RECPT_YN
	,ORDRG_YN
	,REQ_CREAT_DIV
	,CREAT_ID
	,CREAT_PGM
	,CREAT_DTTM
) VALUES (
	#{coCd}
	,#{reqDt}
	,#{reqSeq}
	,#{prjctCd}
	,#{siteCd}
	,#{whCd}
	,#{typCd}
	,#{impYn}
	,TO_DATE(REPLACE(#{dlvrDttm}, '-', ''), 'YYYYMMDD')
	,#{reqRmk}
	,#{totQty}
	,#{totWt}
	,#{reqMngId}
	,#{recptYn}
	,#{ordrgYn}
	,'ERP'
	,#{userId}
	,#{pgmId}
	,SYSDATE
)

INSERT INTO TB_OD02D01
(
	REQ_SEQ
	, REQ_DTL_SEQ
	, PRDT_CD
	, PRDT_DIV
	, PRDT_STKN
	, PRDT_ERQK_YN
	, PRDT_SIZE
	, PRDT_SPEC
	, PRDT_COIL_YN
	, PRDT_LEN
	, PRDT_UNIT
	, REQ_QTY
	, REQ_WT
	, ORDRG_SEQ
	, ORDRG_DTL_SEQ
	, ORDRG_QTY
	, ORDRG_WT
	, ORDRG_YN
	, ORDRG_PROC_ID
	, ORDRG_PROC_DTTM
	, CREAT_ID
	, CREAT_PGM
	, CREAT_DTTM
) VALUES (
	#{reqSeq}
	, TB_OD02D01_SQ01.NEXTVAL
	, #{prdtCd, jdbcType=VARCHAR}
	, #{prdtDiv, jdbcType=VARCHAR}
	, #{prdtStkn, jdbcType=VARCHAR}
	, #{prdtErqkYn, jdbcType=VARCHAR}
	, #{prdtSize, jdbcType=VARCHAR}
	, #{prdtSpec, jdbcType=VARCHAR}
	, #{prdtCoilYn, jdbcType=VARCHAR}
	, #{prdtLen, jdbcType=VARCHAR}
	, #{prdtUnit, jdbcType=VARCHAR}
	, #{reqQty, jdbcType=VARCHAR}
	, #{reqWt, jdbcType=VARCHAR}
	, #{ordrgSeq, jdbcType=VARCHAR}
	, #{ordrgDtlSeq, jdbcType=VARCHAR}
	, #{ordrgQty, jdbcType=VARCHAR}
	, #{ordrgWt, jdbcType=VARCHAR}
	, NVL(#{ordrgYn, jdbcType=VARCHAR},'N')
	, #{ordrgProcId, jdbcType=VARCHAR}
	, #{ordrgProcDttm, jdbcType=VARCHAR}
	, #{userId}
	, #{pgmId}
	, SYSDATE
)

mapper.update:
UPDATE TB_OD02M01
   SET PRJCT_CD = #{prjctCd}
	 , SITE_CD = #{siteCd}
	 , WH_CD = #{whCd}
	 , TYP_CD = #{typCd}
	 , IMP_YN = #{impYn}
	 , DLVR_DTTM = #{dlvrDttm}
	 , REQ_RMK = #{reqRmk}
	 , TOT_QTY = #{totQty}
	 , TOT_WT = #{totWt}
	 , REQ_MNG_ID = #{reqMngId}
	 , RECPT_YN = #{recptYn}
	 , ORDRG_YN = #{ordrgYn}
	 , UDT_ID = #{userId}
	 , UDT_PGM = #{pgmId}
	 , UDT_DTTM = SYSDATE
WHERE REQ_SEQ = #{reqSeq}

UPDATE TB_OD02M01
   SET RECPT_YN = 'Y'
 WHERE REQ_SEQ = #{reqSeq}

UPDATE TB_OD02D01
   SET ORDRG_SEQ = #{ordrgSeq}     
	 , ORDRG_DTL_SEQ    = #{ordrgDtlSeq}   
	 , ORDRG_QTY        = #{ordrgQty}       
	 , ORDRG_WT         = #{ordrgWt}        
	 , ORDRG_YN         = 'Y'    
	 , ORDRG_PROC_ID    = #{userId}   
	 , ORDRG_PROC_DTTM  = SYSDATE
 WHERE REQ_SEQ = #{reqSeq}
   AND REQ_DTL_SEQ = #{reqDtlSeq}

UPDATE TB_OD02M01
   SET ORDRG_YN = 'Y'
 WHERE REQ_SEQ = #{reqSeq}
