mapper.select:
SELECT ROWNUM AS RNUM, D.* FROM( SELECT T1.CO_CD , BM02.CLNT_DIV_CD , T1.CLNT_CD , T1.PRDT_DIV , FN_CM05M01_CD_TO_NM(T1.CO_CD) CO_NM , FN_CM05M01_CD_TO_NM(BM02.CLNT_DIV_CD) CLNT_DIV_NM , FN_CM05M01_CD_TO_NM(T1.PRDT_DIV) PRDT_DIV_NM , NVL(BM02.CLNT_NM, '기타') CLNT_NM , SUM(NVL(T1.PLAN_WT,0)) AS PLAN_WT , SUM(NVL(T1.DAY_WT,0)) AS DAY_WT , SUM(NVL(T1.DAY_AMT,0)) AS DAY_AMT , SUM(NVL(T1.MON_WT,0)) AS MON_WT , SUM(NVL(T1.MON_AMT,0)) AS MON_AMT , TO_CHAR(CASE WHEN SUM(NVL(T1.PLAN_WT,0)) != 0 THEN ROUND(SUM(NVL(T1.MON_WT,0)) / SUM(NVL(T1.PLAN_WT,0)) * 100, 0) ELSE 0 END, '9,999.99') RATE_WT FROM (
SELECT '1' type1 , S.PLAN_SEQ , S.CO_CD , S.CLNT_CD , TRIM(S.PRDT_DIV) PRDT_DIV , NVL(S.SALES_AREA_CD, 'SALESAREA99') SALES_AREA_CD , S.PLAN_YM , SUM(S.PLAN_WT) PLAN_WT , 0 DAY_WT , 0 DAY_AMT , 0 MON_WT , 0 MON_AMT FROM TB_SD01M01 S LEFT JOIN TB_CM06M01 CM06 ON CM06.ID = S.PLAN_SALES_ID LEFT JOIN TB_CM04M01 CM04 ON CM04.DEPT_ID = CM06.DEPT_ID WHERE 1=1
AND S.CO_CD = #{coCd}
AND S.PLAN_YM = TO_CHAR(TO_DATE(REPLACE(#{planDt}, '-', ''),'YYYYMMDD'),'YYYYMM')
AND S.PLAN_SALES_ID = #{planSalesId}
AND S.PAN_DEPT_ID IN (SELECT DEPT_ID FROM ( SELECT LEVEL LV, A.* FROM TB_CM04M01 A WHERE USE_YN = 'Y' START WITH DEPT_ID = #{planDeptId} CONNECT BY PRIOR DEPT_ID = UP_DEPT_ID ORDER SIBLINGS BY SORT_NO ) B)
AND S.PRDT_GRP = #{prdtGrp}
AND ( S.SELPCH_CD = #{selpchCd} OR SELPCH_CD = 'SELPCH3' )
AND ( S.SELPCH_CD = #{selpchCd} OR SELPCH_CD = 'SELPCH4' )
GROUP BY S.PLAN_SEQ , S.CO_CD , S.CLNT_CD , TRIM(S.PRDT_DIV) , NVL(S.SALES_AREA_CD, 'SALESAREA99') , S.PLAN_YM UNION ALL SELECT '2' type1 , 0 AS PLAN_SEQ , AR02.CO_CD , AR02.TRST_CLNT_CD , TRIM(AR02.PRDT_DIV) PRDT_DIV , NVL(AR02.SALES_AREA_CD, 'SALESAREA99') SALES_AREA_CD , SUBSTRING(AR02.TRST_DT, 0, 6) TRST_DT , 0 AS PLAN_WT , SUM(AR02.REAL_TRST_QTY) DAY_WT , SUM(AR02.REAL_TRST_AMT) DAY_AMT , 0 AS MON_WT , 0 AS MON_AMT FROM TB_AR02M01 AR02 INNER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = AR02.TRST_PRDT_CD LEFT JOIN TB_CM06M01 CM06 ON CM06.ID = AR02.SALES_MNG LEFT JOIN TB_CM04M01 CM04 ON CM04.DEPT_ID = CM06.DEPT_ID WHERE 1=1
AND AR02.CO_CD = #{coCd}
AND AR02.TRST_DT = REPLACE(#{planDt}, '-', '')
AND ( AR02.SELPCH_CD = #{selpchCd} OR AR02.SELPCH_CD = 'SELPCH3' )
AND ( AR02.SELPCH_CD = #{selpchCd} OR AR02.SELPCH_CD = 'SELPCH4' )
AND AR02.SALES_MNG = #{planSalesId}
AND BM01.PRDT_GRP = #{prdtGrp}
AND CM06.DEPT_ID IN (SELECT DEPT_ID FROM ( SELECT LEVEL LV, A.* FROM TB_CM04M01 A WHERE USE_YN = 'Y' START WITH DEPT_ID = #{planDeptId} CONNECT BY PRIOR DEPT_ID = UP_DEPT_ID ORDER SIBLINGS BY SORT_NO ) B)
GROUP BY AR02.CO_CD, AR02.TRST_CLNT_CD, TRIM(AR02.PRDT_DIV) , NVL(AR02.SALES_AREA_CD, 'SALESAREA99') , SUBSTRING(AR02.TRST_DT, 0, 6) UNION ALL SELECT '3' type1 , 0 AS PLAN_SEQ , AR02.CO_CD , AR02.TRST_CLNT_CD , TRIM(AR02.PRDT_DIV) PRDT_DIV , NVL(AR02.SALES_AREA_CD, 'SALESAREA99') SALES_AREA_CD , SUBSTRING(AR02.TRST_DT, 0, 6) TRST_DT , 0 AS PLAN_WT , 0 DAY_WT , 0 DAY_AMT , SUM(AR02.REAL_TRST_QTY) MON_WT , SUM(AR02.REAL_TRST_AMT) MON_AMT FROM TB_AR02M01 AR02 INNER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = AR02.TRST_PRDT_CD LEFT JOIN TB_CM06M01 CM06 ON CM06.ID = AR02.SALES_MNG LEFT JOIN TB_CM04M01 CM04 ON CM04.DEPT_ID = CM06.DEPT_ID WHERE 1=1
AND AR02.CO_CD = #{coCd}
AND AR02.TRST_DT BETWEEN TO_CHAR(TO_DATE(REPLACE(#{planDt}, '-', ''), 'YYYYMMDD'), 'YYYYMM') || '01' AND REPLACE(#{planDt}, '-', '')
AND ( AR02.SELPCH_CD = #{selpchCd} OR AR02.SELPCH_CD = 'SELPCH3' )
AND ( AR02.SELPCH_CD = #{selpchCd} OR AR02.SELPCH_CD = 'SELPCH4' )
AND AR02.SALES_MNG = #{planSalesId}
AND BM01.PRDT_GRP = #{prdtGrp}
AND CM06.DEPT_ID IN (SELECT DEPT_ID FROM ( SELECT LEVEL LV, A.* FROM TB_CM04M01 A WHERE USE_YN = 'Y' START WITH DEPT_ID = #{planDeptId} CONNECT BY PRIOR DEPT_ID = UP_DEPT_ID ORDER SIBLINGS BY SORT_NO ) B)
GROUP BY AR02.CO_CD, AR02.TRST_CLNT_CD, TRIM(AR02.PRDT_DIV) , NVL(AR02.SALES_AREA_CD, 'SALESAREA99') , SUBSTRING(AR02.TRST_DT, 0, 6) ) T1 LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = T1.CLNT_CD GROUP BY T1.CO_CD , BM02.CLNT_DIV_CD , T1.CLNT_CD , T1.PRDT_DIV , NVL(BM02.CLNT_NM, '기타') ORDER BY T1.CO_CD, T1.PRDT_DIV, BM02.CLNT_DIV_CD, T1.CLNT_CD ) D WHERE 1=1
AND D.CLNT_CD = #{clntCd}
AND D.CLNT_NM like '%'||#{clntNm}||'%' -->

SELECT ROWNUM AS RNUM, D.* FROM( SELECT BM02.CLNT_DIV_CD , PLAN_SEQ , T1.CO_CD , FN_CM05M01_CD_TO_NM(T1.CO_CD) CO_NM , T1.CLNT_CD , T1.PRDT_DIV , FN_CM05M01_CD_TO_NM(BM02.CLNT_DIV_CD) CLNT_DIV_NM , FN_CM05M01_CD_TO_NM(T1.PRDT_DIV) PRDT_DIV_NM , NVL(BM02.CLNT_NM, '기타') CLNT_NM , NVL(T1.PLAN_WT,0) AS PLAN_WT , NVL(T1.TRST_Y,0) AS TRST_Y , NVL(T1.TRST_N,0) AS TRST_N , TO_CHAR(CASE WHEN NVL(T1.PLAN_WT,0) != 0 THEN ((NVL(T1.TRST_N,0) + NVL(T1.TRST_Y,0)) / NVL(T1.PLAN_WT,0)) * 100 ELSE 0 END, '9,999.99') AS TRST_RATE
,T1.PLAN_SALES_ID ,T1.PLAN_SALES_NM ,T1.PLAN_DEPT_ID , T1.PLAN_DEPT_NM
FROM (SELECT SD01.PLAN_SEQ , NVL(SD01.CO_CD, AR02.CO_CD) CO_CD , NVL(SD01.CLNT_CD, AR02.TRST_CLNT_CD) CLNT_CD , NVL(SD01.PRDT_DIV, AR02.PRDT_DIV) PRDT_DIV , SD01.PLAN_YM
, NVL(SD01.PLAN_SALES_ID, AR02.SALES_MNG) PLAN_SALES_ID , NVL(SD01.PLAN_SALES_NM, AR02.NAME) PLAN_SALES_NM , NVL(SD01.PLAN_DEPT_ID, AR02.DEPT_ID) PLAN_DEPT_ID , NVL(SD01.PLAN_DEPT_NM, AR02.DEPT_NM) PLAN_DEPT_NM
, SD01.PLAN_WT , AR02.TRST_Y , AR02.TRST_N FROM ( SELECT '1' type1 , S.PLAN_SEQ , S.CO_CD , S.CLNT_CD , TRIM(S.PRDT_DIV) PRDT_DIV , S.PLAN_YM
,S.PLAN_SALES_ID , CM06.NAME AS PLAN_SALES_NM ,S.PAN_DEPT_ID AS PLAN_DEPT_ID ,CM04.DEPT_NM AS PLAN_DEPT_NM
, S.PLAN_WT PLAN_WT , 0 TRST_Y , 0 TRST_N FROM TB_SD01M01 S LEFT JOIN TB_CM06M01 CM06 ON CM06.ID = S.PLAN_SALES_ID LEFT JOIN TB_CM04M01 CM04 ON CM04.DEPT_ID = CM06.DEPT_ID WHERE 1=1
AND S.CO_CD = #{coCd}
AND S.PLAN_YM = TO_CHAR(TO_DATE(#{planYm},'YYYY-MM'),'YYYYMM')
AND S.PLAN_SALES_ID = #{planSalesId}
AND CM06.NAME LIKE '%'||#{planSalesNm} || '%'
AND S.PAN_DEPT_ID IN (SELECT DEPT_ID FROM ( SELECT LEVEL LV, A.* FROM TB_CM04M01 A WHERE USE_YN = 'Y' START WITH DEPT_ID = #{planDeptId} CONNECT BY PRIOR DEPT_ID = UP_DEPT_ID ORDER SIBLINGS BY SORT_NO ) B)
AND CM04.DEPT_NM IN (SELECT DEPT_NM FROM ( SELECT LEVEL LV, A.* FROM TB_CM04M01 A WHERE USE_YN = 'Y' START WITH DEPT_NM like '%'||#{planDeptNm}||'%' CONNECT BY PRIOR DEPT_ID = UP_DEPT_ID ORDER SIBLINGS BY SORT_NO ) B)
AND S.PRDT_GRP = #{prdtGrp}
AND S.PRDT_DIV = #{prdtDiv}
AND ( S.SELPCH_CD = #{selpchCd} OR SELPCH_CD = 'SELPCH3' )
AND ( S.SELPCH_CD = #{selpchCd} OR SELPCH_CD = 'SELPCH4' )
AND S.CLNT_CD = #{clntCd}
) SD01 FULL OUTER JOIN (SELECT '2' type1 , 0 AS PLAN_SEQ , AR02.CO_CD , AR02.TRST_CLNT_CD , TRIM(AR02.PRDT_DIV) PRDT_DIV , SUBSTRING(AR02.TRST_DT, 0, 6) TRST_DT
, AR02.SALES_MNG , CM06.NAME , CM06.DEPT_ID ,CM04.DEPT_NM
, 0 AS PLAN_WT , SUM(DECODE(AR02.IMP_YN, 'Y', AR02.REAL_TRST_WT)) TRST_Y , SUM(DECODE(AR02.IMP_YN, 'N', AR02.REAL_TRST_WT)) TRST_N FROM TB_AR02M01 AR02 INNER JOIN TB_BM01M01 BM01 ON BM01.PRDT_CD = AR02.TRST_PRDT_CD LEFT JOIN TB_CM06M01 CM06 ON CM06.ID = AR02.SALES_MNG LEFT JOIN TB_CM04M01 CM04 ON CM04.DEPT_ID = CM06.DEPT_ID WHERE 1=1
AND AR02.CO_CD = #{coCd}
AND SUBSTRING(AR02.TRST_DT, 0, 6) = TO_CHAR(TO_DATE(#{planYm},'YYYY-MM'),'YYYYMM')
AND ( AR02.SELPCH_CD = #{selpchCd} OR AR02.SELPCH_CD = 'SELPCH3' )
AND ( AR02.SELPCH_CD = #{selpchCd} OR AR02.SELPCH_CD = 'SELPCH4' )
AND AR02.SALES_MNG = #{planSalesId}
AND CM06.NAME LIKE '%'||#{planSalesNm} || '%'
AND BM01.PRDT_DIV = #{prdtDiv}
AND BM01.PRDT_GRP = #{prdtGrp}
AND CM06.DEPT_ID IN (SELECT DEPT_ID FROM ( SELECT LEVEL LV, A.* FROM TB_CM04M01 A WHERE USE_YN = 'Y' START WITH DEPT_ID = #{planDeptId} CONNECT BY PRIOR DEPT_ID = UP_DEPT_ID ORDER SIBLINGS BY SORT_NO ) B)
AND CM04.DEPT_NM IN (SELECT DEPT_NM FROM ( SELECT LEVEL LV, A.* FROM TB_CM04M01 A WHERE USE_YN = 'Y' START WITH DEPT_NM like '%'||#{planDeptNm}||'%' CONNECT BY PRIOR DEPT_ID = UP_DEPT_ID ORDER SIBLINGS BY SORT_NO ) B)
AND AR02.TRST_CLNT_CD = #{clntCd}
GROUP BY AR02.CO_CD, AR02.TRST_CLNT_CD, TRIM(AR02.PRDT_DIV) , SUBSTRING(AR02.TRST_DT, 0, 6)
, AR02.SALES_MNG , CM06.NAME , CM06.DEPT_ID , CM04.DEPT_NM
) AR02 ON SD01.CO_CD = AR02.CO_CD AND SD01.CLNT_CD = AR02.TRST_CLNT_CD AND SD01.PRDT_DIV = AR02.PRDT_DIV
AND SD01.PLAN_SALES_ID = AR02.SALES_MNG
) T1 LEFT JOIN TB_BM02M01 BM02 ON BM02.CLNT_CD = T1.CLNT_CD ORDER BY
T1.PLAN_DEPT_NM , T1.PLAN_SALES_ID, T1.PLAN_SALES_NM,
T1.CO_CD, BM02.CLNT_DIV_CD, T1.PRDT_DIV, T1.CLNT_CD ) D
WHERE D.CLNT_NM like '%'||#{clntNm}||'%'
ORDER BY CO_CD, CLNT_DIV_CD, PRDT_DIV, CLNT_CD
