mapper.select:
SELECT PRDT_CD, PRDT_NM, PRDT_UPR FROM TB_BM01M01 
WHERE 
	PRDT_DIV = #{prdtDiv}
AND PRDT_STKN = #{prdtStkn}
AND PRDT_SIZE = #{prdtSize}
AND PRDT_UNIT = #{prdtUnit}

AND PRDT_CD = #{prdtCd}

SELECT A.EST_NO, A.CO_CD, A.SALES_EMP_ID, C.NAME, A.EST_COPRT, FN_CM05M01_CD_TO_NM(A.EST_COPRT) AS EST_COPRT_NM, A.EST_CLNT_CD, A.EST_CLNT_NM, A.EST_RPRC, A.BIZ_TEL_NO, A.BIZ_FAX_NO, A.MNG_EMAIL, A.PRJCT_NM, TO_CHAR(TO_DATE(EST_DT, 'YYYYMMDD'),'YYYY-MM-DD') AS EST_DT, A.RMK, SUM(B.EST_QTY) as EST_QTY, SUM(B.EST_AMT) as EST_AMT, A.SALES_EMP_ID, A.CREAT_PGM FROM TB_SD03M01 A, TB_SD03D01 B, TB_CM06M01 C WHERE 1=1 AND A.EST_NO = B.EST_NO AND A.SALES_EMP_ID = C.ID
AND A.CO_CD = #{coCd}
AND ${searchValue} LIKE '%${searchName}%'
AND A.EST_NO IN (SELECT EST_NO FROM TB_SD03D01 SD03D INNER JOIN TB_BM01m01 BM01 ON BM01.PRDT_CD = SD03D.PRDT_CD WHERE EST_NO = A.EST_NO
AND SD03D.PRDT_DIV = #{prdtDiv}
AND BM01.PRDT_NM like '%${prdtNm}%'
)
AND ROWNUM BETWEEN #{firstIndex} AND #{lastIndex} GROUP BY A.EST_NO, A.CO_CD, A.EST_DT, A.EST_COPRT, A.EST_CLNT_CD, A.EST_CLNT_NM, A.EST_RPRC, A.SALES_EMP_ID, A.BIZ_TEL_NO, A.BIZ_FAX_NO, A.BIZ_FAX_NO, A.MNG_EMAIL, A.PRJCT_NM, A.RMK, C.NAME, A.CREAT_PGM ORDER BY EST_DT DESC
