mapper.select:
SELECT COUNT(*) FROM ( SELECT CO_CD, TO_CHAR(TO_DATE(REQ_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS REQ_DT, CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.CLNT_CD) AS CLNT_NM, ODR_SEQ, SITE_CD, (SELECT SITE_NM FROM TB_SD09M01 WHERE SITE_CD = T.SITE_CD) AS SITE_NM, PRJCT_CD, (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = T.PRJCT_CD) AS PRJCT_NM, MAKR_CD, FN_CM05M01_CD_TO_NM(T.MAKR_CD) AS MAKR_NM, TYP_CD, FN_CM05M01_CD_TO_NM(T.TYP_CD) AS TYP_NM, OWNER_CD, IMP_YN, WH_CD, FN_CM05M01_CD_TO_NM(T.WH_CD) AS WH_NM, MNG_TEL, TO_CHAR(DLVR_DTTM, 'YYYY-MM-DD') AS DLVR_DTTM, ADDR_ZIP, ADDR_MAIN || ' ' || ADDR_SUB AS ADDR, ODR_RMK, DIRTRS_YN, TOT_QTY, TOT_WT, TOT_AMT, SALES_MNG, (SELECT NAME FROM TB_CM06M01 WHERE ID = T.SALES_MNG) AS SALES_MNG_NM, ODR_CREAT_DIV, CLOSE_YN, CLOSE_ID, CLOSE_DTTM, ETC_FIELD1, ETC_FIELD2, ETC_FIELD3, CREAT_ID, CREAT_PGM, CREAT_DTTM, UDT_ID, UDT_PGM, UDT_DTTM FROM TB_SD04M01 T )
AND CO_CD = #{coCd}
AND TO_DATE(REQ_DT, 'YYYY-MM-DD') >= TO_DATE(#{startDt}, 'YYYY-MM-DD')
AND TO_DATE(REQ_DT, 'YYYY-MM-DD') < TO_DATE(#{endDt}, 'YYYY-MM-DD') + 1
AND OWNER_CD = #{ownerCd}
AND TYP_CD = #{typCd}
AND CLNT_CD = #{clntCd}
AND CLOSE_YN = #{closeYn}
AND ${searchType} LIKE '%${searchValue}%'

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT * FROM ( SELECT CO_CD, TO_CHAR(TO_DATE(REQ_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS REQ_DT, CLNT_CD, (SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = T.CLNT_CD) AS CLNT_NM, ODR_SEQ, SITE_CD, (SELECT SITE_NM FROM TB_SD09M01 WHERE SITE_CD = T.SITE_CD) AS SITE_NM, PRJCT_CD, (SELECT PRJCT_NM FROM TB_SD05M01 WHERE PRJCT_CD = T.PRJCT_CD) AS PRJCT_NM, MAKR_CD, FN_CM05M01_CD_TO_NM(T.MAKR_CD) AS MAKR_NM, TYP_CD, FN_CM05M01_CD_TO_NM(T.TYP_CD) AS TYP_NM, OWNER_CD, IMP_YN, WH_CD, FN_CM05M01_CD_TO_NM(T.WH_CD) AS WH_NM, MNG_TEL, TO_CHAR(DLVR_DTTM, 'YYYY-MM-DD') AS DLVR_DTTM, ADDR_ZIP, ADDR_MAIN || ' ' || ADDR_SUB AS ADDR, ODR_RMK, DIRTRS_YN, TOT_QTY, TOT_WT, TOT_AMT, (SELECT NVL(SUM(REAL_DLVR_QTY), 0) FROM TB_OD01D01 WHERE ORDRG_SEQ IN (SELECT ORDRG_SEQ FROM TB_OD01M01 WHERE ODR_SEQ = T.ODR_SEQ)) AS TOT_ORDRG_QTY, (SELECT NVL(SUM(REAL_SHIP_QTY), 0) FROM TB_AR01D01 WHERE SHIP_SEQ IN (SELECT SHIP_SEQ FROM TB_AR01M01 WHERE ODR_SEQ = T.ODR_SEQ)) AS TOT_SHIP_QTY, SALES_MNG, (SELECT NAME FROM TB_CM06M01 WHERE ID = T.SALES_MNG) AS SALES_MNG_NM, ODR_CREAT_DIV, CLOSE_YN, CLOSE_ID, CLOSE_DTTM, ETC_FIELD1, ETC_FIELD2, ETC_FIELD3, CREAT_ID, CREAT_PGM, CREAT_DTTM, UDT_ID, UDT_PGM, UDT_DTTM FROM TB_SD04M01 T )
AND CO_CD = #{coCd}
AND TO_DATE(REQ_DT, 'YYYY-MM-DD') >= TO_DATE(#{startDt}, 'YYYY-MM-DD')
AND TO_DATE(REQ_DT, 'YYYY-MM-DD') < TO_DATE(#{endDt}, 'YYYY-MM-DD') + 1
AND OWNER_CD = #{ownerCd}
AND TYP_CD = #{typCd}
AND CLNT_CD = #{clntCd}
AND CLOSE_YN = #{closeYn}
AND ${searchType} LIKE '%${searchValue}%'
ORDER BY CREAT_DTTM DESC ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}
