mapper.select:
SELECT COUNT(*) FROM TB_SD05M01 A, TB_BM02M01 B, TB_CM06M01 C WHERE A.CLNT_CD = B.CLNT_CD(+) AND A.MNG_ID = C.ID(+) AND A.USE_YN = 'Y'
AND A.CO_CD = #{coCd}
AND ( A.CREAT_DTTM >= #{strtDt})
AND ( A.CREAT_DTTM <= to_timestamp(to_date(#{endDt}, 'YYYY-MM-DD')+1) )
AND ${searchType} LIKE '%${searchValue}%'
AND A.ORD_TYP = #{ordTyp}

SELECT * FROM( SELECT ROWNUM AS RNUM, D.*, NVL(D.TOT_WT,0) - NVL(D.TOT_CELL,0) AS TOT_REM FROM( SELECT A.CO_CD ,FN_CM05M01_CD_TO_NM(A.CO_CD) AS CO_NM ,A.PRJCT_CD ,A.PRJCT_NM ,A.TNKEY_YN ,A.CLNT_CD ,B.CLNT_NM ,A.MAKR_CD ,FN_CM05M01_CD_TO_NM(A.MAKR_CD) AS MAKR_NM ,A.PRJCT_ADDR_ZIP ,A.PRJCT_ADDR ,A.PRJCT_ADDR_SUB ,A.PRJCT_MNG_NM ,A.TEL_NO ,A.LOSS_RATE ,A.MNG_ID ,C.NAME AS MNG_NM ,TO_CHAR(TO_DATE(A.STRT_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS STRT_DT ,TO_CHAR(TO_DATE(A.END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS END_DT ,TO_NUMBER(A.TOT_WT) / 1000 AS TOT_WT ,A.ODR_NO ,A.ODR_DT ,A.USE_YN ,A.RMK ,A.PRJCT_MES_CD ,A.ETC_FIELD1 ,A.ETC_FIELD2 ,A.ETC_FIELD3 ,A.CREAT_PGM ,TO_CHAR(A.CREAT_DTTM,'YYYY-MM-DD') AS CREAT_DTTM ,A.CREAT_DTTM ,A.UDT_ID ,A.UDT_PGM ,TO_CHAR(A.UDT_DTTM,'YYYY-MM-DD') AS UDT_DTTM ,NVL((SELECT SUM(REAL_TRST_QTY) FROM TB_AR02M01 WHERE PRJCT_CD = A.PRJCT_CD),0) AS TOT_CELL ,A.ORD_TYP ,FN_CM05M01_CD_TO_NM(A.ORD_TYP) AS ORD_TYP_NM ,A.BILDNG_TYP ,FN_CM05M01_CD_TO_NM(A.BILDNG_TYP) AS BILDNG_TYP_NM ,A.ENGRK_TYP ,FN_CM05M01_CD_TO_NM(A.ENGRK_TYP) AS ENGRK_TYP_NM ,A.MAKR_MNG_NM ,A.CLNT_EMAIL ,A.CLNT_FAX_NO ,A.PRCSN_UPR ,A.DC_DIV ,FN_CM05M01_CD_TO_NM(A.DC_DIV) AS DC_DIV_NM ,A.PRDT_UPR ,A.CPLR_YN ,A.PMNT_MTD_CD ,FN_CM05M01_CD_TO_NM(A.PMNT_MTD_CD) AS PMNT_MTD_NM ,A.SHOP_CLNT_CD ,(SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = A.SHOP_CLNT_CD) AS SHOP_CLNT_NM ,A.SHOP_UPR ,A.CTRT_RMK ,A.OUTORD_UPR ,A.OUTORD_LOSS_RATE ,A.OUTORD_MNG_ID ,A.OUTORD_MNG_NM ,A.OUTORD_TEL_NO ,A.OUTORD_FAX_NO ,A.WH_CD ,FN_CM05M01_CD_TO_NM(A.WH_CD) AS WH_NM ,A.SCND_WH_CD ,FN_CM05M01_CD_TO_NM(A.SCND_WH_CD) AS SCND_WH_NM ,A.THRD_WH_CD ,FN_CM05M01_CD_TO_NM(A.THRD_WH_CD) AS THRD_WH_NM ,TO_CHAR(TO_DATE(A.PCHS_STRT_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS PCHS_STRT_DT ,TO_CHAR(TO_DATE(A.PCHS_END_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS PCHS_END_DT ,A.CLMN_DAY ,A.CLMN_RMK ,A.CLMN_DIV_CD ,FN_CM05M01_CD_TO_NM(A.CLMN_DIV_CD) AS CLMN_DIV_NM ,A.PCHS_CLNT_CD ,(SELECT CLNT_NM FROM TB_BM02M01 BM02 WHERE BM02.CLNT_CD = A.PCHS_CLNT_CD) AS PCHS_CLNT_NM ,(SELECT MAX(TNKEY_CF_YN) FROM TB_SD09M01 SD09 WHERE A.PRJCT_CD = SD09.PRJCT_CD ) AS TNKEY_CF_YN ,(SELECT MAX(TNKEY_CF_DT) FROM TB_SD09M01 SD09 WHERE A.PRJCT_CD = SD09.PRJCT_CD ) AS TNKEY_CF_DT FROM TB_SD05M01 A, TB_BM02M01 B, TB_CM06M01 C WHERE A.CLNT_CD = B.CLNT_CD(+) AND A.MNG_ID = C.ID(+) AND A.USE_YN = 'Y'
AND A.CO_CD = #{coCd}
AND ( A.CREAT_DTTM >= #{strtDt})
AND ( A.CREAT_DTTM <= to_timestamp(to_date(#{endDt}, 'YYYY-MM-DD')+1) )
AND ${searchType} LIKE '%${searchValue}%'
AND A.ORD_TYP = #{ordTyp}
ORDER BY A.STRT_DT DESC ) D ) WHERE RNUM BETWEEN ${firstIndex} AND ${lastIndex}

