mapper.select:
SELECT count(*) FROM ( select max(PRDT_DT) AS PRDT_DT, max(PRDT_UPR) AS PRDT_UPR, max(SELL_UPR) AS SELL_UPR, max(basis_price_seq) AS BASIS_PRICE_SEQ, PRDT_IMP_YN, PRDT_DIV, FN_CM05M01_CD_TO_NM(PRDT_DIV) AS PRDT_DIV_NM from( select * from TB_SD06M01 where ( PRDT_IMP_YN, PRDT_DIV, PRDT_DT) in ( select PRDT_IMP_YN, PRDT_DIV, max(TO_DATE(PRDT_DT, 'YYYYMMDD')) as PRDT_DT from TB_SD06M01 WHERE 1=1
AND TO_DATE(PRDT_DT, 'YYYYMMDD') <= TO_DATE(#{prdtDt}, 'YYYYMMDD')
AND PRDT_IMP_YN = #{prdtImpYn}
AND PRDT_DIV = #{prdtDiv}
group by PRDT_IMP_YN, PRDT_DIV ) order by PRDT_DT desc ) t group by t.PRDT_IMP_YN, t.PRDT_DIV )

SELECT TO_CHAR(TO_DATE(PRDT_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS PRDT_DT , PRDT_UPR, SELL_UPR, BASIS_PRICE_SEQ, PRDT_IMP_YN, PRDT_DIV, PRDT_DIV_NM from( SELECT ROWNUM AS RNUM, A.* FROM ( select max(PRDT_DT) AS PRDT_DT, max(PRDT_UPR) AS PRDT_UPR, MAX(SELL_UPR) AS SELL_UPR, max(basis_price_seq) AS BASIS_PRICE_SEQ, PRDT_IMP_YN, PRDT_DIV, FN_CM05M01_CD_TO_NM(PRDT_DIV) AS PRDT_DIV_NM from( select * from TB_SD06M01 where ( PRDT_IMP_YN, PRDT_DIV, PRDT_DT) in ( select PRDT_IMP_YN, PRDT_DIV, max(TO_DATE(PRDT_DT, 'YYYYMMDD')) as PRDT_DT from TB_SD06M01 WHERE 1=1
AND TO_DATE(PRDT_DT, 'YYYYMMDD') <= TO_DATE(#{prdtDt}, 'YYYYMMDD')
AND PRDT_IMP_YN = #{prdtImpYn}
AND PRDT_DIV = #{prdtDiv}
group by PRDT_IMP_YN, PRDT_DIV ) order by PRDT_DT desc ) t group by t.PRDT_IMP_YN, t.PRDT_DIV )A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}


SELECT
	count(*)
FROM TB_SD06M01
WHERE  PRDT_IMP_YN = #{prdtImpYn}

	AND PRDT_DT = #{prdtDt}

	AND PRDT_DIV = #{prdtDiv}

SELECT
	COUNT(*)
FROM 
(
	SELECT
		*
	FROM 
		TB_SD06D01 
	WHERE BASIS_PRICE_SEQ = #{basisPriceSeq}

	AND PRDT_STKN = #{prdtStkn}



	AND PRDT_ERQK_CD = #{prdtErqkCd}


)

SELECT
	COUNT(*)
FROM 
(
	SELECT
		*
	FROM 
		TB_SD06D02
	WHERE BASIS_PRICE_SEQ = #{basisPriceSeq}

	AND PRDT_SIZE = #{prdtSize}



	AND PRDT_ERQK_CD = #{prdtErqkCd}


)

SELECT
	*
FROM 
	TB_SD06D01
WHERE BASIS_PRICE_SEQ = #{basisPriceSeq} 
		AND PRDT_STKN = #{prdtStkn}
		AND PRDT_ERQK_CD = #{prdtErqkCd}

SELECT
	*
FROM 
	TB_SD06D02
WHERE BASIS_PRICE_SEQ = #{basisPriceSeq}
		AND PRDT_SIZE = #{prdtSize}
		AND PRDT_ERQK_CD = #{prdtErqkCd}

SELECT count(*)
FROM TB_SD06M02 SD06
		WHERE ( CLNT_CD,PRDT_IMP_YN, PRDT_DIV, PRDT_DT) IN (
				SELECT  CLNT_CD,PRDT_IMP_YN, PRDT_DIV, MAX(TO_DATE(PRDT_DT, 'YYYYMMDD')) AS PRDT_DT
				from TB_SD06M02 M02
				WHERE SD06.CLNT_CD = M02.CLNT_CD

			  	  AND USE_YN = #{useYn}


			  	  AND TNKEY_YN = #{tnkeyYn}



				  AND TO_DATE(PRDT_DT, 'YYYYMMDD') <=  TO_DATE(#{prdtDt}, 'YYYYMMDD')  


				  AND PRDT_IMP_YN = #{prdtImpYn}


				  AND PRDT_DIV = #{prdtDiv}

				GROUP BY CLNT_CD, PRDT_IMP_YN, PRDT_DIV
		)

		  AND  CLNT_CD = #{clntCd}


		  AND  USE_YN  = #{useYn}


		  AND PRDT_IMP_YN = #{prdtImpYn}


		  AND PRDT_DIV = #{prdtDiv}


		  AND TNKEY_YN = #{tnkeyYn}

SELECT Z.CLNT_CD , (SELECT CLNT_NM FROM TB_BM02M01 BM02 WHERE BM02.CLNT_CD = Z.CLNT_CD) CLNT_NM , Z.USE_YN , Z.TNKEY_YN , TO_CHAR(TO_DATE(PRDT_DT, 'YYYYMMDD'), 'YYYY-MM-DD') AS PRDT_DT , Z.PRDT_UPR , Z.SELL_UPR , (SELECT MAX(M01.BASIS_PRICE_SEQ) FROM TB_SD06M01 M01 WHERE M01.PRDT_DT <= #{prdtDt} AND M01.PRDT_DIV = Z.PRDT_DIV AND M01.PRDT_IMP_YN = Z.PRDT_IMP_YN AND M01.SELPCH_CD = 'SELPCH2' ) AS BASIS_PRICE_SEQ , Z.PRDT_IMP_YN , Z.PRDT_DIV , Z.PRDT_DIV_NM , Z.CREAT_ID , Z.CREAT_PGM , Z.CREAT_DTTM , Z.UDT_ID , (SELECT NAME FROM TB_CM06M01 WHERE ID = Z.UDT_ID) AS UDT_NM , Z.UDT_PGM , TO_CHAR(Z.UDT_DTTM,'YYYY-MM-DD HH24:MI:SS') AS UDT_DTTM FROM( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT PRDT_DT AS PRDT_DT, PRDT_UPR AS PRDT_UPR, SELL_UPR AS SELL_UPR,
CLNT_CD, USE_YN, TNKEY_YN, PRDT_IMP_YN, PRDT_DIV, FN_CM05M01_CD_TO_NM(PRDT_DIV) AS PRDT_DIV_NM, CREAT_ID, CREAT_PGM, CREAT_DTTM, UDT_ID, UDT_PGM, UDT_DTTM from( SELECT * FROM TB_SD06M02 SD06 WHERE ( CLNT_CD,PRDT_IMP_YN, PRDT_DIV, PRDT_DT) IN ( SELECT CLNT_CD,PRDT_IMP_YN, PRDT_DIV, MAX(TO_DATE(PRDT_DT, 'YYYYMMDD')) AS PRDT_DT from TB_SD06M02 M02 WHERE SD06.CLNT_CD = M02.CLNT_CD
AND USE_YN = #{useYn}
AND TNKEY_YN = #{tnkeyYn}
AND TO_DATE(PRDT_DT, 'YYYYMMDD') <= TO_DATE(#{prdtDt}, 'YYYYMMDD')
AND PRDT_IMP_YN = #{prdtImpYn}
AND PRDT_DIV = #{prdtDiv}
GROUP BY CLNT_CD, PRDT_IMP_YN, PRDT_DIV )
AND CLNT_CD = #{clntCd}
AND USE_YN = #{useYn}
AND PRDT_IMP_YN = #{prdtImpYn}
AND PRDT_DIV = #{prdtDiv}
AND TNKEY_YN = #{tnkeyYn}
order by PRDT_DT, CLNT_CD DESC ) t
)A ) Z WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}


SELECT
	count(*)
FROM TB_SD06M02
WHERE  PRDT_IMP_YN = #{prdtImpYn}
  AND  CLNT_CD     = #{clntCd}
  AND  USE_YN      = #{useYn}
  AND  TNKEY_YN    = #{tnkeyYn}

	AND PRDT_DT = #{prdtDt}

	AND PRDT_DIV = #{prdtDiv}

mapper.update:

MERGE INTO TB_SD06M02
USING DUAL
ON (

	    CLNT_CD     = #{clntCd}
	AND TNKEY_YN    = #{tnkeyYn}
	AND PRDT_DT     = #{prdtDt} 

	AND PRDT_IMP_YN = #{prdtImpYn}
	AND PRDT_DIV    = #{prdtDiv}

)
WHEN MATCHED THEN 
	UPDATE SET 


	      USE_YN   = #{useYn},
		  PRDT_UPR = #{prdtUpr},
		  SELL_UPR = #{sellUpr},
		  UDT_ID   = #{userId},
		  UDT_PGM  = 'SD0602P02.html',
		  UDT_DTTM = sysdate
WHEN NOT  MATCHED THEN 
INSERT 
 ( CLNT_CD,PRDT_DT, PRDT_IMP_YN, PRDT_DIV, PRDT_UPR, SELL_UPR, USE_YN,TNKEY_YN

 , CREAT_ID, CREAT_PGM, CREAT_DTTM, UDT_ID, UDT_PGM, UDT_DTTM) 
VALUES
(#{clntCd},#{prdtDt}, #{prdtImpYn}, #{prdtDiv}, #{prdtUpr}, #{sellUpr}, #{useYn}, #{tnkeyYn}

, #{userId}, #{pgmId}, sysdate,  #{userId}, #{pgmId}, sysdate);


	UPDATE  TB_SD06M02
	   SET  USE_YN      = DECODE(#{useYn},'Y','N','Y'),
		 	UDT_ID      = #{userId},
		 	UDT_PGM     = 'SD0602P02.html',
		 	UDT_DTTM    = sysdate
    WHERE   CLNT_CD     = #{clntCd}
	  AND   TNKEY_YN    = #{tnkeyYn} 
	  AND   PRDT_DT     = #{prdtDt} 
	  AND   PRDT_IMP_YN = #{prdtImpYn}
	  AND   PRDT_DIV    = #{prdtDiv}
	  AND   USE_YN      = #{useYn}
