mapper.select:
	SELECT 
 		COUNT(*) 
	  FROM GOLDMOON.TB_SD08M01 A
	     , GOLDMOON.TB_BM01M01 B
	  WHERE A.PRDT_CD  = B.PRDT_CD 

		   	AND A.CO_CD = #{coCd}		


		   	AND A.PRDT_DT BETWEEN REPLACE(#{startDt}, '-', '') AND REPLACE(#{endDt}, '-', '')		


		   	AND A.CLNT_CD = #{clntCd}	


		   	AND A.SELPCH_CD = #{selpchCd};

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT A.CO_CD , A.CLNT_CD , (SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = A.CLNT_CD) AS CLNT_NM , TO_CHAR(TO_DATE(A.PRDT_DT, 'YYYYMMDD'), 'YYYY-MM-DD') PRDT_DT , A.SELPCH_CD , FN_CM05M01_CD_TO_NM(SELPCH_CD) SELPCH_NM , A.PRDT_CD , B.PRDT_NM , A.PRDT_UPR , NVL(FN_CM05M01_CD_TO_NM(B.PRDT_STKN), B.PRDT_STKN) PRDT_STKN_NM , NVL(FN_CM05M01_CD_TO_NM(B.PRDT_SIZE), B.PRDT_SIZE) PRDT_SIZE_NM , NVL(FN_CM05M01_CD_TO_NM(B.PRDT_SPEC), B.PRDT_SPEC) PRDT_SPEC_NM , NVL(FN_CM05M01_CD_TO_NM(B.PRDT_UNIT), B.PRDT_UNIT) PRDT_UNIT_NM , A.ETC_FIELD1 , A.ETC_FIELD2 , A.ETC_FIELD3 , A.RMK , A.CREAT_ID , A.CREAT_PGM , A.CREAT_DTTM , A.UDT_ID , A.UDT_PGM , A.UDT_DTTM FROM GOLDMOON.TB_SD08M01 A , GOLDMOON.TB_BM01M01 B WHERE A.PRDT_CD = B.PRDT_CD
AND A.CO_CD = #{coCd}
AND A.PRDT_DT BETWEEN REPLACE(#{startDt}, '-', '') AND REPLACE(#{endDt}, '-', '')
AND A.CLNT_CD = #{clntCd}
AND A.SELPCH_CD = #{selpchCd}
ORDER BY PRDT_DT DESC ) A ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT A.CO_CD , A.CLNT_CD , (SELECT CLNT_NM FROM TB_BM02M01 WHERE CLNT_CD = A.CLNT_CD) AS CLNT_NM , TO_CHAR(TO_DATE(A.PRDT_DT, 'YYYYMMDD'), 'YYYY-MM-DD') PRDT_DT , A.SELPCH_CD , FN_CM05M01_CD_TO_NM(SELPCH_CD) SELPCH_NM , A.PRDT_CD , B.PRDT_NM , A.PRDT_UPR , NVL(FN_CM05M01_CD_TO_NM(B.PRDT_STKN), B.PRDT_STKN) PRDT_STKN_NM , NVL(FN_CM05M01_CD_TO_NM(B.PRDT_SIZE), B.PRDT_SIZE) PRDT_SIZE_NM , NVL(FN_CM05M01_CD_TO_NM(B.PRDT_SPEC), B.PRDT_SPEC) PRDT_SPEC_NM , NVL(FN_CM05M01_CD_TO_NM(B.PRDT_UNIT), B.PRDT_UNIT) PRDT_UNIT_NM , A.ETC_FIELD1 , A.ETC_FIELD2 , A.ETC_FIELD3 , A.RMK , A.CREAT_ID , A.CREAT_PGM , A.CREAT_DTTM , A.UDT_ID , A.UDT_PGM , A.UDT_DTTM FROM GOLDMOON.TB_SD08M01 A , GOLDMOON.TB_BM01M01 B WHERE A.PRDT_CD = B.PRDT_CD
AND A.CO_CD = #{coCd}
AND A.PRDT_DT BETWEEN REPLACE(#{startDt}, '-', '') AND REPLACE(#{endDt}, '-', '')
AND A.CLNT_CD = #{clntCd}
AND A.SELPCH_CD = #{selpchCd}
AND A.PRDT_CD = #{prdtCd}

mapper.update:
UPDATE TB_SD08M01
SET PRDT_UPR = #{prdtUpr}
  , RMK = #{rmk}
  , UDT_ID = #{userId}
  , UDT_PGM = #{pgmId}
  , UDT_DTTM = SYSDATE
WHERE CO_CD = #{coCd}
  AND CLNT_CD = #{clntCd}
  AND PRDT_DT = REPLACE(#{prdtDt}, '-', '')
  AND SELPCH_CD = #{selpchCd}
  AND PRDT_CD  = #{prdtCd}

mapper.delete:
DELETE FROM TB_SD08M01 
 WHERE CO_CD = #{coCd}
   AND CLNT_CD = #{clntCd}
   AND PRDT_DT = REPLACE(#{prdtDt}, '-', '')
   AND SELPCH_CD = #{selpchCd}
   AND PRDT_CD  = #{prdtCd}
